css:
@-moz-document domain("pochta.ru") {
html {-webkit-filter: invert(100%);background: #000;}

html.rpst-portal.aui .page-about__title, 
html.rpst-portal.aui .page-press-centre__title, 
html.rpst-portal.aui .postcard-page__postcard, 
html.rpst-portal.aui .postcard-page__popup-postcard-image {-webkit-filter: invert(100%);opacity: 0.4;}

html.rpst-portal.aui  img {-webkit-filter: invert(100%);opacity: 0.8;}

html.rpst-portal.aui .tracking-card {box-shadow: 0 0 0 1px #DDD;}

html.rpst-portal.aui .tracking-card--open.tracking-card--last {margin-bottom: 24px;}

html.rpst-portal.aui .product-sticky-summary-container {display: none;}

html.rpst-portal.aui .input__btn-search::after {color: #000;}

html.rpst-portal.aui .input__btn-search:hover::after {color: #0055A6;}

html.rpst-portal.aui .input__btn-search::before {background-color: transparent;}

html.rpst-portal.aui .input__btn-search {background-color: transparent;background-image: none;border: 0 solid;}

html.rpst-portal.aui .input__btn-search:hover {background-color: transparent;border: 0 solid;}
}
