*{margin:0;padding:0;}
a{color:#b15c5c;text-decoration: none}
a:hover{color:red;}
body{background:#2b0000; background-repeat:repeat-x scroll 0 0;color:maroon;font:normal 80% "Lucida Sans Unicode",sans-serif;margin:0;}
input{color:#990000 !important;font:normal 1em "Lucida Sans Unicode",sans-serif;}
p,cite,code,ul{padding-bottom:1.2em;}
h2{font-size:1.4em;margin-bottom:4px;}
h1,h2,h3{color:#FFF;padding-top:6px;}
.clearer{clear:both;}
.container{background-image: none !important}
 div .popupContent {background-color: black}
.themeHeader{height:92px;min-width:800px;}
.title{padding:28px 20px 0 76px;width:125px;}
.themeHeader .title a
  {
    text-transform: lowercase;
    font-family: 'Raleway', sans-serif !important;
    font-weight: 50 !important;
    font-size: 40px;
    z-index: 10 !important;
    top:-50px
  }
.themeHeader a
  {
    color: #990000;
    font-family: 'Raleway', sans-serif !important;
    font-weight: 100;
    font-size: 12px;
  }
.navigation{height:92px;padding:0 16px;vertical-align:bottom;white-space:nowrap;}
.navigation a{color:#b15c5c;font: bold 1.2em "Trebuchet MS",sans-serif;margin-top:56px;padding:8px 8px;text-align:center;text-decoration:none;}
.navigation a:hover{background-color:#fff;color:red;}
.main{background:2b0000 !important;background-image: none;clear:both;padding:12px 12px 0 52px;}
.sidenav{float:left;width:24%;}
