.gameView .inputPanel .txtInput.txtLogographicInput{overflow:hidden;height:50px;font-size:20px!important;}
.gameView .inputPanel .txtInput{color:#990000 !important;width:98%;height:35px;font-family:Arial,serif;font-size:26px;margin-top:2px;margin-bottom:10px;background:transparent none repeat scroll 0;border-width:medium;}
.gameView .inputPanel .txtInput-unfocused{color:#010101;}
.gameView .inputPanel .txtInput-error{background-color:#010101;}
.arrowPopup{width:60px;height:35px;vertical-align:middle;font-size:13px;text-align:center;}
.arrowPopup-start{background-position:0 0;}
.arrowPopup-error{background-position:0 35px;}
.arrowPopupText{font-weight:bold;padding-right:10px;font-variant:small-caps;}
.arrowPopupTextOneLine{line-height:33px;}
