css:
@namespace url(http://www.w3.org/1999/xhtml);
/* BASICS */

*{
   background-color: transparent !important;
   color: #bcbcbc !important;
}

body, div, form, table, tbody, tr, td, fieldset, ul, li, a,
#content, #bodyContent{
   background-color: #282828 !important;
   background-image: none !important;
}

/* FORMS */

input, select, button, checkbox{
   -moz-appearance: none !important;
   border: 1px solid white  !important;
   outline: none !important;
   background-color: #282828 !important;
}

#simpleSearch{
   border: none !important;
}


/* OTHER */
#content{
   font-family: trebuchet, arial !important; 
   font-size: 12pt !important;
   border: 0 !important;
}

div#siteNotice{
   display:none !important;
}
p{
   border: none !important;
}
a{
   color: #FFF !important;
}
a:visited{
   font-weight: bold !important;
   opacity: 0.65 !important;
}
a:link{
   color: #FFF !important;
}

h2{
   border-bottom: #c6cece 1px solid !important;
   font-size: 120% !important;
}
h2 > span{
   color: #FFF !important;
}
h2 > span > span{
   color: #FFF !important;
   font-size: 100% !important;
}

fieldset{
   border-color: #c6cece !important;
}

#bodyContent ul{
   list-style-image: none !important;
   list-style-type: disc !important;
}
#toc ul{
   list-style-type: none !important;
}

.thumb{
   border-color: #262626 !important;
}



/*  ADDING LOGO */
#p-logo a {
   background: url(http://upload.wikimedia.org/wikipedia/en/b/bc/Wiki.png) no-repeat !important;
   position: absolute !important;
   clip: rect(0, 135px, 115px, 0) !important;
   height: 115px !important;
   overflow: hidden !important;
   margin-top: 30px !important;
}


/* *** IMAGES *** */
img{
   background-color: #FFF !important;
}
img.tex{
   padding: 5px !important;
}
span.tex{
   border: 1px white solid !important;
   padding: 5px !important;
}
