@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("modelmayhem.com") {

/*      this section controls the main body text                     */
body {
background: white !important;
color: white !important;
}

a:link {font-weight:500 !important; color: white !important; }
a:visited { color: white !important; }
a:hover { color: white !important; }

.maintable, .labelpicpage,tr,td {
/* the next background color controls the dark grey background */
/* brighten it if you want to see the profile labels better.*/
background:white !important;
color: white !important;
}


/*  this removes the banner        */

.logobar {
display: none !important;
}

/*      this section controls dashed lines between post */

.postwrap {
background:white !important;
}

/*      this section controls the color of quoted text in forums */

.quote {
background:white !important;
color: white  !important;
}

/*  this controls the background color of main page nav bar         */
.navbarwrap, .lineit {
background:white !important;
color: white!important;
}

/*  BECKS IS A DORK   */
/*  BECKS IS A DORK   */
/*  BECKS IS A DORK   */
/*  BECKS IS A DORK   */
/*  this controls the background color of avatars   */

.postbit {
background: white !important;
color: white !important;
}

h1, .big {
color: white !important;
}

.small, .small_link, .thepagenums {
font-size:10pt !important;
font-style:normal !important;
color: white !important; }

}

/*  this section makes the unlinked urls red for hightlighting new emails                       */
@-moz-document url-prefix(http://www.modelmayhem.com/me.php),
url-prefix(http://www.modelmayhem.com/me.php?),
url-prefix(http://www.modelmayhem.com/outbox.php) {

a:link { color: white !important; }
a:visited { color: white !important; }
a:hover { color: white !important; }

}
/*  this section modifies the Plastic Puppet search to make it consistent                     */
@-moz-document url-prefix(http://search.plasticpuppet.com){

body {
background:white !important;
font-family: verdana, sans-serif, serif!important;
font-size: 10pt !important;
color: white !important;
}

#results td {
font-size: 10pt !important;
font-family: verdana, sans-serif, serif!important;
padding-bottom:2px !important;
padding-left:5px !important;
color: white !important;
}

#results tr.odd {
background-color:white !important; }

#results tr:hover {
background-color:white !important;
}

a:link { font-weight: bold !important; color: white !important; }
a:visited { color: white !important; }
a:hover { color: white !important; }
}

.castingheader {
color: white !important;
}
