css:
@-moz-document url("https://catwar.su/cw3/") {
div#history_block div
  {
    background-color: #838383;
  }

div#thdey
  {
    margin-left: 10px;
  }

#history div /* история фон */
  {
    background-image: url('http://d.zaix.ru/aAoB.png');
    margin-top: 10px;
    margin-left: 1px;
    color: transparent;
  }

span#location /* локация */
  {
    color: #fffc !important;
    position: fixed;   
    width: 170px;
    top: 1px;
    left: 1px;

    border-radius: 10px;

    padding: 1px;
    font-weight: bold;
  }

a#history_clean
  {
    margin-left: 5px;
  }

span#ist /* цвет текста в истории */
  {
    margin-left: 5px; /* отступ в начале предложения */
    color: #fffa !important;
  }

div#cages_div /* свет в игровой */
  {
    opacity:1 !important;
  }

select
  {
    background-color: #111111;
    color: #838383;
  }

body /* ФОН */
  {    
	color: #efecec;
    background: #7c6b56c7 url(http://d.zaix.ru/aAos.png) left fixed !important;
    background-size: cover !important;
    background-repeat: no-repeat !important;
  }

.small /* мой кот чат лс */
  {
    font-size: 14px;
    margin-left: 42%;
  }

div#tir
  {
    margin-left: 10px;
    margin-top: 10px;
  }

#akten
  {
    margin-left: 5px;
  }

a /* цвет кликабельного текста */
  {
    color: #B5A18C;
  }

input /* окошка ввода сообщения чят */
  {
   background-color: #9f907f;
    color: #fffd;
    border: 0.5px solid #46220063;
    border-radius: 0px;
  }

hr /* разделяющие линии сообщений */
  {
    border: 0.5px solid #000;
  }

.ui-slider /* цвет фона ползунка */
  {
    background: #9f907f;
    border: 0.5px solid #46220063;
    margin-right: 3px;
    border-radius: 0px;
  }

.ui-slider .ui-slider-handle /* цвет ползунка */
  {
    background: #bbaea0;
    border: 0.1px solid #bbaea0;
  }

.move_name /* цвет названия перехода */
  {
    color: #000000;
  }

.nick /* ник в чате */
  {
    color: #ecececf5;
  }

.adopt-id
  {
    color: #fff;
  }

.hotkey /* кнопочки на панельке боя */
  {
    background-color: #dfccc0a9;
    color: #000;
    border: 0.5px solid #00000049;
    border-radius: 3px;
  }

.ui-scroll
  {
    background-color: #bcbcbc;
  }

#fightPanel /* панель боя */
  {
    background-color: #BFA688F0;
    border: 0.5px solid #dde3eea5;
  }

#block_mess
  {
    margin-left: 10px;
  }

#tr_chat /* полоса чята [сверху] */
  {
    background-color: #bba1849c	;
    padding: 0px;
  }

#chat_msg /* сообщения чята [снизу] */
  {
    background-color: #bba1849c;
    border: 0px;
  }

#tr_actions /* действия */
  {
    background-color: #bba1849c;
    border: 0px;
  }

#block_deys
  {
    margin-top: 5px;
  }

#mit /* выбор действия */
  {
    background-color: #BBA184;
    color: #fff;
    border: 0.5px solid #a89c8f8a;
    border-radius: 0px;
  }

#mitok /* показать действия */
  {
    margin-top: 2px;
    background-color: #a89c8f8a;
    color: #fff;
    border: 0.5px solid #8f9aa81a;
    border-radius: 0px;
  }

#main_table /* мой кот и прочее */
  {
    border: 0px;
    margin-top: 7px;
  }

/* #tr_tos  ТОС выкл
  {
    display: none;
  } */

#tr_tos /* тос сезон время */
{
	background: #03264200;
	color: #000;
	position: absolute;
    width: 170px;
    top: 0px;

    border-radius: 10px;
    text-align: left;
    padding: 3px;
}

#tr_mouth /* что во рту */
  {
    background-color: #bba1849c;
  }

#info_main
  {
    background-color: #bba1849c;
  }

#family /* семья */
  {
    background-color: #bba1849c;
  }

#history.toggle /* история, кнопка */
  {
    color: #fff6f6;
    font-size: 16px;
    margin-left: 5px;
    margin-bottom: 5px;
  }

#history
  {
    background-color: #bba1849c;
    width: 100%;
    margin-left: 10px;
  }

#parameter /* параметры */
  {
    position: fixed;
    width: 170px;
    top: 100px;
    left: 1px;
    border-radius: 10px;
    text-align: left;
    padding: 3px;
  }


a#parameters.toggle  /* "параметры и навыки" */
  {
	 text-align: center;
     font-size: 15px;
  } 

#block_mess
  {
    margin-top: 5px;
    margin-bottom: 5px;
  }

#family /* скрывает семью */
  {
    display: none;
  }

#cages /* цвет над фоном локации, затемнение */
  {
    background-color: transparent;
  }

#text /* текст в окошке чата */
  {
    color: #fff;
  }

#might_table
  {
    margin-bottom: 21px;
  }

p#error /* окошко ошибки */
  {
	position:fixed;
    top: 350px;
    left: 4px;
    background-color: #db804bc7;
    border: 0.5px solid #db804ba5;
  }

#items
  {
    padding-left: 17px;
    padding-top: 5px;
    padding-bottom: 5px;
  }

#iinput:focus
  {
    background-color: #383838;
  }

#newchat /* новое соо чат */
  {
    background-color: transparent;
    color: #884a0b59;
  }

#newls /* новое соо лс */
  {
    background-color: transparent;
    color: #884a0b59;
  }

a:hover /* выделенное кликабельное слово */
  {
    color: #b9a98a;
  }

.myname /* твой ник в чяте */
  {
    background-color: #726250c9;
    color: #fff;
  }

.title /* заголовок ДЕЙСТВИЯ офф */
  {
    display:none;
  }

#tr_sky /* небо офф */
  {
    display:none;
  }
}
