css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ebay.com") {
/* - You will need to block the following yellow eBay images first via Adblock Plus or another application -

imgPanelBGYellowGrad1.gif
imgYellowBG_1x30.gif
imgHPHdrRgtYellow.gif
imgHPHdrLftYellow.gif
imgYellowGrad_1x97.gif
imgHdrDefaultY.gif
imgSearchbg_1x89.gif
atAllCat.gif
yukon-actionbar-background.gif
http://pics.ebaystatic.com/aw/pics/globalHeader*

- then create your Stylish stylesheet */

/* Replaces the rainbow bar found on all eBay pages with a single color */
td.brnd1, td.brnd2, td.brnd3, td.brnd4, td.brnd5, td.brnd6, td.brnd7 {
    background-color : #3857aa !important; 
}
/* Replaces much of the yellow found on eBay's home page and other landing pages */
#HomePageMebaagPortlet .hprcp_head {
    background-color : #efefef !important;
}
.hpTabbedModule .hprcp_head {
    background : #cccccc !important;
}
.hpTabbedModule .navTabListContainer, .c-rcp_y_l_h-rcp .c-rcp-head {
    background : #fafafa !important;
}
.hprcp_n {
    background-color : #e8e8e8 !important;
}
.hprcp_head {
    border-top : #cccccc !important;
    border-right : 1px solid #cccccc !important;
    border-left : 1px solid #cccccc !important;
    border-bottom : 1px solid #cccccc !important;
}
.hprcp_e {
    background : #cccccc !important;
}
.gbhprcp_s .hprcp_w {
    background : #cccccc !important;
}
.r3_hm {
    background : #ededed !important;
    border-left-color : #cccccc !important;
    border-right-color : #cccccc !important;
}
.r3_l, .r3_r {
    background : #cccccc !important;
}
.tertiary .r3_l {
    background : #cccccc !important;
}
.tertiary .r3_m {
    border-top-color : #cccccc !important;
    border-left-color : #cccccc !important;
    border-right-color : #cccccc !important;
}
/* Replaces much of the yellow found in search results and category pages */
.fsbar .bar_m {
    background : #fafafa !important;
    border-right-color : #cccccc !important;
    border-left-color : #cccccc !important;
}
.fsbar .bar_h .bar_e, .fsbar .bar_f .bar_e, .fsbar .hbar_f .bar_e, .fsbar .bar_h .bar_w, .fsbar .bar_f .bar_w, .bsbar .hbar_f .bar_w {
    background : #cccccc !important;
}
#areaTitle div {
    background : #cccccc !important;
}
#areaTitle {
    background : #cccccc !important;
    border-bottom-color : #3857aa !important;
}
.c-yl .r3_hm {
    border : 1px solid #cccccc !important;
    border-bottom : 1px solid #cccccc !important;
}
.c-yl .r3-r-sq, .c-yl .r3_m, .c-yl .r3_hm_sq {
    border-top : 6px solid #cccccc !important;
}
.c-yl .r3_l, .c-yl .r3_r {
    background : #cccccc !important;
}
.c-yl .r3_hm {
    background : #fafafa !important;
}
.lw-action-bar {
    border-bottom : 1px solid #cccccc !important;
}
.c-rcp_y_l_h-rcp .c-rcp-n .c-rcp-w {
    background : #cccccc !important;
}
.c-rcp_y_l_h-rcp .c-rcp-head {
    border-left : 1px solid #cccccc !important;
    border-right : 1px solid #cccccc !important;
    border-bottom : 1px solid #cccccc !important;
}
.c-rcp-rcp .gn .gw, .c-rcp-rcp .rcp-n .rcp-w, .c-rcp-rcp .gn .ge, .c-rcp-rcp .rcp-n .rcp-e {
    background : #cccccc !important;
}
.c-rcp_y_s_h-rcp .c-rcp-head {
    background : #fafafa !important;
    border-left-color : #cccccc !important;
    border-right-color : #cccccc !important;
    border-bottom-color : #cccccc !important;
}
/* Replaces much of the yellow scattered throughout eBay */
.basr-rcp .basr-n .basr-w, .basr-rcp .basr-n .basr-e {
    background : #cccccc !important;
}
.basr-rcp .basr-head {
    border-left-color : #cccccc !important;
    border-right-color : #cccccc !important;
}
.bmrcp-rcp .bmrcp-n .bmrcp-w, .bmrcp-rcp .bmrcp-n .bmrcp-e {
    background : #cccccc !important;
}
.bmrcp-rcp .bmrcp-head {
    border-left-color : #cccccc !important;
    border-right-color : #cccccc !important;
}

}
