css:
@-moz-document regexp(".*mattermost.*"), regexp(".*framateam.*") {
[data-url*="://framateam.org"] .inMM {
    background-color: #1f1f1f;
    padding-top: 20px;
}

[data-url*="://framateam.org"] .inMM #root {
    margin-top: -20px !important;
}

[data-url*="://framateam.org"] .inMM .sidebar--left .sidebar__switcher {
    margin-bottom: 0px
}

[data-url*="://framateam.org"] .inMM .sidebar--left {
    height: calc(100%);
}

.textarea-wrapper .help__text {
    visibility: hidden;
}
.sidebar--left {
    top: 20px;
}
.team-sidebar {
    top: 20px;
}

.sidebar--right .sidebar--right__content {
    margin-top: 20px;
}

.post-create__container {
    margin-bottom: -12px;
}

#framanav.navbar {
    display: none !important;
}

#framanav_container {
    display: none !important;
}

.multi-teams .team-sidebar .team-wrapper .team-container.active::before {
    background: #45af19;
}

.sidebar--left .nav li.active .sidebar-item::before,
.app__body .modal .settings-modal .nav-pills > li.active button::before {
    background: #45af19;
}

.sidebar--left .nav-pills__unread-indicator {
    background: #45af19;
}

/* SUB-COMMENT INDENT */
.post.same--root.post--comment,
.post.same--root.same--user.post--comment {
    padding-left: 4em;
}
}
