css:
@-moz-document domain("forum.fivem.net") {
tr {
    border-bottom: 1px solid #121212;
}
.category-list, .topic-list, .latest-topic-list {
    box-shadow: 0 2px 5px 0 rgba(0,0,0,0.16), 0 0 0 0 rgba(0,0,0,0.12);
    background-color: #222;
}
.nav-pills>li>a:hover {
    background-color: #373737;
}

.nav-pills>li.active>a, .nav-pills>li>a.active {
    color: #aaa !important;
    font-weight: 500;
    background-color: #272727 !important;
    border-bottom: 3px solid #ff5722 !important;
}

tbody {
    border-top: 3px solid #171717;
}

body {
    background: #111;
}

.badge-notification.new-posts, .badge-notification.unread-posts {
    background-color: #844b39;
    color: #fafafa;
    font-weight: normal;
}

a.mention, a.mention-group {
    padding: 2px 4px;
    color: #646464;
    background: #373737;
    border-radius: 8px;
    font-weight: bold;
    font-size: 0.93em;
}

hr {
    display: block;
    height: 1px;
    margin: 1em 0;
    border: 0;
    border-top: 1px solid #333;
    padding: 0;
}

.list-controls .combo-box .combo-box-header {
    background: #272727;
    color: #aaa;
    border: 1px solid #333;
    font-size: 1em;
    transition: none;
    height: 10px;
}

.nav-pills>li.active>a, .nav-pills>li>a.active {
    color: #373737;
    background-color: #ff5722;
}

.category-list tbody .category h3 a[href] {
    color: #aaa;
}

.nav-pills>li>a {
    border: none;
    padding: 6px 12px;
    color: #666;
    font-size: 1.1487em;
    line-height: 1;
    box-sizing: border-box;
    min-height: 30px;
    display: flex;
    align-items: center;
    transition: background 0.15s;
}

#banner {
    padding: 10px;
    background: #844b39;
    color: #eee;
    z-index: 2;
    overflow: auto;
}

.d-header {
    background: linear-gradient(to right,rgb(23, 21, 35),rgb(33, 32, 47)),url(https://fivem.net/17c4e5d0b35262c228dcf66c98365982.jpg) center top/100%;
}

.title {
    color: #2d95bf !important;
}

.select-kit .select-kit-row.is-highlighted {
    background: #844b39;
}

.select-kit .select-kit-collection {
    background: #333;
    overflow-x: hidden;
    overflow-y: auto;
    border-radius: inherit;
    -webkit-overflow-scrolling: touch;
    margin: 0;
    padding: 0;
    max-height: 200px;
}
.select-kit .select-kit-row.is-highlighted {
    background: #844b39;
}
.select-kit.combo-box .select-kit-filter {
    padding: 4px 10px;
    border-top: 1px solid #333;
    border-bottom: 1px solid #333;
}

.select-kit .select-kit-body {
    display: none;
    background: #222;
    box-sizing: border-box;
}

.badge-wrapper.bullet span.badge-category {
    color: #9e9e9e;
    overflow: hidden;
    text-overflow: ellipsis;
}

span.topic-count {
    color: #9e9e9e;
}

.category-list .topics .badge-notification, .category-list .category .badge-notification {
    color: #e66840 !important;
}

.menu-panel {
    border: 1px solid #222;
    box-shadow: 0 6px 14px rgba(0,0,0,0.55);
    background-color: #111;
    z-index: 1000;
    padding: 0.5em;
    width: 300px;
}

.user-menu .notifications li {
    background-color: #222;
}

.user-menu .notifications .read {
    background-color: transparent;
    filter: grayscale(95%);
    opacity: 0.2;
}

.user-menu .notifications li span {
    color: #ccc;
}

a:visited {
    color: #da6833;
}
a {
    color: #da6833;
    text-decoration: none;
    cursor: pointer;
}

.user-menu .notifications li:hover, .user-menu .notifications li:focus {
    background-color: #070707;
    outline: none;
}

.menu-panel li a:hover, .menu-panel li a:focus, .menu-panel li.heading a:hover, .menu-panel li.heading a:focus {
    background-color: inherit;
    outline: none;
}

div.menu-links-header a:hover, div.menu-links-header a:focus {
    background-color: #2f2f2f;
    outline: none;
}

.panel-body::-webkit-scrollbar-thumb {
    background-color: #272727;
}

.panel-body::-webkit-scrollbar {
    background-color: #070707;
    width: 0.8em;
}

.menu-panel {
    border: 1px solid #222;
    box-shadow: 0 6px 14px rgba(0,0,0,0.55);
    background-color: #111;
    z-index: 1000;
    padding: 0.5em;
    width: 300px;
}

.topic-body {
    box-shadow: 0 2px 5px 0 rgba(0,0,0,0.16), 0 0 0 0 rgba(0,0,0,0.12);
    background-color: #222;
    margin-top: 10px;
    border-radius: 2px;
    padding: 12px 20px 0 20px;
}
.topic-body {
    width: calc(690px + 11px * 2);
    float: left;
    position: relative;
    border-top: 1px solid #333;
    padding: 12px 0 0 0;
}

.list-cell, .table-heading, .category-list td, .category-list th {
    color: #474747 !important;
}

html {
    color: #aaa;
    font-family: Helvetica,Arial,sans-serif;
    font-size: 14px;
    line-height: 1.4;
    overflow-y: scroll;
    direction: ltr;
}

#topic-title h1 a {
    color: #bbb;
}

.btn {
    background: #2f2f2f !important;
}

.cooked ins, .d-editor-preview ins {
    background-color: #0b3e0e;
    color: #bbb;
}

p>code, li>code, pre>code {
    color: #777;
    background: #111;
    padding: 2px;
    border-radius: 3px 4px;
    border: 1px solid #171717;
}

.post-retort {
    background: #444;
    float: left;
    padding: 2px;
    z-index: 1;
    margin: 20px 2px;
    border-radius: 5px;
    border: 1px solid #666;
    position: relative;
    outline: 0;
}

.post-links-container .post-links {
    margin-top: 1em;
    padding-top: 1em;
    border-top: 1px solid #373737;
}

.topic-map {
    border-top: none;
}

.topic-map section {
    border-top: 1px solid rgba(0, 0, 0, 0.4);
}

.topic-map .buttons .btn {
    border: 0;
    padding: 0 23px;
    color: #909090;
    background: #f4f4f4;
    border-left: 1px solid #444;
    border-top: 1px solid transparent;
}

.badge-notification.clicks {
    font-weight: normal;
    background-color: #444;
    top: -1px;
    color: #777;
    position: relative;
    border: none;
}

nav.post-controls .like-button:hover {
    background: #444;
}

nav.post-controls .show-replies:hover {
    background: #444;
}

nav.post-controls .show-replies {
    margin-left: 0;
    font-size: inherit;
}

.embedded-posts {
    border: 1px solid #444;
}

.embedded-posts.bottom .row .topic-avatar, .embedded-posts.bottom .row .topic-body {
    border-top: 1px solid #444;
}

aside.onebox {
    border: 5px solid #444;
    margin-bottom: 1em;
    padding: 12px 25px 12px 12px;
    font-size: 1em;
}
.lightbox-wrapper .lightbox {
    position: relative;
    display: inline-block;
    overflow: hidden;
    background: #222;
    border: 1px solid #333;
}

.embedded-posts .collapse-down, .embedded-posts .collapse-up {
    position: absolute;
    color: #909090;
    background: #fafafa;
    border: 1px solid #444;
    padding: 6px 9px 8px;
    z-index: 99;
}

blockquote {
    border-left: 5px solid #444;
    background-color: rgba(0, 0, 0, 0.33);
    clear: both;
}
aside.quote .title {
    border-left: 5px solid #474747;
    color: #646464;
    background: linear-gradient(to right, rgba(0,0,0,0.45) 0%,rgba(0,0,0,0.33) 100%);
    padding: 12px 12px 1px 12px;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
}

.names .new_user a, .names .user-title, .names .user-title a {
    color: #444;
    background-color: rgba(0, 0, 0, 0.2);
    padding: 2px 8px;
    border-radius: 2px 4px;
}

.names span a {
    border: none !important;
    background-color: transparent;
}

.extra-info-wrapper .topic-link {
    color: #ccc;
    display: block;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

.hljs-keyword, .javascript .hljs-title, .nginx .hljs-title, .hljs-subst, .hljs-request, .hljs-status {
    color: #aaa;
    font-weight: bold;
}
.hljs {
    display: block;
    padding: 0.5em;
    color: #777;
}

code.hljs::-webkit-scrollbar-thumb {
    background-color: #111;
}
code.hljs::-webkit-scrollbar {
    background-color: #2c2c2c;
}

tr.highlighted {
    animation: background-fade-highlight 2.5s ease-out;
}

@keyframes background-fade-highlight {
  0% {
    background-color: lighten(#444, 20%);
  }
  100% {
    background-color: lighten(#222, 10%);
  }
}

#reply-control {
    position: fixed;
    display: flex;
    flex-direction: column;
    bottom: 0;
    height: 0;
    right: 0;
    left: 0;
    margin-left: auto;
    margin-right: auto;
    max-width: 1475px;
    width: 100%;
    z-index: 400;
    transition: height 250ms ease, background 250ms ease, transform 250ms ease, max-width 250ms ease;
    background-color: #222222fa;
    box-shadow: 0 -1px 40px rgba(0,0,0,0.12);
}

input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"] {
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    display: inline-block;
    padding: 4px 10px;
    margin-bottom: 9px;
    font-size: 1em;
    line-height: 1;
    color: #444;
    background-color: #171717;
    border: 1px solid #272727;
    border-radius: 0;
    box-sizing: border-box;
    min-height: 30px;
}

.select-kit.combo-box .select-kit-header {
    background: #171717;
    border: 1px solid #272727;
    padding: 0 5px 0 10px;
    font-weight: 500;
    font-size: 1em;
}

.d-editor-textarea-wrapper {
    display: flex;
    flex-direction: column;
    background-color: #171717;
    position: relative;
    border: 1px solid #272727;
}

.d-editor-button-bar {
    display: flex;
    align-items: center;
    min-height: 30px;
    padding-left: 3px;
    border-bottom: 1px solid #444;
    background-color: #2f2f2f;
}

.d-editor-spacer {
    width: 1px;
    height: 20px;
    margin: 0 5px;
    background-color: #444;
    display: inline-block;
}

.d-editor-textarea-wrapper textarea {
    background: transparent;
    color: #ccc;
}

.select-kit.dropdown-select-box.composer-actions .select-kit-header {
    background: none;
    outline: none;
    padding: 0;
    margin-right: 5px;
    border: 1px solid #444;
    min-height: unset;
}

.fullscreen-composer #reply-control.fullscreen:before {
    content: "";
    width: 100%;
    height: 100%;
    position: fixed;
    z-index: -1;
    left: 0;
}

.user-main .about .secondary {
    background: #222;
    border-top: 1px solid #333;
    border-bottom: 1px solid #333;
}

.small-action {
    display: flex;
    align-items: center;
    max-width: 758px;
    border-top: none;
}

.topic-map .participants .user {
    border: 1px solid rgba(0, 0, 0, 0.2);
    border-radius: 0.25em;
    padding: 0;
    margin: 0.125em 0.25em 0.125em 0;
    display: flex;
    align-items: center;
    height: 26px;
}

.topic-map {
    background: rgba(0, 0, 0, 0.2);
    border: 1px solid rgba(0, 0, 0, 0.2);
    border-top: none;
}

.btn {
    color: #777;
}

.user-main .about .secondary dd {
    padding: 0;
    margin: 0 15px 0 0;
    overflow: hidden;
    text-overflow: ellipsis;
    color: inherit;
}

.user-stream .excerpt {
    margin: 5px 0;
    font-size: 1em;
    word-wrap: break-word;
    color: inherit;
}

.user-main .about.collapsed-info .details {
    margin-top: 0;
    background: transparent;
}

.user-main .about .details {
    background: transparent;
}

.user-stream .item, .user-stream .user-stream-item {
    background-color: #272727;
    border-bottom: 1px solid #333;
}

.fullscreen-composer #reply-control.fullscreen .reply-to {
    border-bottom: 1px solid #373737;
    padding-bottom: 3px;
    margin: 0;
}

.fullscreen-composer #reply-control.fullscreen.show-preview .d-editor-textarea-wrapper {
    border-right: 1px solid #373737;
}
}
