css:
@-moz-document domain("gitconnected.com") {
.base-page, body, html {
  background-color: #222;
}

body {
  color: #aaa;
}

/*** Top Bar ***/

.navbar__bar {
  background-color: #282828;
}

.ui.input > input {
  background-color: #222;
  color: #aaa;
}

.ui.input.focus > input, .ui.input > input:focus {
  background-color: #222;
  color: #aaa;
}

/* Site logo */
.navbar-logo__logo {
  opacity: 0.6;
}

/* column items */
.post-item__title {
  color: #aaa;
}

/* Dropdown */

.ui.search > .results .result {
  color: #aaa;
  border-bottom-color: #444;
}

.ui.category.search > .results .category .result:hover, .ui.search > .results .result:hover {
  background-color: #333;
}

.ui.search > .results {
  background-color: #282828;
  border-color: #333;
}

.ui.search > .results .result .title {
  color: #aaa;
}

.ui.search > .results .result .description {
  color: #aaa;
}

.ui.category.search > .results .category .result.active, .ui.search > .results .result.active {
  background-color: #333;
}

.ui.search > .results .result.active .title {
  color: #2185d0;
}

.ui.search > .results .result.active .description {
  color: #2185d0;
}

.ui.search > .results .result .image img {
  opacity: 0.5;
}

/* 'Developer News', 'Coding Tutorials' */
.navbar__nav-link:active, .navbar__nav-link:hover, .navbar__nav-link:visited {
  color: #aaa;
}

/* 'Sign Up', 'Sign In' button */
.ui.positive.button, .ui.positive.buttons .button {
  background-color: #004f13; /* #21ba45; */
  color: #aaa;
}

/* '+ New Post' on right corner */
.post-form-trigger__text {
  background-color: #282828;
}

.post-form-trigger__text:hover {
  background-color: #333;
}

/* Overlay on click of '+ New Post' */
.ui.modal > .content {
  background-color: #222;
}

.auth-description-modal__learn-more a {
  color: #999;
}

.ui.modal > .actions {
  background-color: #222;
  border-top-color: #333;
}

.ui.input {
  color: #999;
}


/*** Left column ***/

/* '+ Add a Story' button */
.ui.blue.button, .ui.blue.buttons .button {
  background-color: #333; /* #2185d0; */
  color: #aaa;
}

.ui.card, .ui.cards > .card {
  background-color: #282828;
  border-color: #666;
  box-shadow: none;
}

.ui.card > .content, .ui.cards > .card > .content {
  border-color: #666;
}

/* Item */
.learn-feed-cta__row {
  color: #aaa;
}

/* Arrow */
.ui.list .list > a.item i.icon, .ui.list > a.item i.icon {
  color: #aaa6;
}

.ui.list .list > a.item:hover .icon, .ui.list > a.item:hover .icon {
  color: #2185d0;
}

/*** Right column ***/

/** Top Header - 'Top', 'Latest', 'Tutorials' **/

.ui.menu {
  background-color: #282828;
}

.ui.menu .item {
  color: #aaa;
}

.ui.link.menu .item:hover, .ui.menu .dropdown.item:hover, .ui.menu .link.item:hover, .ui.menu a.item:hover {
  color: #2185d0;
}

.ui.menu .active.item {
  background-color: #333;
  color: #aaa;
}

/** Item List **/
.post-item {
  border-bottom-color: #444;
}

/** Item **/

.post-item__comments {
  color: #aaa;
}

.post-item__comments:hover {
  color: #2185d0;
}

/* Image */
.post-item__image {
  opacity: 0.5;
}

/***** Search Results Page - gitconnected.com/search?q=<query> *****/

.ui.card > .content > .header, .ui.cards > .card > .content > .header {
  color: #aaa;
}

/* 'Join gitconnected' block */
.sidebar-auth__value-prop {
  color: #aaa !important;
}

/***** Find Coding Tutorials & Courses Page - gitconnected.com/learn *****/

/* 'Join GitConnection' button */
.ui.green.button, .ui.green.buttons .button {
  background-color: #004f13; /* #21ba45; */
  color: #aaa;
}

/** Tile **/

.learn-category-card__title {
  color: #aaa;
}

.ui.cards a.card:hover, .ui.link.card:hover, .ui.link.cards .card:hover, a.ui.card:hover {
  background-color: #333;
  box-shadow: 0 1px 3px 0 #333,0 0 0 1px #666;
}

/***** Tutorial Page - gitconnected.com/learn/<topic> *****/

.tutorial-list {
  background-color: #282828;
  border-color: #333;
}

.tutorial-list-item {
  border-bottom-color: #333;
}

.tutorial-list-item__title {
  color: #aaa;
}

/* Tag */
.tutorial-tag {
  background-color: #222;
  color: #2185d0; /* #366193; */
}

/** 'Filter by Tag' right column **/

.tutorial-filter-tag-list__title {
  border-bottom-color: #666;
}

.ui.checkbox label, .ui.checkbox + label {
  color: #aaa;
}

.ui.checkbox .box::before, .ui.checkbox label::before {
  background-color: #222;
  border-color: #333;
}

.ui.checkbox .box:hover::before, .ui.checkbox label:hover::before {
  background-color: #333;
}

.ui.checkbox input:checked ~ .box::before, .ui.checkbox input:checked ~ label::before {
  background-color: #666;
  color: #aaa;
}

/* 'Recently Viewed' */
.recently-viewed-tutorials-card__title {
  border-bottom-color: #666;
}

.ui.card .meta, .ui.cards > .card .meta {
  color: #aaa;
}

/* Share overlay */
.tutorial-share-box {
  background-color: #282828;
  border-top-color: #333;
  border-left-color: #333;
}

/****** After login ******/

/***** Main Page *****/

/* Notification Bell Icon */
.notification-dropdown i.icon {
  color: #666;
}

/** Notification Icon dropdown **/

.notification-dropdown-header__header {
  color: #aaa;
}

.empty-notification__content {
  color: #aaa;
}

.notification-dropdown-header {
  border-bottom-color: #333;
}

.notification-dropdown-header:hover .notification-dropdown-header__link {
  color: #2185d0; 
}

/* Profile Icon */
.ui.avatar.image, .ui.avatar.image img, .ui.avatar.image svg, .ui.avatar.images .image, .ui.avatar.images img, .ui.avatar.images svg {
  opacity: 0.5;
}

.user-dropdown {
  color: #666;
}

/** Profile Icon dropdown **/

.ui.dropdown .menu {
  background-color: #222;
}

.ui.pointing.dropdown > .menu::after {
  background-color: #222;
}

.user-dropdown__menu .item {
  color: #aaa;
}

.user-dropdown__menu .item:hover {
  background-color: #282828;
  color: #2185d0; 
}

/***** Profile Page - gitconnected.com/<user> *****/

/*** Left column ***/

/* 'Followers', 'Following' separator */
.user-card-follow__section--left {
  border-right-color: #666;
}

/*** Middle column ***/

/* Item */

.featured-project__name {
  color: #999;
}

.featured-project__summary {
  color: #aaa;
}

/*** Right Column ***/

/** 'Links' box **/

.profile-contact-list__add-more {
  color: #aaa;
}

.contact-link-item a {
  color: #aaa;
}

/***** Notifications Page - gitconnected.com/notifications *****/

.notification-page__title {
  color: #aaa;
}

/***** Projects Page - gitconnected.com/projects *****/

/* 'Featured Projects', 'All Projects' */
.ui.header {
  color: #aaa;
}

/** Page No.s **/

.pagination li {
  background-color: #222;
  border-top-color: #333;
  border-bottom-color: #333;
  border-right-color: #333;
}

.pagination li:hover {
  background-color: #333;
}

.pagination li.disabled {
  background-color: #222;
  border-left-color: #333;
}

.pagination li:first-child {
  border-left-color: #333;
}

.pagination li.active {
  background-color: #003068; /* #5293df; */
}

.pagination li.active a {
  color: #ccc;
}

/** List of items **/

.ui.items > .item > .content > .header {
  color: #aaa;
}

.ui.items > .item .meta {
  color: #aaac;
}

.ui.items > .item > .content > .description {
  color: #aaa9;
}

.ui.divided.items > .item {
  border-top-color: #333;
}

/***** Settings Page - gitconnected.com/settings *****/

/**** Profile Tab - gitconnected.com/settings/profile ****/

.ui.segment {
  background-color: #282828;
}

.ui.attached.segment {
  border-color: #333;
}

.ui.form input:not([type]), .ui.form input[type="date"], .ui.form input[type="datetime-local"], .ui.form input[type="email"], .ui.form input[type="file"], .ui.form input[type="number"], .ui.form input[type="password"], .ui.form input[type="search"], .ui.form input[type="tel"], .ui.form input[type="text"], .ui.form input[type="time"], .ui.form input[type="url"] {
  background-color: #222;
  color: #aaa;
}

.ui.form input:not([type]):focus, .ui.form input[type="date"]:focus, .ui.form input[type="datetime-local"]:focus, .ui.form input[type="email"]:focus, .ui.form input[type="file"]:focus, .ui.form input[type="number"]:focus, .ui.form input[type="password"]:focus, .ui.form input[type="search"]:focus, .ui.form input[type="tel"]:focus, .ui.form input[type="text"]:focus, .ui.form input[type="time"]:focus, .ui.form input[type="url"]:focus {
  background-color: #222;
  color: #aaa;
}

/* 'Bio' */
.ui.form textarea {
  background-color: #222;
  color: #aaa;
}

.ui.form textarea:focus {
  background-color: #222;
  color: #aaa;
}

/* 'Experience' dropdown */

.ui.selection.dropdown {
  background-color: #222;
  color: #aaa;
}

i.icon::before {
  background-color: #666;
}

.ui.dropdown .menu > .item {
  color: #aaa;
}

.ui.selection.dropdown .menu > .item {
  border-top-color: #333;
}

.ui.dropdown .menu > .item:hover {
  background-color: #333;
}

.ui.selection.visible.dropdown > .text:not(.default) {
  color: #aaa;
}

.ui.dropdown .menu .selected.item, .ui.dropdown.selected {
  color: #aaa;
}

/**** Email Tab - gitconnected.com/settings/email ****/

.primary-email-dropdown__primary-dropdown > .text {
  border-bottom-color: #444;
}

}
