css:
@-moz-document url("https://www.tumblr.com/dashboard") {
.flag--accessibility-design-update .rapid-recs-container .rapid-recs{background-image:linear-gradient(180deg,rgba(0,25,53,0) 0,#36465D 15%,#36465D 85%,rgba(0,25,53,0))}
.flag--accessibility-design-update .promote-form .campaign-container .ad-preview{background-color:#36465D}
.flag--accessibility-design-update .toastr .toast{background:#36465D}
.flag--accessibility-design-update .search_control_items .menu_item:hover{color:#36465D}
body.flag--always-opaque-peepr.flag--accessibility-design-update .ui_peepr_glass{background-color:#36465D}
.flag--accessibility-design-update .ui_peepr_glass.opaque{background-color:#36465D}
.flag--accessibility-design-update .radar .radar_footer .radar_avatar::before{background-image:linear-gradient(to right, rgba(0,25,53,0), #36465D)}
.flag--accessibility-design-update .promote-form .campaign-container .ad-preview{background-color:#36465D}
.flag--accessibility-design-update .rapid-recs-container .rapid-recs{background-image:linear-gradient(180deg,rgba(0,25,53,0) 0,#36465D 15%,#36465D 85%,rgba(0,25,53,0))}
.flag--accessibility-design-update .toastr .toast{background:#36465D}
.flag--accessibility-design-update .search_control_items .menu_item:hover{color:#36465D}
.flag--accessibility-design-update .identity, .identity.flag--accessibility-design-update {background-color: #36465D;}
.flag--accessibility-design-update .post_avatar {background-color: #36465D;}
.flag--accessibility-design-update .l-container.l-container--two-column-dashboard .left_column {background-color: #36465D;}
.flag--accessibility-design-update .l-container.l-container--two-column-dashboard .l-content, .flag--accessibility-design-update .l-container.l-container--two-column .l-content {background-color: #36465D;}
.flag--accessibility-design-update .l-container.l-container--two-column-dashboard .right_column, .flag--accessibility-design-update .l-container.l-container--two-column .right_column {background-color: #36465D;}
.flag--accessibility-design-update .l-header-container--refresh {background-color: #36465D;}
.identity.flag--accessibility-design-update .user_list li .follow_list_item_blog:before {
    background-image: linear-gradient(90deg,rgba(0,25,53,0),#36465D);
    border-right: 5px solid #36465D;
}
.identity.flag--accessibility-design-update .user_list li .follow_list_item_blog:hover:before {
    background-image: none;
    border-right: none;
}
.flag--accessibility-design-update .plus-follow-button {
    background: hsla(0,0%,100%,.4);
    color: #36465D;
}
.identity.flag--accessibility-design-update .right_column:after {
    background: linear-gradient(180deg,#36465D,rgba(0,25,53,0));
}
}
