css:
@-moz-document domain("stepik.org") {
	body{
		background-color:#1a1a1a;
        color:white;
    }
    h1 , h2, h3 ,h4 ,h5, h6,strong{
           color:white ! important;
    }    
    .cert-widget__title{
		color:white !important;
    }
    .st-link , .profile-header-widget__stat-rank, .ember-view, .ember-link{
        color:white !important;
    }
    .active{
		color:#8d9392 !important;
    }
    span{
		color:white !important;
    }
    .light-tabs__header:not(.light-tabs_classic) .light-tabs__switch.active, .light-tabs__header:not(.light-tabs_classic) .light-tabs__switch.light-tabs__switch_active{
		color:#e47c7c !important;                                            
    }
    /*Активность за последний год*/
    .activity-graph .cal-heatmap-container .q1{
        fill: #3f4bb3;                                              
    }
    .activity-graph .cal-heatmap-container .q2{
        fill: #7b80a8;                                                 
    }
    .activity-graph .cal-heatmap-container .q3{
        fill: #8e94c4;                                                 
    }
    .activity-graph .cal-heatmap-container .q4{
        fill: #bcbfd9;                                                 
    }
    .activity-graph .cal-heatmap-container .q5{
        fill: #e7e8ec;                                      
    }
    
    footer{
       background-color:#333030 !important;
    }
   #ember1421, #ember1420, #ember1419{
        color:rgb(90,79,79) !important;    
    }
    /*menu*/
    .st-cmenu,.st-cmenu_align_right, .ember-view{
         background-color:#1a1a1a;
    }
    /*settings*/
    nav{
		background-color:#1a1a1a!important;
    }
    .user-edit .horizontal-scroll-menu-widget__link{
       background-color:#525330!important;
    }
    .user-edit__avatar-field{
		background-color:#305347!important;
    }
    input, textarea{
		background-color:#1a1a1a!important;
    }
    /*tasks*/
    code, .CodeMirror-scroll{
		background-color:#1a1a1a!important;
    }
    .attempt-wrapper{
		background-color:#1a1a1a!important;
    }
    .attempt-wrapper.evaluation .attempt__inner, .attempt-wrapper.evaluation .attempt__message{
    	background-color:#1a1a1a!important;
        color:white!important;
    }
    .attempt-wrapper.evaluation .quiz__typename, .attempt-wrapper.pending .quiz__typename, .attempt-wrapper:not(.attempt-wrapper_with_review).evaluation .attempt__footer{
		background-color:#1a1a1a!important;
    }
    span>svg{
        color:#6c6!important;                                             
    }
    .modern-topbar__step-pins>div{
		border: 1px solid #6c6!important;
    }
    .step-text-wrapper{
		color:white!important;
    }
    .attempt-wrapper.error .quiz__typename, .attempt-wrapper.outdated .quiz__typename, .attempt-wrapper.rotten .quiz__typename, .attempt-wrapper.wrong .quiz__typename, .attempt-wrapper:not(.attempt-wrapper_with_review).wrong .attempt__footer{
		/*Решило: 20367 Успешных решений: 47% Вы получите: 1 балл*/
        background-color: #1a1a1a!important;
    }
    .CodeMirror-gutter, .CodeMirror-gutters, .CodeMirror-linenumber, .CodeMirror-scroll, .CodeMirror-sizer{
        background-color: #1a1a1a!important;                                            
    }
    /*comments*/
    .lesson-modern .lesson__discussions-wrapper, .lesson__player{
		background-color:#1a1a1a!important;
    }
    .lesson-modern .lesson__footer{
        	background-color:#1a1a1a!important;
    }
    .s-select .s-select__select{
            background-color: #773b3b!important;
    }
    .lesson-modern__wrapper .quiz__typename{
		background-color:#1a1a1a!important;
    }
    .button:not(.st-button_style_none).expand, button:not(.st-button_style_none).expand{
		/*button*/
    	background-color:#6c6!important;
        color:white!important;
    }
    .button:not(.st-button_style_none).white, button:not(.st-button_style_none).white{
    	background-color:#6c6!important;
    }
    .lesson-modern .lesson__footer .modern-button__footer .svg-icon{
		background-color: rgba(0,0,0,0)!important;                                            
    }
    .lesson-controls__item>.ember-view, .st-cmenu, .st-cmenu_align_right{
		background-color: rgba(0,0,0,0)!important;                                            
    }
    .svg-icon.right-arrow_icon svg{
         color: white!important;
    }
    /*likes*/
    .lesson-modern__wrapper .lesson__likes-widget.vote-widget .vote-widget__segment{
		background-color:#1a1a1a!important;                             
    }
    /*Новости Stepik*/
    .modal-dialog-inner{
		background-color:black!important;    	
    }
    .platform-news__theme-text{
		color:#c4d2d5!important;
    }
    
    /*lider table*/
    table tr.alt, table tr.even, table tr:nth-of-type(even){
         background-color:#1a1a1a!important;
		border-bottom: 1px solid white;
		color:white!important;
    }
    .ember-view .leaders-list-item{
		border-bottom: 1px solid white;
    }
    .leaders-list-item__rank-num{
		color:white!important;
    }
    .st-tabs{
		background-color:#1a1a1a!important;
    }
    .st-tabs__link, .st-tabs__link.active{
		background-color:#1b0f1c!important;
    }
    /*sherch*/
    #ember4871:hover{
		background-color:#252222!important;
    }
    /*course list*/
    .lesson-widget__title-text{
		color:#fff;
    }
    .toc-component .toggle__title{
		background-color:#000;
    }        
    /*nutification*/
    .lesson-modern__wrapper .lesson__mode_certificate_bar, .lesson-modern__wrapper .lesson__mode_mode_review{
		background-color:#3b4771!important;
    }
    .lesson-modern:not(.lesson_edit) {
		background-color:#3b4771!important;
    }
    /*forum*/
    .submission-show .submission__top{
		background-color: #1a1a1a!important;
    }
    .CodeMirror-cursor{
        border-left: 1px solid #fff!important;                              
    }       
    .CodeMirror-linenumber{                                            
    	color: #fff;
    }
}
