css:
/* VULCAN UONET+*/
@-moz-document domain("vulcan.net.pl"){
/* TEST MODE*/
body, html[xmlns], html[class]{
    background: #000 !important;
    color: #FFF !important;
}
html{background:#252525 !important;}
header {
    background: #252525 !important;
}
form[action]{
    background: #0000 !important;
    border:0 !important;
}
a {
    color: #FD6700
}
label {
    color: #FFF !important
}
/* *** LOGIN BUTTON *** */
.topBar{
    background: #000 !important;
    border-bottom: 1px solid #FFF !important;
}
iframe[src="https://vulcanm.home.pl/_uonetads/vulcan_lekcja"]{
    display:none !important;
}
.loginBox {
    background: #000 !important;    
    border-top: 1px #FFF solid !important;
}
.loginButton:first-of-type{
    display:none !important;
}
.loginButton:last-of-type{
    position: static !important;
    height:100px !important;
    background: #D60000 !important;
    border-radius: 50px !important;
    line-height:100px !important;
}
/*  *** LOGIN *** */
.LogOnBoard {
    color: #FFF !important;
    border: 1px solid #FFF;
    background: #383838 !important;
}
.LogOnBoard a.Account {
    color: #FD6700 !important;
}
.LogOnBoard input[type="submit"]{
    background:#D60000 !important;
    border-radius:15px;
}
.LogOnBoard h1{color:#D60000 !important;}
.LogOnBoard h2{color:#FFF !important;}

footer {
    display: none !important;
}
#globalfooter {
    padding: 0 !important;
    color:cyan !important;
}
#globalfooter #left:after {
    content: " || Dark Mode made by Janusze Informatyki © ||";
}


/* *** MAIN PAGE *** */
header nav ul:last-of-type li {
    background: #D60000 !important;
}
header nav ul:first-of-type  {
    background: #252525  !important;    
}
header a h1{
    background: #D60000 !important;
}
.holder:before, .holder:after {
    display: none !important;
}
.linkownia > .newAppLink{
display:none !important;
}
#panel_wiadomosci .description{
    color: #000 !important;
}
.widget{
    background: #D60000 !important;
}
.background-img{
    display:none !important; 
}
.bottomBar {
    border-top: 1px solid #FFF !important;
    background: #000 !important;
    color:cyan !important;
}
.bottomBar span:after {
    content: " || Dark Mode made by Janusze Informatyki © ||";
}
body > nav > ul  {
    border-top: 3px solid #fff !important;
}
body > nav > ul > li  {
    border-top: 1px solid #fff !important;
    background:#D60000 !important;
}
#idSection {
    background: #5a5a5a !important;

}
nav ul li a[open]{
    background:#a00 !important;
    color: #FFF !important;
    border:0 !important; 
}
body > nav > ul > li:last-of-type{
  border-bottom:0 !important;
}
/* $$ Data in main $$ */
.mainContainer{
    background: #595252 !important;
    margin:0 !important;
}
.mainContainer h1,.mainContainer h2, .mainContainer h3 {
    color: #ff0000c0 !important;
}
.filters div:last-of-type span{color:#FFF !important}


/* !! Oceny !! */
/* Czarne oceny > Białe oceny */

.ocenaCzastkowa[style*="color:#000000;"]{
    color:#fff !important;
}
table thead tr{
    background: rgb(171, 47, 47) !important;
}
 tr, th, td{
    border:1px solid #FFF !important;
        color:white !important;
}
table tbody tr:nth-child(even) {
    background: #FF000010
}
.g-holder{
    color: #FFF !important;
}
.tooltip{
    background: #000 !important;
    color:#FFF !important;
    border:1px solid #FFF !important;
}
/* !! Frekwencja !! */
.ui-dialog{
    background: #000 !important;
}
.ui-dialog-buttonpane{
    background: #000 !important;
}
.ui-dialog-titlebar{
    background:#252525 !important;
}
#excuseForm span{
    color: #FFF !important;
}
.ui-button {
    background: #F00 !important;
    border:0 !important;
    border-radius:15px;
    color: #FFF !important; 
}
.ui-button:active{
    background: #ab2f2f !important;
    border: 1px solid white !important;
}
.excuse{
    background:#252525 !important
}
.excuse img{
    margin-left: 5px !important;
    filter: contrast(100) !important;
}
#excuseBtn{
    background: #F00 !important;
    border-radius: 15px !important;
}
.navigation a{
    background:#2b2b2b;
    border:1px solid #fff !important;
}
.tableForm{
    background: #0000 !important;
}
form table tbody td span{
    color:#FFF !important;
}

.x-obecnosc img{
    display:none !important;
}
.x-obecnosc:before{
    content: "[•]";
    color: #FFF !important;
    text-shadow: 1px 1px 2px #000;
}
.x-nieobecnosc-uspr{
    background: #ffd100c4 !important;
}
.x-nieobecnosc-uspr img{
    display:none !important;
}
.x-nieobecnosc-uspr:before{
    content:"[U]";
    color: #FFF !important;
    text-shadow: 1px 1px 2px #000;
}
.x-nieobecnosc-nieuspr{
    background: #FF0000 !important;
}
.x-nieobecnosc-nieuspr img{
    display:none !important;
}
.x-nieobecnosc-nieuspr:before{
    content: "[-]";
    color:#FFF !important;
    text-shadow: 1px 1px 2px #000;
}
.x-sp-usprawiedliwione {
    background: #00bd00 !important;
}
.x-sp-usprawiedliwione img{
    display:none !important;
}
.x-sp-usprawiedliwione:before{
    content: "[S]";
    color: #FFF !important;
    text-shadow: 1px 1px 2px #000;

}
.x-nieobecnosc-przycz-szkol{
    background: #00F !important;
}
.x-nieobecnosc-przycz-szkol img{
    display:none !important;
}
.x-nieobecnosc-przycz-szkol:before{
    content: "[Z]";
    color:#FFF !important;
    text-shadow: 1px 1px 2px #000;
}
.x-sp-zwolnienie{
    background: #8900a1 !important;
}
.x-sp-zwolnienie img{
    display:none;
}
.x-sp-zwolnienie:before{
    content: "[dom]";
    color: #FFF;
    text-shadow: 1px 1px 2px #000;     
}
.x-sp-nieusprawiedliwione  {
	background-color: #ff9d00 !important;
}
.x-sp-nieusprawiedliwione img{
    display:none;
}
.x-sp-nieusprawiedliwione:before{
    content: "[S]";
    color: #FFF;
    text-shadow: 1px 1px 2px #000;     
}

/* !! Uwagi !! */
article{
    border-bottom:2px solid #F00 !important; 
    padding:0;
}
article:before{
    display:none;
}
article, .tytul, .wartosc, .daneWiersz{
    color: #FFF !important
}
/* !! Uczeń na tle klasy !! */
form{
    background:#252525 !important;
    border: solid 1px white;
}
.ct-legend span{
    color:#FFF !important;
}
/* !! Plan !! */
.presentData  span{
    color:#FFF !important
}
.description, .description div, .description span{
    color: #FFF !important
}
/* !! Lekcje zrelizowane !! */
.calendar{
    background:#252525 !important;
    
}
.calendar span{
    color: #FFF !important;
}
.calendar td:hover span{
    color: #F00 !important;
    border-radius:50px !important;
    -ms-border-radius:50px !important;
}
/* !! Sprawdziany !! */
table .text-center td div:first-of-type {
    background: #000;
    color:#FFF !important
}
table td > div{
    color:#FFF !important;
}

table td div b{
    color: #FD6700 !important;
}
legend{color:#FFF !important;}
.break-word {
    color: #FFF !important;
}

/* !! Zadania domowe !! */
fieldset + .navigation + table  div:first-of-type{
    background: #ab2f2f !important;
    border-bottom: 1px solid white
}
fieldset + .navigation + table  div span{
    color: #FFF !important;
}
/* !! Pomoc !! */
dl{
    border-bottom: 1px solid #F00 !important;
    color: #FFF !important;
}
dl dt{
    color: #D60000 !important;
}
dl dd{
    color: #FFF !important;
}
dl a{
    color: #D60000 !important;
}
a:before{
    filter: contrast(500%) !important;
}
.button{
    background:#F00 !important;
    border-radius:15px !important;
}
li{
    color: #FFF !important;
}
p{
    color: #FFF !important;
}
.panel{
    transform:none !important;
    float:left !important;
    position: relative !important;
}
}






