@-moz-document domain("sitepoint.com") {
/* New Icons */


/* Arrows _______________________________________________________________________*/

.splide__arrow--prev svg {
    transform: none!important;
}
/* Up _________________*/

selector
{
	background: url(https://i.ibb.co/bvZmSFj/red-up.png) no-repeat!important;
	box-sizing: border-box;
}

/* Down _________________*/

.d-icon-chevron-down
{
	background: url(https://i.ibb.co/z7MRv74/direction-arrow-down.png) no-repeat!important;
	box-sizing: border-box;
}

/* Left _________________*/

.control-left svg,
[aria-label="Previous slide"] svg
{
	background: url(https://i.ibb.co/mRKFvH8/direction-arrow-left.png) no-repeat!important;
	box-sizing: border-box;
}

/* Right _________________*/

.control-right svg,
[aria-label="Next slide"] svg
{
	background: url(https://i.ibb.co/LQDKzQT/direction-arrow-right.png) no-repeat!important;
	box-sizing: border-box;
}

.control-right svg,
.control-left svg,
.d-icon-chevron-down,
[aria-label="Next slide"] svg,
[aria-label="Previous slide"] svg
{
	background-size: 29px!important;
	height: 33px!important;
	padding-left: 39px!important;
	width: 1px!important;
}

/* Heart ________________________________________________________________________________*/

/* On ___________________*/

selector
{
	background: url(https://i.ibb.co/FB9kX4W/healthy-heart.png) no-repeat!important;
	box-sizing: border-box;
}

/* Off ___________________*/

[title="like this post"] svg
{
	background: url(https://i.ibb.co/cD452WP/healthy-hear-darkt-32x32.png) no-repeat!important;
	box-sizing: border-box;
}

[title="like this post"] svg
{
    background-size: 15px!important;
    height: 26px!important;
    padding-left: 22px!important;
    width: 1px!important;
}

/* Search ___________________________________________________________________________*/

.fa-search:before {
    content: "";
}

.fa-search:before,
.s1jmsysx svg
{
	background: url(https://image.ibb.co/hVxuzR/search.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-search:before,
.s1jmsysx svg
{
	background-size: 25px!important;
	height: 26px!important;
	padding-left: 26px!important;
	width: 1px!important;
}

}

