css:
@-moz-document domain("thingofthings.wordpress.com") {
body {
    background: #101510;
    color: #ddbbdd !important;
}

p {
    color: #ddbbdd !important;
}

.post-entry {
    background: #192719;
    color: #ddbbdd;
    width: 360px;
    padding: 15px 20px 0 30px;
    margin-right: 64px;
}

.comment-text {
    background: #192719;
}

#comments h3,
a {
    color: #ebe !important;
}

.post-date em,
#comments h3 span, 
#nav-below a,
#menu li {
    background: transparent !important;
}

.post-date span,
.post-info,
.post-extras,
#main-title #site-description,
.post-title h1 {
    color: #ccc;
}

#recent-comments-4,
.wpcnt,
.widget_eu_cookie_law_widget,
#actionbar,
.sharedaddy{
    display: none !important;
}
#secondary {
    float: right;
}
#primary {
    float: left;
}
#content {
    border-left: none;
}
}
