/* ----- ----- ----- ----- ----- ----- ----- ----- ----- -----
   Card Labels
   ----- ----- ----- ----- ----- ----- ----- ----- ----- ----- */
.card-label-red {
	background-color: hsl(0, 45%, 55%) !important;
}
.card-label-orange {
	background-color: hsl(30, 45%, 55%) !important;
}
.card-label-yellow {
	background-color: hsl(60, 45%, 55%) !important;
}
.card-label-lime {
	background-color: hsl(90, 45%, 55%) !important;
}
.card-label-green {
	background-color: hsl(120, 45%, 55%) !important;
}
.card-label-sky {
	background-color: hsl(180, 45%, 55%) !important;
}
