css:
 @-moz-document domain(mangadoor.com) {
     body {
         color: white !important;
    }
   
   #taboola-above-article-thumbnails{
     display:none;
     }
   .trc_related_container {
    	display:none !important;
	}
     .container-fluid {
         background-color: black !important;
         color:black !important;
    }
   .btn, .btn-primary, .tag-links{
     background-color: black  !important;;
         background-image: radial-gradient( rgba(0, 150, 0, 0.75), black 120% )  !important;; 
         color:white !important;
         font-size:0.8em !important;
   }
   .tag-links > a {
       background-color:green;
   }
   .tag-links a::before {
    border-right: 8px solid green !important;
}
.media-heading + div {
    width:100% !important;
}

     li{
         background-color: black;
         background-image: radial-gradient( rgba(0, 150, 0, 0.75), black 120% );
         color:white !important;
    }
   .nav > li{
   	    background-image: none !important;
   }
     .well {
         background-color: black;
         background-image: radial-gradient( rgba(0, 150, 0, 0.75), black 120% );
         border-color: green !important;
         height: 6em !important;
         text-align:center !important;
         vertical-align:center !important;
         padding:1% !important;
         text-overflow: ellipsis !important;
    }
   .panel-default{
   		border-color:rgba(0, 150, 0, 0.75) !important;
   }
   .list-group-item{
   		border-color: rgba(0, 150, 0, 0.75) !important;
   }
     .panel-body{
         background-color: black;
         background-image: radial-gradient( rgba(0, 150, 0, 0.75), black 120% );
    }
   /*
     .navbar, .navbar-default{
         background-color: black;
         background-image: radial-gradient( rgba(0, 150, 0, 0.75), black 120% );
    }
   */
     .alert-success {
         background-color: black !important;
         border-color: green !important;
         color: #ffffff !important;
    }
     .panel-success>.panel-heading {
         background-color: black;
         background-image: radial-gradient( rgba(0, 150, 0, 0.75), black 120% );
         color:white !important;
         border-color: green !important;
         color: #ffffff !important;
    }
     .panel-default > .panel-heading{
         background-color: black;
         background-image: radial-gradient( rgba(0, 150, 0, 0.75), black 120% );
         color:white !important;
    }
     .panel-success {
         border-color: green !important;
    }
     .label-warning {
         height: 35px !important;
         background-color: black;
         background-image: radial-gradient( rgba(0, 150, 0, 0.75), black 120% );
         border: 1px solid rgba(0, 150, 0, 0.75) !important;
         word-break: break-word !important;
         border-radius: 2px !important;
         word-break: break-word !important;
         padding-top: 4% !important;
         border-bottom-left-radius: 0px !important;
         border-bottom-right-radius: 0px !important;
         color:white !important;
    }
     .label-warning:hover {
         background-color: white !important;
         border: 1px solid black !important;
         color:black !important;
    }
     a > strong {
         color: white !important;
    }
     .ads{
         display:none;
    }
     .col-sm-12{
         margin-top: -7.5%;
    }
     div + .alert-success {
         display:none;
    }
     .label{
         white-space: normal;
         color:white !important;
    }
     .manga-item{
         color:white !important;
         border: 1px solid green;
         padding: 1%;
    }
     input[type="submit"] {
         display: inline-block;
         margin-bottom: 0;
         font-weight: normal;
         text-align: center;
         vertical-align: middle;
         -ms-touch-action: manipulation;
         touch-action: manipulation;
         cursor: pointer;
         background-image: none;
         border: 1px solid transparent;
         white-space: nowrap;
         padding: 10px 15px;
         font-size: 15px;
         line-height: 1.42857143;
         border-radius: 4px;
         -webkit-user-select: none;
         -moz-user-select: none;
         -ms-user-select: none;
         user-select: none;
         color: #ffffff;
         border-color:  rgba(0, 150, 0, 0.75) !important;
       background-color:  rgba(0, 150, 0, 0.75) !important;
         width:100%;
         box-shadow: 0 9px #999;
    }
     input[type="submit"]:active {
         background-color: black;
         box-shadow: 0 5px #666;
         transform: translateY(4px);
    }
     #autocomplete > input[type="text"] {
         width:100%;
         color:black !important;
    }
   input[type="text"] {
         color:black !important;
    }
   .active > a { background-color:black !important;}
     .pull-right{
         color:white !important;
    }
     div + div {
         color:white !important;
         bottom: 0px !important;
    }
     a > strong {
         color: #18bc9c;
    }
     .thumbnail{
         border: 1px solid green;
         background-color: black !important;;
         background-image: radial-gradient( rgba(0, 150, 0, 0.75), black 120% ) !important;;
         border-top-left-radius: 0px !important;
         border-top-right-radius: 0px !important;
         color:white !important;
    }
     img:not(.img-responsive){
         transition: color 1s ease-in-out;
         -webkit-transform: scaleX(1);
         transform: scaleX(1);
         perspective: 1000px;
         transition: transform 2s;
         transform-style: preserve-3d;
    }
     img:hover:not(.img-responsive) {
         -webkit-transform: scaleX(-1);
         transform: scaleX(-1);
         perspective: 1000px;
         transition: transform 2s;
         transform-style: preserve-3d;
    }
     .manga-nam{
         background-color: black;
         background-image: radial-gradient( rgba(0, 150, 0, 0.75), black 120% );
         color:white !important;
    }
     .fa {
         color: white !important;
    }
     li > a:hover{
         background-color:rgba(0, 150, 0, 0.75) !important;
         color:white !important;
         border-radius:50px;
         -webkit-transition: background-color 1000ms linear;
         -ms-transition: background-color 1000ms linear;
         transition: background-color 1000ms linear;
    }
     body{
         background-color: black;
         background-image: radial-gradient( rgba(0, 150, 0, 0.75), black 120% );
         height: 100vh;
         font: 1.5rem Inconsolata, monospace !important;
         text-shadow: 0 0 5px #C8C8C8 !important;
    }
     body::after {
         /*content: "";*/
         position: absolute;
         top: 0;
         left: 0;
         width: 100%;
         height: 100vh;
         background: repeating-linear-gradient( 0deg, rgba(black, 0.15), rgba(black, 0.15) 1px, transparent 1px, transparent 2px );
    }
   /*
     ::selection {
         background: #0080FF;
         text-shadow: none;
    }
   */
     .hotmanga-header{
         background-color: black;
         background-image: radial-gradient( rgba(0, 150, 0, 0.75), black 120% );
    }
    h1,h2,h3,h4,h5,h6,h7,p{
        font: 1.5rem Inconsolata, monospace !important;
        color:white;
    }
   .collapse, .nav-header{
   		background-color: black;
   }
   #rbox-t2m {
     display:none;
   }
   iframe {
    display:none !important;
    visibility:hidden !important;
  }
  .fa{color:orange !important}

}
 @-moz-document url(http://mangadoor.com/latest-release) {
     .col-sm-12{
     	margin-top:0% !important;
     }
     }
