/* #F4389A */

body   {
    background: #FFF url(/*[[image]]*/) fixed center;
    background-position: 50% 0 !important;
    background-size: cover;
    font-size: 14px !important;
}

#page_header_cont .back {
    overflow: hidden;
    border-bottom: 0;
    background: #FFF;
    height: 43px;
}

body {
    background-size: cover;
    font-family: Segoe UI, Arial, sans-serif;
    font-size: 15px;

}

.fakeinput, div[contenteditable="true"], input.big_text, input.file, input.search, input.text, input[type="button"], input[type="password"], input[type="search"], input[type="submit"], input[type="text"], input[type~="email"], input[type~="password"], input[type~="search"], input[type~="text"], textarea {
    font-family: Segoe UI, Arial, sans-serif;
}

#page_header_cont {
    height: 43px;
}

.page_block, #side_bar_inner, #page_header_cont .back, .chat_onl_inner {
     box-shadow: 0 0 25px rgba(0,0,0,.5);
    border-radius: 0 !important;
}

.im-page:before {
    content: '';
    box-shadow: 0 0 25px rgba(0,0,0,.5);
    position: absolute;
    left: 0;
    top: 15px;
    height: calc(100% - 17px);
    width: 100%;
}

.im-page--dialogs._im_page_dialogs.page_block, .im-page .im-page--history {
    box-shadow: none !important;
}

.chat_onl_inner {
    background-color: #FFF;
}

.page_avatar_img, .page_avatar, .profile_msg_split .profile_btn_cut_left, .profile_msg_split .profile_btn_cut_right, .page_actions_cont.narrow .page_actions_header, .page_actions_cont.narrow .page_extra_actions_btn, .button_blue button, .button_gray button, .button_light_gray button, .flat_button, .page_cover{
    border-radius: 0 !important;
}

.chat_tab_imgcont.online.mobile:after {
    background-color: #FFF;
}
.chat_tab_imgcont.online:after {
    border-color: #FFF;
}

/* Header */

.top_home_logo {
    background: /*[[color]]*/ url('data:image/svg+xml;utf8,  <svg version="1.1" id="Слой_2" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px"	 width="43px" height="43px" viewBox="0 0 43 43" style="enable-background:new 0 0 43 43;" xml:space="preserve"><polygon style="fill:#FFFFFF;" points="18.281,24.516 23.844,22.961 17.016,18.855 12.102,20.225 12.129,27.44 25.127,35.221 31,32 	31,13 25.063,8.469 12.281,11.938 19.031,16.031 25.094,14.406 25.063,28.438 "/></svg>') !important;
    height: 43px !important;
    width: 43px !important;
    margin: 0 0 0 -30px !important;
}

.top_profile_name {
    font-size: 16px;
    font-weight: 400;
    line-height: 40px;
    color: /*[[color]]*/;
}

.top_profile_arrow {
    display: none;
}

.top_nav_link {
    height: 43px;
}

.top_nav_link:hover, .top_nav_link.active {
    background-color: /*[[color]]*/;
}

.top_nav_link.active{
    filter:  brightness(.9)
}

.top_nav_link:hover .top_profile_name, .top_nav_link.active .top_profile_name {
    color: #FFF;
}

img.top_profile_img {
    width: 43px;
    height: 43px;
    border-radius: 0;
    margin: 0;
}

.input_back_content {
    font-size: 0px !important;
}





input.text.ts_input {
    font-size: 0px !important;
    background: url('data:image/svg+xml;utf8, <svg version="1.1" id="Слой_2" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px"	 width="43px" height="43px" viewBox="0 0 43 43" style="enable-background:new 0 0 43 43;" xml:space="preserve"><path style="fill:/*[[color]]*/;" d="M23.313,12.75c-3.136,0-5.688,2.551-5.688,5.688c0,1.468,0.563,2.803,1.48,3.813l-6.083,6.083	l0.707,0.707l6.11-6.11c0.962,0.746,2.165,1.194,3.474,1.194c3.136,0,5.688-2.551,5.688-5.688S26.448,12.75,23.313,12.75z	 M23.313,23.125c-2.584,0-4.688-2.103-4.688-4.688s2.103-4.688,4.688-4.688c2.585,0,4.688,2.103,4.688,4.688	S25.897,23.125,23.313,23.125z"/></svg> ') no-repeat;
    width: 43px!important;
    padding-left: 19px!important;
    border-left: 8px solid transparent;
    background-position: -8px 0px !important;
    border-radius: 0px;
    margin: 0px 0;
    height: 43px;
    cursor: pointer
}

input.text.ts_input:hover, input.text.ts_input:focus {
    background: /*[[color]]*/ url('data:image/svg+xml;utf8, <svg version="1.1" id="Слой_2" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px"	 width="43px" height="43px" viewBox="0 0 43 43" style="enable-background:new 0 0 43 43;" xml:space="preserve"><path style="fill:#fff;" d="M23.313,12.75c-3.136,0-5.688,2.551-5.688,5.688c0,1.468,0.563,2.803,1.48,3.813l-6.083,6.083	l0.707,0.707l6.11-6.11c0.962,0.746,2.165,1.194,3.474,1.194c3.136,0,5.688-2.551,5.688-5.688S26.448,12.75,23.313,12.75z	 M23.313,23.125c-2.584,0-4.688-2.103-4.688-4.688s2.103-4.688,4.688-4.688c2.585,0,4.688,2.103,4.688,4.688	S25.897,23.125,23.313,23.125z"/></svg> ') no-repeat;
}

input.text.ts_input:hover {
    color: #FFF;
    width: 43px!important;
    padding-left: 19px!important;
    cursor: pointer
}

input.text.ts_input:focus {
    font-size: 13px !important;
    color: #FFF;
    width: 248px!important;
    padding-left: 6px!important;
    background-size: 0%;
    cursor: text
}



.ts_wrap .input_back_content {
    font-size: 0px
}

.head_nav_btns {
    padding-left: 0;
}

.top_nav_btn {
    height: 43px;
    width: 43px;
    background: none !important;
}

.top_nav_btn#top_notify_btn .top_nav_btn_icon {
     background: url('data:image/svg+xml;utf8, <svg version="1.1" id="Слой_3" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px"	 width="43px" height="43px" viewBox="0 0 43 43" style="enable-background:new 0 0 43 43;" xml:space="preserve"><polygon style="fill:none;stroke:/*[[color]]*/;stroke-miterlimit:10;" points="28.5,13.5 13.5,13.5 13.5,25.5 18.374,25.5 21,28.126 	23.626,25.5 28.5,25.5 "/></svg>') 0 1px !important
} 

.top_nav_btn:hover#top_notify_btn .top_nav_btn_icon, .top_nav_btn.active#top_notify_btn  {
     background: /*[[color]]*/ url('data:image/svg+xml;utf8, <svg version="1.1" id="Слой_3" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px"	 width="43px" height="43px" viewBox="0 0 43 43" style="enable-background:new 0 0 43 43;" xml:space="preserve"><polygon style="fill:none;stroke:#FFF;stroke-miterlimit:10;" points="28.5,13.5 13.5,13.5 13.5,25.5 18.374,25.5 21,28.126 	23.626,25.5 28.5,25.5 "/></svg>')!important
} 

.top_notify_count {
        border: 1px solid /*[[color]]*/ ;
        color: /*[[color]]*/;
        background-color: #FFF;
        
    }

.top_nav_btn:hover .top_notify_count, .top_nav_btn.active .top_notify_count {
        border: 1px solid #FFF;
        color: #FFF;
        background-color: /*[[color]]*/
    }

.top_nav_btn .top_nav_btn_icon {
    margin: 0;
    background: 100% / contain no-repeat;
    height: 43px;
    width: 43px;
}



.top_nav_btn#top_audio .top_nav_btn_icon {
    background: url('data:image/svg+xml;utf8,  <svg version="1.1" id="Слой_4" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px"	 width="43px" height="43px" viewBox="0 0 43 43" style="enable-background:new 0 0 43 43;" xml:space="preserve"><path style="fill:/*[[color]]*/;" d="M28.97,13.214l-11.064,1.993l0.028,11.184c-0.426-0.261-0.951-0.422-1.527-0.422	c-1.396,0-2.531,0.911-2.531,2.031s1.136,2.031,2.531,2.031S18.938,29.12,18.938,28v-0.001l0,0l-0.03-11.956l9.061-1.632	l-0.026,10.986c-0.428-0.265-0.955-0.428-1.535-0.428c-1.396,0-2.531,0.911-2.531,2.031s1.136,2.031,2.531,2.031	s2.53-0.91,2.531-2.03l0,0L28.97,13.214z M16.406,29.031c-0.83,0-1.531-0.473-1.531-1.031s0.701-1.031,1.531-1.031	s1.531,0.473,1.531,1.031l0,0C17.938,28.559,17.236,29.031,16.406,29.031z M26.406,28.031c-0.83,0-1.531-0.473-1.531-1.031	s0.701-1.031,1.531-1.031s1.531,0.473,1.531,1.031S27.236,28.031,26.406,28.031z"/></svg> ') 0 -1px !important
}

.top_nav_btn:hover#top_audio .top_nav_btn_icon {
   /* background-color: /*[[color]]*/ !important */
}

.top_nav_btn:hover#top_audio .top_nav_btn_icon {
      filter: grayscale(1) brightness(20);  
}

.top_nav_btn:hover {
    background-color: /*[[color]]*/ !important
}


.top_audio_player .top_audio_player_btn > div {
    background-image: url('data:image/svg+xml;utf8, <svg version="1.1" id="Слой_2" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px"	 width="102.833px" height="25px" viewBox="0 0 102.833 25" style="enable-background:new 0 0 102.833 25;" xml:space="preserve"><line style="fill:none;stroke:/*[[color]]*/;stroke-miterlimit:10;" x1="8.5" y1="7" x2="8.5" y2="18"/><polygon style="fill:none;stroke:/*[[color]]*/;stroke-miterlimit:10;" points="88.469,12.688 81.5,7.406 81.5,18 "/><line style="fill:none;stroke:/*[[color]]*/;stroke-miterlimit:10;" x1="92.5" y1="7" x2="92.5" y2="18"/><line style="fill:none;stroke:/*[[color]]*/;stroke-miterlimit:10;" x1="60.5" y1="6" x2="60.5" y2="19"/><line style="fill:none;stroke:/*[[color]]*/;stroke-miterlimit:10;" x1="65.5" y1="6" x2="65.5" y2="19"/><polygon style="fill:none;stroke:/*[[color]]*/;stroke-miterlimit:10;" points="44.75,12.667 36.5,6.417 36.5,19 "/><polygon style="fill:none;stroke:/*[[color]]*/;stroke-miterlimit:10;" points="12.531,12.688 19.5,7.406 19.5,18 "/></svg>') !important;
}
.top_audio_player .top_audio_player_prev > div {
    background-position: -7px -7px !important
}
.top_audio_player .top_audio_player_next > div {
    background-position: -80px -7px !important
}
.top_audio_player.top_audio_player_playing .top_audio_player_play > div {
    background-position: -56px -5px !important
}
.top_audio_player .top_audio_player_play > div {
    background-position: -33px -5px !important
}
.top_audio_player .top_audio_player_btn .top_audio_player_btn_icon {
    width: 14px;
    height: 15px
}
.top_audio_player .top_audio_player_btn {
    padding: 15px 4px 14px
}
.top_audio_player .top_audio_player_play {
    padding: 13px 4px !important
}
.top_audio_player .top_audio_player_title_wrap {
    margin: 12px 0
}
.top_audio_player .top_audio_player_btn .top_audio_player_btn_icon {
    opacity: 1 !important
}
.top_audio_player_title {
    font-size: 16px;
    font-weight: 400;
    line-height: 40px;
    color: /*[[color]]*/ !important;
    padding-bottom: 3px;
}


div#top_audio_player {
    height: 43px;
    background: #FFF;
    padding-left: 8px;
}
div#top_audio_player:hover {
    background: /*[[color]]*/;
}

div#top_audio_player:hover .top_audio_player_title {
    color: #FFF !important
}

div#top_audio_player:hover  .top_audio_player_btn {
    filter: grayscale(1) brightness(20);
}


.top_audio_play__button:before, .top_audio_play__button, .top_audio_play__button:hover, .top_audio_play__button:hover:before {
    background-color: /*[[color]]*/;
}

.top_audio_play__button:hover {
    filter: brightness(1.2);
}

.top_audio_play__button:after {
     filter: grayscale(1) brightness(20);
}

#top_audio_play {
    padding-left: 5px;
}

/* Notif */

.notifier_baloon_wrap {
    padding: 0 0 10px 10px !important;
}

.notifier_baloon_msg.wrapped, .notifier_baloon_title {
    color: #000 !important;
}

.notifier_baloon, .notifier_baloon.notifier_type_mail {
    background: #FFF;
    box-shadow: 0 0 25px rgba(0,0,0,.5);
    border-radius: 0;
}


.notifier_baloon_msg span.group_link, .notifier_baloon_msg span.mem_link, .notifier_baloon_wrap a, .im-page-pinned--name, .im-page-pinned--media {
    color: /*[[color]]*/;
}

.audio-msg-track .audio-msg-track--btn {
    background-color: /*[[color]]*/ !important;
}

.audio-msg-track .audio-msg-track--wave-wrapper .audio-msg-track--wave path {
    stroke: /*[[color]]*/;
}


div#ts_cont_wrap {
    border-radius: 0;
    margin: 4px 0 0 7px;
    box-shadow: 0 10px 33px -8px rgba(0,0,0,.5);
}



