.body_im .side_bar {
    overflow: visible;
}

#side_bar #l_nwsf .left_icon {
    background-position: 7px -916px;
}

#side_bar #l_doc .left_icon {
    background-position: 7px -271px;
}

#side_bar .genre11 .left_icon {
    background-position: 7px -500px;
}

#side_bar ol li .left_row {
    color: /*[[color]]*/;
}

#side_bar ol li .left_row .left_count_wrap {
    margin: 0;
    padding: 6px 6px;
    font-size: 16px;
    font-weight: 400;
    line-height: 14px;
    background: #0000;
    color: /*[[color]]*/;
}


.left_count_wrap {
    position: relative;
}




#side_bar ol li {
    margin-left: -15px;
    padding-left: 7px;
    transition: .2s
}


#side_bar ol li .left_row:hover {
    background: #0000;
}

div#side_bar_inner {
    background: #ffffff;
    padding: 15px 15px 10px;
    margin-left: -30px;
    margin-top: 57px;
}

.left_count_wrap:after {
    content: '';
    position: absolute;
    left: 0;
    top: 0;
    height: 100%;
    width: 100%;
    border-radius: 2px;
    transform: translatez(0);
    animation: glowing 1s linear infinite
}

@keyframes glowing {
    0%,75%, 100%{
     box-shadow: 0 0 0px 1px  transparent;
    }
    25%, 50% {
     box-shadow: 0 0 0px 1px  /*[[color]]*/;
    }
}

.left_settings {
    margin: -28px 0 0 -36px;
    filter: brightness(2) grayscale(1) !important;
}

/* im */

.im-page_classic.im-page .im-page--header, .im-page_classic .im-page--chat-header {
    border: none !important;
    margin: -48px 0 0 0;
    box-shadow: 0 4px 10px -2px  #0003, 0 -4px 10px -2px  #0003
}

.im-page_classic .im-page--chat-header {
    margin: 0;
}

