@-moz-document domain("danisch.de") {
/* ==UserStyle==
@name         DANISCH.DE Dark Theme 2019
@namespace    de.sidneys.userstyles
@version      1.0.0
@description  A dark theme for Hadmud Danischs Blog. Very gentle on your eyes.
@author       sidneys <sidneys.github.io@outlook.com> (https://github.com/sidneys)
@homepageURL  https://userstyles.org/users/301302
@supportURL   https://forum.userstyles.org/messages/add/sidneys
@license      MIT
@preprocessor default
==/UserStyle== */
}

@-moz-document domain("danisch.de") {
/* ====================================
   VARIABLES
   ==================================== */

/* GLOBAL
   ==================================== */

:root
{
  --color-container-dark-0: rgb(60, 60, 60);
  --color-container-dark-1: rgb(40, 39, 36);
  --color-container-dark-2: rgb(33, 33, 32);
  --color-border-1: rgb(90, 90, 90);
  --color-border-2: rgb(100, 100, 100);
  --color-text-0: rgb(153, 153, 153);
  --color-text-1: rgb(204, 204, 204);
  --color-text-2: rgb(225, 225, 225);
  --color-text-3: rgb(225, 225, 225);
  --color-text-4: rgb(255, 255, 255);
  --color-accent-0: rgb(251, 143, 81);
}

}

@-moz-document domain("danisch.de") {
/* ====================================
   ELEMENTS
   ==================================== */

/* GLOBAL
   ==================================== */

html,
html *
{
    -webkit-font-smoothing: subpixel-antialiased;
    -moz-osx-font-smoothing: grayscale;
    font-smooth: auto;
}

a
{
    color: var(--color-accent-0);
}

body
{
    color: var(--color-text-1);
    background-color: var(--color-container-dark-0);
}

}

@-moz-document domain("danisch.de") {
/* ====================================
   ELEMENTS
   ==================================== */

/* TYPOGRAPHY
   ==================================== */


header h1,
footer h1
{
    color: var(--color-text-4);
}

header
{
    margin-bottom: 2rem;
}

header nav
{
    margin: 0;
    padding: 1rem 0;
}

body .wpboxanfang
{
    background-color: var(--color-container-dark-1);
    border-width: 0.5px;
    border-color: var(--color-border-1);
    padding: 0.6rem;
}

div.blogindexentry h1,
div.blogindexentry a.indextitle
{
    color: var(--color-text-2);
    font-weight: 400;
}

div.blogindexentry .wpdate,
div.blogindexentry .wpcomments,
div.blogindexentry .wpedit
{
    color: var(--color-text-1);
}

div.blogindexentry .entry p
{
    color: var(--color-text-0);
    line-height: 1.25;
}

div.blogindexentry .entry p:first-child
{
    margin-top: 1rem;
}

div.blogindexentry .entry p:last-child {
    margin-bottom: 0;
}

div.blogindexentry,
div.blogindexad
{
    border-width: 1px;
    border-color: var(--color-border-2);
}

div.blogindexentry,
div.blogindexad
{
    padding: 2rem 0;
    margin: 0;
}

blockquote,
.quote
{
    border-color: var(--color-border-2);
    background-color: var(--color-container-dark-2);
    border-radius: 0 !important;
    border-width: 1px;
}

.rund,
.infobox, 
.info,
.verlaufbox,
pre, .gesetz,
blockquote,
.quote,
.notice,
.radioaktiv,
.tipp,
.forbidden,
.death,
body .wpboxanfang
{
    border-radius: 0.2rem;
    box-shadow: none;
    min-height: unset;
}

nav.sidemenu .wpboxanfang h2,
#wp-calendar caption
{
    font-size: 0.8rem;
}

nav.index .alignleft,
nav.index .alignright
{
    padding: 1rem 0;
}
}
