css:
@-moz-document url("https://app.roll20.net/editor/popout") {
.sheet-section-inventory textarea {
    font-family: Consolas;
    width: calc(92vw - 10px);
    height: calc(80vh - 10px);
    resize: none;
}
}

@-moz-document url("https://app.roll20.net/editor/") {
.sheet-section-inventory textarea {
    font-family: Consolas;
    resize: both;
}
#macrobar_macros {
    white-space: inherit;
    padding-top: 0;
    padding-bottom: 0;
}
}
