css:
@-moz-document domain("fz.se") {
:root {
    --color-dark: #333;
    --color-darker: #272727;
    --color-darkness: #000;
    --color-fz-red: #ff4258;
    --color-white: #bdbdbd;
    --color-whiter: #f1f1f1;
    --color-whiteness: #fff;
    --color-grey: #888;
    --color-transparent: #0000;
}

.fp-news .fpni-bg,
.primary-content .pc-bg,
.thread-list .tl-thread .tl-col,
.forum-post .fp-content,
.forum-post .fpp-top,
.forum-post .fpp-bottom,
.flc-col,
.forum-list .fls-col,
.article-list-item .ali-banner,
.article-list-item .ali-bg,
.fp-news .fpni-related,
.fp-news .fpni-credit,
.content-body,
.content-primary,
.bbcode-engagementbox
{
    background: var(--color-darker);
}

.bbPoll > .inner {
    background: var(--color-dark)
}

.bb-quote,
.quickreply.form-box, 
textarea,
.form-box {
    background: rgba(0, 0, 0, 0);
}

.bb-quote.is-expandable > .bbq-frame > .bbq-fade {
    background: linear-gradient(to bottom,
        rgba(35,35,35,0) 0,
        rgba(35,35,35,0) 45%,
        rgba(35,35,35,1) 100%);
}

.bbq-head,
.bbq-content .bbParagraph, .form-box h6,
.bbPoll > .inner .optionLabel,
.slpm-item,
.votesRel,
.votesAbs,
.pollSums,
ul li,
a.acc-text,
.bbcode-engagementbox,
.cards .card .title a {
    color: var(--color-white);
}

.fpp-title,
div.fp-content > .fp-text > .bbcode > .bbParagraph, 
textarea[id^='__laika_cnt.textarea'] {
    color: var(--color-whiter);
}

.fpni-meta,
.fpnim-primary,
.fpnim-secondary,
.fpnim-date,
.bbParagraph,
.fpal-items,
.fp-articlelist .fpali-title a,
.fp-news .fpnih-title,
.pch-header,
ul.bbList li,
.article-prisjakt,
h1.pch-title,
.bbcode h2,
.bbcode h3,
.ab-detail .ab-title,
.ab-detail .ab-name,
.pages a,
.pc-header .pch-descr,
.tl-col a.tlt-title,
.tlc-spacing,
.fp-content .fp-title,
.flsl-title,
.flsl-byline,
h1.phi-username,
div.phit-primary,
.side-menu h4,
.side-menu ul li a,
h2.ali-title a,
#createForm,
.slpm-def,
.slpm-term,
.staff-list h1,
.bbPoll > .inner .pollQuestion,
h1,
h2.title > a
{
    color: var(--color-whiteness);
}

div.fp-text > .bbcode > .bbParagraph,
.fp-signature .bbParagraph,
td.cell p.bbParagraph {
    color: var(--color-darker);
}

.fpp-meta,
.fpp-online,
.fps-content > .bbcode > .bbParagraph,
.flsc-spacing .flscs-descr,
.tanukiControl > .tcLabel,
.fp-news .fpni-related a,
.fp-news .fpni-credit {
    color: var(--color-grey);
}

div.fpp-name,
.flscs-title,
.bbcode h1,
.fppp-name,
.fpppt-primary,
.tlc-posts,
.thread-list .tlt-meta a,
.content-text .descr {
    color: var(--color-fz-red);
}

.article-prisjakt {
    background: #333;
    box-shadow: 0px 5px 20px -5px rgba(0,0,0,0.75);
    -webkit-box-shadow: 0px 5px 20px -5px rgba(0,0,0,0.75);
    -moz-box-shadow: 0px 5px 20px -5px rgba(0,0,0,0.75);
}

.quickreply.form-box {
    box-shadow: 0px 5px 20px -5px rgba(0,0,0,0.75);
    -webkit-box-shadow: 0px 5px 20px -5px rgba(0,0,0,0.75);
    -moz-box-shadow: 0px 5px 20px -5px rgba(0,0,0,0.75);
    border: 1px solid var(--color-darkness);
}

.pages .next-page div {
    width: 6px;
    height: 10px;
    background-position-x: -650px;
    background-position-y: -50px;
}

.pages .prev-page div {
    width: 6px;
    height: 10px;
    background-position-x: -600px;
    background-position-y: -50px;
}

.article-bbc-infobox {
    background: rgba(0, 0, 0, 0.3);
    border: 1px solid var(--color-darkness);
}

::-webkit-scrollbar-track,
.thread-list.highlight-stickies .tl-thread.is-sticky .tl-col {
	background-color: var(--color-darkness);
}

::-webkit-scrollbar {
	width: 6px;
	background-color: var(--color-darkness);
}

::-webkit-scrollbar-thumb,
a.next-page,
a.last-page {
	background-color: var(--color-fz-red);
}

.fp-news .fpn-footer {
    background: var(--color-dark);
    border-top: 1px solid var(--color-darkness);
}
.fp-news .fpnf-button {
    border-right: 1px solid var(--color-darkness);
}

.fp-news .fpnf-button:hover .fpnfb-label {
    color: #000;
}

.fp-news .fpnfb-label {
    color: #fff;
}

.fp-news .fpn-item+.fpn-item,
.article-list-item+.article-list-item,
.cards .card {
    border-top: 1px solid var(--color-darkness);
}

.ninja-search .ns-field, textarea, .form-box {
    border: 1px solid var(--color-dark);
}
}
