/* v 1.68
 * Name  : My Tianya.cn 
 * Author : ilinkcn
 * Updated        : 2011.11.26
 * Description : 新版天涯去广告+宽屏
 * Usage : 用 Stylish 扩展导入或者将之粘贴到你的 userContent.css 文件中。
 *+百宝箱改版
 *+细节改进
 *+增加论坛首页支持（仅1366*768）
 *+细节改进
 *+紧急修正部分页面不能拉成全屏显示的问题
 *+添加部分改版页面支持
 *+更改页面字体配色
 *+增加帖子页面的修改
 *+细节改进
 *+修着若干域名问题
 *+去除两侧空白
 *+增加注释
 *+帖子页面拉伸
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tianya.cn") {

/*antiAD*/
/*#tianyatopguide_nav,#apublishArea,#bottom_nav,#bottom_nav_container,*/
.ad,.focustop2,
.left_ad line_c,
#adContentDiv,#adsp_content_title_banner_left,#adsp_content_title_banner_right,
#tianyaSense1,#tianyaSense2,#tianyaSense3,#tianyaSense4,#randPanel,#responsebanner,#adsp_list_top_banner,#adsp_content_adtopic,#adsp_content_top_banner,
#adsp_content_banner_1,#adsp_content_banner_2,#adsp_content_banner_3,#adsp_content_banner_4,
#adsp_content_left_banner,#adsp_content_banner_1,#adsp_content_banner_2,#adsp_content_adtopic,#adsp_content_replybox_img_1,
#google_ads_div_bbs_index,
#tianyaBrandSpan1,
#serviceEntranceDIV,
#couplet_left_adsp_banner_couplet,#couplet_right_adsp_banner_couplet,#tj_top_div,#couplet_left_NULL,
#adsp_list_floatDiv,#adsp_list_top_text_root,
.ad_w_box,.ad_l_2,.focus_rig,.btmad,.wdk_user_status_span,.topnav,
.focus_ad,.list_no,
.banner710_80,.banner230_80,
#CL_670_80_AD_1,#CL_270_80_AD_1,#CL_670_80_AD_2,#CL_270_220_AD_1,#CL_710_80_AD_1,#CL_230_80_AD_1,
#FOCUS_270_80_AD_1,#FOCUS_270_80_AD_2,#FOCUS_270_80_AD_3,#FOCUS_670_80_AD_1,#FOCUS_670_80_AD_2,#FOCUS_670_80_AD_3,#FOCUS_230_230_AD_1,#FOCUS_250_120_AD_1,#CT_710_80_AD_1,
IFRAME[name="google_ads_frame"],
td[width="137"],td[width="100"],
#bottom_hackiframe

{display:none !important}

/*更改页面字体配色
table a:link{color:#3B5998 !important; text-decoration: none !important}
table a:visited{color:#777  !important; text-decoration: none !important}
table a:hover{color:#3399FF !important;text-decoration: none !important}
u,u:link,u:visited{color:#C0C0C0 !important;text-decoration:none !important }
u:hover{padding-top:2px !important;background:#eeeeee !important; text-decoration:underline !important}
*/

/*字体表*/
*{font-family: Microsoft Yahei, Arial, Helvetica, sans-serif !important; }

/*拉伸上下导航条*/
#topNav,#bottomNav{
background-position:center center !important;
background-repeat:repeat-x !important;
}

/*修正左侧导航条*/
#leftNav {
bottom:0 !important;
top:0 !important;;
}

/*拉伸元素*/
#forumWrapper,
#topNavWrapper,#bottomNavWrapper,#bottomNav,#topNav,#topNavWrapper,#bottomNavWrapper,#bottom_nav,#bottom_nav_container,
#mainDiv,
#conArea,#forumContentDiv,#forumContentDiv #content
{
max-width:100% !important;
width:100% !important;
}

#topNav .center {padding:5px 7px 3px 445px !important;}

/*用户如果发现帖子列表页面不能占满屏幕，请调整这个值*/
#forumContentDiv table,#newmainmidleft{width:90% !important;margin:2px !important;}
#postlistwrapper .listtable{width:100% !important;margin:2px !important;}

/*版头*/
#forumContentDiv table[width="640" ][height="22px"][ cellspacing="0"][ cellpadding="0"][ border="0"]{
font-size:26px !important;
font-weight: bold !important;
line-height:40px !important;
padding:0px !important;
margin:0px !important;
text-align: justify !important;
text-shadow:0px 0px 10px #000;
}


/*字体修正*/
font[face="wingdings"][color="blue"]{display:none !important}

#forumWrapper a:link{font-size: 16px !important;}

}


/*论坛首页*/
@-moz-document 
url-prefix("http://www.tianya.cn/bbs/index.shtml")
{
#pageright{width:100% !important;}
.box{
width:1180px !important;
padding-left:130px !important;
}
.fence_left {width:900px !important;}
}

/*帖子页面*/
@-moz-document 
url-prefix("http://bbs.city.tianya.cn/new/tianyacity/Content"),
url-prefix("http://bbs.city.tianya.cn/new/TianyaCity/content"),
url-prefix("http://bbs.city.tianya.cn/tianyacity/Content"),
url-prefix("http://bbs.city.tianya.cn/tianyacity/content"),
url-prefix("http://bbs.city.tianya.cn/tianyacity/articleslist"),
url-prefix("http://groups.tianya.cn/bulo/ShowArticle"),
url-prefix("http://blog.tianya.cn/blogger/post_show"),
url-prefix("http://www.tianya.cn/publicforum/Content/"),
url-prefix("http://cache.tianya.cn/publicforum/Content/"),
url-prefix("http://www.tianya.cn/publicforum/content"),
url-prefix("http://cache.tianya.cn/publicforum/content"),
url-prefix("http://www.tianya.cn/techforum/content"),
url-prefix("http://cache.tianya.cn/techforum/content"),
url-prefix("http://www.tianya.cn/new/Publicforum/content"),
url-prefix("http://cache.tianya.cn/new/Publicforum/content"),
url-prefix("http://www.tianya.cn/new/publicforum/content"),
url-prefix("http://cache.tianya.cn/new/publicforum/content"),
url-prefix("http://www.tianya.cn/new/techForum/content"),
url-prefix("http://cache.tianya.cn/new/techForum/content"),
url-prefix("http://www.tianya.cn/new/TechForum/Content"),
url-prefix("http://cache.tianya.cn/new/TechForum/Content"),
url-prefix("http://groups.tianya.cn/tribe/showArticle"),
url-prefix("http://ebook.tianya.cn/forum/content")
{

/*拉伸帖子页面*/
.body,.body2,.wrapper,.allpost,.pagewrap,.mcon,
#pContentDiv,#tech_body,#content,
#pContentDiv table,#content table,
.comment
{
width:100% !important;
padding: 0!important;
margin: 0 !important;
}

.wrapper{
height:auto !important;
}

/*主题*/
body,.body,.body2,.body3,.pagewrap{background: #69c !important;padding: 0px !important;margin: 0px !important;}
.content,#content{background:#fff !important;padding: 0px !important;margin: 0px !important;}

/*TABLE{background: #567 !important;}*/
#pContentDiv TABLE{background: #eeefff !important;}
#pContentDiv td .content td,.post,.allpost,.mcon{background:#fff !important;}
.post,.allpost,.mcon{line-height:26px !important;MARGIN: 0px !important;font-size: 18px !important}


/*位置*/
.myplace,.guide{
background: #555 !important;
padding: 0 !important;margin: 0 !important;
}
.footer,.foot{
background: #555 !important;
padding: 0 !important;margin: 0 !important;
width:100% !important;
height:20px !important;
}
.myplace a,.lb12,.guide a,.footer a{color: #abc !important;font-size:14px !important;}


/*分页*/
#pageForm,#pageDiv,#pageDiv1,#pageDivTop,#pageDivBottom,#FORM[id=pageForm],#pageDivTop td,#cttPageDiv,#cttPageDiv1,
.pages
{background: #69c !important;color: #e7f0ec !important;padding: 0px !important;margin: 0px !important;}


/*回复框*/
.respond{width:100% !important;margin: 0px !important;}
FORM#FormResponse,#FormResponse TABLE,.respond,#adsp_content_replybox_frame_1{background: #eeefff !important;}
FORM#FormResponse,
#adsp_content_replybox_frame_1 table{
margin-left:20px !important;margin-right:auto !important;clear:both !important;
}

INPUT#strAlbumPicURL {width:400px !important;}
#adsp_content_replybox_area,#adsp_content_replybox_box{width:600px!important;margin: 0px !important;}

/*发表按钮*/
#submit2,.replybtn ,#sendBtn{
font-weight:bold  !important;
padding:3px 5px 2px !important;
width:100px !important;
}

/*楼层号*/
.floornum,.floor,.content td[align="right"] font{color: #f90 !important;font-size:16px !important;}
.content td[align="right"]{width:10px !important;color: #f90 !important;font-size:16px !important;}

/*关注楼主*/
#my_tomy_p {
z-index:1001;
-moz-box-shadow: 0px 0px 10px #000;
position:fixed !important;
background:#666 !important;
height:20px !important;
width:auto !important;
bottom: 0px !important;
padding: 0px !important;
-moz-border-radius: 2px ! important;
opacity:0.9;
}
#my_tomy_p a
{
color:#f91 !important;
font-size:13px !important;
font-weight:bold !important;
text-decoration: none !important; 
}
/*百宝箱*/
.boxbg img,.line-a{display:none !important}
.boxbg{height:0px !important;
position:fixed !important;}
.topcenter1,.topcenter2,.main-les {background:none !important;}

.topcenter1{
z-index:1001;
position:fixed !important;
height:20px !important;
width:360px !important;
top: -2px !important;
left:auto !important;
right:80px !important;
padding: 0!important;
margin: 2px 0 0 0 !important;
border:none !important;
}
.topcenter2{
z-index:1001;
position:fixed !important;
height:20px !important;
width:360px !important;
top: -2px !important;
left:auto !important;
right:0px !important;
padding: 0!important;
margin: 2px 0 0 0 !important;
border:none !important;
}



