/*修正techforum只看楼主*/
DIV.content[style="word-wrap: break-word;"] > DIV {width:auto !important;border:none !important;padding:0 !important;}
#tech_body .body2 #pContentDiv .content TABLE {width:100%!important;background: #D5E2DE!important;}
#tech_body .body2 #pContentDiv .content TABLE  TD{padding:0 !important;}

/*id及时间背景*/
font[size="-1"],td center,table[align="center"] td[align="center"],.allpost table td{
background:#eeefff !important;font-size:10px !important;text-align:right !important;
width:1360px !important;
overflow:auto !important;
MARGIN-left:auto !important;
MARGIN-right:10px !important;
}

/*楼主*/
#firstAuthor,#firstAuthor td{width:1349px !important;}

.vcard {
background:#eeefff !important;font-size:10px !important;text-align:right !important;
padding: 0px 0 0px !important;
}

/*帖子标题*/
.mtitle,.mtime,
table[cellspacing="2"][bordercolor="#ffffff"] td[bgcolor="#336699"],table[bgcolor="#f5f9fa"],
h1 span
{background:#69c !important;}

.mtitle,table[cellspacing="2"][bordercolor="#ffffff"] td[bgcolor="#336699"] font[size="+1"],
h1 span{
font-size:26px !important;
font-weight: bold !important;
line-height:42px !important;
padding:0px !important;
MARGIN:0px !important;
text-align: justify !important;
text-shadow:0px 0px 10px #000;
}

.mtitle,table[cellspacing="2"][bordercolor="#ffffff"] td[bgcolor="#336699"] font[size="+1"]{
width:auto !important;
padding:0px !important;
MARGIN:0px !important;
/*-moz-border-radius:2px !important;
border:2px solid #eeefff !important;*/
}

#adsp_content_title_banner,#tech_body h1,.pagewrap h1 {
height:60px !important;}

/*背景上方分隔条*/
br{line-height:0px !important}


/*图片内嵌居中*/
.content img,.content embed,.content iframe,.post img,.post embed,#pContentDiv img{
padding: 0;margin: 0;
display: block !important;
margin-left: auto !important;
margin-right: auto !important;
max-width: 800px !important;
}
.content img:hover,.post img:hover,#pContentDiv img:hover{
max-height: none !important;
max-width: none !important;
height: auto !important;
width: auto !important;
}
.content img,.post img,#pContentDiv img{
-moz-box-shadow: 0px 0px 25px #aaa;
}

/*手机*/
u,.fromwap,.fromwap a{font-size:10px !important;color:#d3dfd1 !important;text-decoration:none !important;margin: 0px !important;padding:0px !important;}

/*信息*/
.function,.btmfunc{padding: 0px !important;margin: 0px !important;height:auto !important;}
.info{COLOR: #e7f0ec !important}


}
