css:
@-moz-document domain("harvestapp.com") {
/* based on "Harvest - Burnt Orange" style: https://userstyles.org/styles/163418/harvest-burnt-orange */


body,
#main_content,
#sub_nav,
.nav,
.clearfix,
nav #sub_nav,
.js-nav,
.main-navigation,
#global-header,
.footer,
#global-footer {
	background: #222 !important;
	color: #eee !important;
}
.page-top-line h1 {
	color: white !important;
}
.btn-action {
	color: #111 !important;
}
.overview {
	background-image: linear-gradient(#444, #333) !important;
}
.overview h2 {
	color: #111 !important;
	padding-top: 5px !important;
}
.overview a:hover {
	color: #FFF !important;
	background-color: #F66420 !important;
	text-decoration: underline !important;
}
.running,
.day-view-entry td,
.hui-empty {
	color: white !important;
	/*background-image: linear-gradient(#444, #333) !important;*/
	background-color: #333;
}
.big_link span {
	color: white !important;
}
.harvest_newsfeed a {
	color: white !important;
}
.hui-empty {
	border: 1px solid #333 !important;
}
.js-day,
.test-not-today,
.day-view-week-nav,
.hui-button:not(.hui-button-primary):not(.hui-button-running),
.day-view-week-nav #day-view-week-nav-total {
	background-image: linear-gradient(#444, #272727) !important;
	color: white !important;
}
.hui-button a {
	color: white !important;
}
.day-view-duplication button {
	color: #888 !important;
}
.is-today {
	background-image: linear-gradient(#323232, #191919) !important;
	color: white !important;
}
.hui-button-selected {
	/*background-image: linear-gradient(#F7912B, #F66420) !important;*/
	background-image: linear-gradient(#4e4e4e, #404040) !important;
	color: white !important;
}
.flash-message {
	background: #F76900 !important;
	color: white !important;
	border: 1px solid white !important;
}

.button-new-time-entry {
	background: rgba(255, 255, 255, 0) !important;
}

.day-view-total > span {
	color: #666;
}


#main, .white-background, .invoices-data-box .invoices-big-data, .invoices-data-box .invoices-graph, .project-data-box, .project-graph, .chrome, 
table.table .tr-dropdown td.td-item.opened-dropdown, .invoice_container_edit .notes_block {
	background-color: inherit;
	background-image: none;
	background: inherit;
}

.invoices-data-box .invoices-graph .axes .x-axis text {
	text-shadow: initial;
}

.has-clickable-table-rows .clickable-row:hover, .has-clickable-table-rows .clickable-row.is-hovered, .graph-tooltip, .hui-dialog {
	background-image: linear-gradient(#4e4e4e, #404040) !important;
}

.sub-nav-tabs a:hover, .sub-nav-tabs a:active, .sub-nav-tabs a.current {
	color: white !important;
}

.hui-label {
	color: #888;
}

.project-graph .y-axis-label, .project-graph .this-week text, .project-graph .this-month-text {
	fill: #eee;
}

.project-analysis-table-breakdown .is-expandable:hover, .project-analysis-table-breakdown .is-expandable.is-focused, table.table .tr-dropdown:hover, table.table .tr-dropdown:hover td.td-item, .edit-clients-warnings div.client-tip {
	background-color: #1a0e0a !important;
}

.project-analysis-table-breakdown .is-breakdown, table.table tr.opened-row, .hui-well, table.line_items thead th, .gray-background {
	background-color: #060606;
}

.invoice_container_edit #invoice_header table td.label {
	color: #eee !important;
}

}
