css:
@-moz-document url-prefix(https://www.rabb.it/)
{
    /*
     * Rabbit -- Cosmetic Fixes
     * Created by: PonyStick
     * VERSION: 2019-02-16 04:45:09 UTC
    */

    /* Reduce the height of the bottom tray */
        div.tray.screencast {
            padding-top: 0px !important;
        }

        /* NEW */
        /*
        div.leftSide
        div.tray {
            max-height: 65px !important;
        }
        */

        div.videoChatView:not(.spaceRoom)
        div.leftSide
        div.tray {
            max-height: 65px !important;
        }

        div.tray.screencast
        div.trayView
        div.topLine.noBubbles {
            max-height: 35px !important;
        }

        div.tray.screencast >
        div.trayView >
        div.topLine {
            max-height: 65px !important;
        }

        div.topLine
        div.userSessionView {
            max-height: 50px !important;
            max-width: 50px !important;
        }

        div.topLine
        div.bubblesContainer {
            max-height: 65px !important;
        }

        /* NEW */
        div.bubblesContainer
        div.sessions {
            padding-top: 25px !important;
        }

        div.topLine
        div.avButtons.vertical {
            margin-bottom: 0px !important;
        }

        div.topLine.noBubbles
        div.avButtons {
            margin-bottom: 5px !important;
        }

        div.topLine
        div.avButtons.vertical
        div.avButtonsView
        div.microphone.option {
            margin-bottom: 5px !important;
        }

        div.tray
        div.trayView
        div.avButtons
        div.avButtonsView
        div.option {
            height: 24px !important;
            width: 24px !important;
            border-radius: 15% !important;
        }

        /*div.topLine*/
        div.trayButtons
        div.trayButton {
            padding: 5px 0 4px !important;
        }

        /*div.topLine*/
        div.trayButtons
        div.trayButton.muteFriendsVideo {
            margin-top: 3px !important;
        }

        div.bottomLine {
            height: 45px !important;
        }

        div.bottomLine > div.exploreButton,
        div.bottomLine > div.reactions {
            margin-bottom: 5px !important;
        }

    /* Change the appearance of PFPs */
        div.userSessionView * {
            border-radius: 15% !important;
        }

        /*
        img.userPicture {
            border-radius: 15% !important;
            width: 45px !important;
            height: 45px !important;
        }
        */

