css:
@-moz-document url-prefix("http://tvhay.org"), domain("tvhay.org"), domain("tvhayz.org"), domain("tvhay.net"), domain("tvhayz.net"), domain("tvhai.org") {
/* Credit - tvhay.org Interface Remake
Thằng vọc css (Author): JAK
Facebook: https://www.facebook.com/wblekhoa (Lê Khoa)
Email: wblekhoa@gmail.com */

:root {
--darkC1:  #131313;
--darkC2:  #252525;
--darkC3: #1f1f1f;
--whiteC1: #efefef;
--whiteC2: #a8a8a8;
--whiteC3: #7d7d7d;
--mainC: #26A7ED;
}

body {
    background-image: none;
    background-color: var(--darkC1);
}

.container, #content {
    width: 100%;
    display: grid;
    grid-template-columns: 1fr;
    grid-template-rows: 1fr;
    justify-items: center;
}

.container #content {
    width: 100%;
    background-color: var(--darkC1, #131313);
}

.block .blocktitle {
    background-image: none;
    background-color: transparent;
}

.list-film .inner >a {
    display: block;
    height: calc(300px);
    overflow: hidden;
}

ul.list-film li img {
    background-color: #252525;
    background-image: none;
    width: auto;
    height: calc(350px);
    position: relative;
    top: 50%;
    left: 50%;
    transform: translate(-50%,-50%);
    z-index: 1;
}

#page-watch .block {
	margin: auto;
}

/* Noi bat - movie hot */
#movie-hot {
    margin-top: 7%;
    width: 90vw!important;;
    max-width: 1280px;
    height: calc(55vh)!important;
}

#movie-hot .listfilm li {
    width: calc(200px)!important;
    height: calc(400px)!important;
    margin-right: 50px;
}

#movie-hot .listfilm .overlay {
    z-index: 2;
    position: absolute;
    bottom: 0;
    left: 0;
    height: auto;
    width: 100%;
    background-color: var(--darkC1);
    background-image: none;
}

#movie-hot .listfilm .name {
    margin-top: 20px;
    margin-bottom: 15px;
    font-weight: bold;
    font-size: 28px;
    line-height: 1.5;
    min-height: 87px;
    text-align: left;
    color: var(--whiteC1);
}

#movie-hot .listfilm .name2 {
    text-align: left;
    color: var(--whiteC2);
}

ul.list-film li .status, #movie-hot .listfilm .status {
    font-size: 12px;
    left: 0px;
    top: 0px;
    padding: 10px 15px 11px;
    position: absolute;
    border-radius: 0px;
    font-weight: normal;
    letter-spacing: 1px;
    z-index: 1;
}

#movie-hot .listfilm img {
    height: auto;
    width: 100%;
}

#movie-hot .listfilm .name a:hover, #movie-recommend ul.list li a:hover, #movie-recommend .col2 .blockbody li:hover span, #movie-recommend .col2 .blockbody li:hover a {
    color: var(--mainC);
}

#movie-hot .next, #movie-hot .prev {
    height: 60px;
    opacity: 0.8;
    position: absolute;
    top: 40%;
    transform: translateY(-50%);
    width: 47px;
    z-index: 5;
}

#movie-hot .next {
    background-position: -121px -69px;
    right: 30px;
}

#movie-hot .prev {
    background-position: -75px -68px;
    left: 20px;
}

/* menu */
#wrapper {
    position: fixed;
    width: 100%;
    background-color: var(--darkC3);
    z-index: 100;
}

#header {
    padding-top: 5px;
    width: auto;
    margin: auto;
    height: auto;
    display: table;
    background-color: var(--darkC3);
}

#nav {
    margin-top: 1px;
    padding-left: 5px;
}

#nav li a {
    height: 29px;
    width: auto;
    float: none;
    font-size: 12px;
    text-transform: none;
    color: var(--whiteC1);
}

#nav .sub-menu {
    display: none;
    position: absolute;
    width: 480px;
    background-color: var(--darkC3);
    background-image: none;
    left: 0;
    top: 29px;
    list-style: none;
    opacity: 1;
    border-radius: 0 0 2px 2px;
    padding: 5px;
    padding-top: 10px;
    padding-bottom: 5px;
    border: none;
    border-top: 0;
}

#nav .sub-menu li a  {
    color: var(--whiteC1);
}

#nav .sub-menu li a:hover {
    border-radius: 4px;
}

#search .keyword {
    width: 240px;
    color: var(--whiteC2);
    padding: 4px 15px 3px;
    background-color: var(--darkC1);
    border: none;
    box-shadow: none;
    font-size: 12px;
}

#search .submit {
    float: left;
    height: 21px;
    width: 30px;
    background-color: #0000000f;
    border: none;
    box-shadow: none;
    border-left: 0;
    background-position: 5px -67px;
    cursor: pointer;
    filter: invert(90.5%);
}

#logo {
    background-position: 10px 0;
    background-size: 230px;
    width: 130px;
    height: 32px;
    margin-top: 0px;
    margin-right: auto;
    margin-left: auto;
    filter: invert(90.5%) hue-rotate(175deg) brightness(120%);
}

#logo a {
    text-indent: -999%;
    padding-top: 35px;
    font-size: 0px;
}

.block .blocktitle { 
    border: 1px solid #cfd1d1!important;
}

.more {
    text-transform: none;
    font-size: 14px;
    font-weight: normal;
    letter-spacing: 1px;
    margin-right: auto;
    margin-left: auto;
    margin-top: -15px;
    margin-bottom: 40px;
    border-radius: 0px;
    padding: 4px 25px 3px 20px;
    float: none;
    display: table;
    color: var(--mainC);
    background: transparent;
}
.more a {
    color: var(--mainC)!important;
}
.block .blockbody.list, .block .blocktitle {
    border: none!important;
}

/* List film - danh sach */
ul.list-film {
    background-color: var(--darkC1, #131313);
}

ul.list-film li .name, ul.list-film li .name a {
    font-weight: bold;
    font-size: 28px;
    margin-top: 5px;
    margin-bottom: 5px;
    line-height: 1.5;
    padding: 0;
}

ul.list-film li .name2 {
    margin-bottom: 5px;
    padding: 0;
}

ul.list-film li .inner {
    margin: 15%;
    border: none;
    border-radius: 0;
}

ul.list-film li div {
    border: none!important;
}

ul.list-film li div a {
    color: white!important;
}

ul.list-film li div a:hover {
    color: #2BA5F2!important;
}

ul.list-film li .name2 {
    color: var(--whiteC2)!important;
    margin-top: 7px;
}

ul.list-film li .info {
    background: transparent!important;
    color: white!important;
    padding-top: 15px;
}

ul.list-film li .stats {
    color: var(--whiteC2)!important;
    background: transparent!important;
    border-radius: 0;
    font-weight: normal;
    letter-spacing: 2px;
    padding: 0;
}

ul.list-film li .name {
    min-height: 90px;
}

ul.list-film li .name2 {
    min-height: 25px;
}

/* movie recommend */
#movie-recommend {
    margin: 50px;
}

#movie-recommend .col2 .blocktitle .tabs {
    height: auto;
}

#movie-recommend .col2 .blocktitle .tab {
    background-color: var(--darkC3);
    color: var(--whiteC3);
    font-size: 32px;
    margin-right: 50px;
    margin-bottom: 30px;
}

#movie-recommend .col2 {
    width: auto;
    max-width: 550px;
    border: none;
    margin: auto;
    margin-top: 70px;
    margin-bottom: 70px;
    float: inherit;
}

#movie-recommend ul.list, #movie-recommend .blockbody {
    background: transparent!important;
    color: white!important;
}

#movie-recommend .col2 .blockbody li {
    font-family: Tahoma, Geneva, sans-serif;
    padding-left: 12px;
    background-image: none;
    height: 80px;
    line-height: 40px;
    border-bottom: 1px solid var(--darkC1);
    color: white!important;
    font-weight: bold;
    font-size: 24px
}

#movie-recommend .col2 .blocktitle .tab.active {
    background-color: var(--darkC3)!important;
    font-size: 32px;    
}

#movie-recommend ul.list li span {
    color: #a1a1a1;
}

#movie-recommend ul.list li a {
    letter-spacing: 1px;
    color: white;
}

#movie-recommend .col2 .blocktitle {
    padding-left: 24px;
    background-image: none;
    height: auto;
}

#page-list .blockbody {
    border: none;
}

/* Phim de cu */
#movie-recommend .col1{
    display: none;
}

/* Phim cap nhat */
#movie-update, .block {
    width: 100%;
	max-width: 1280px;
}

#movie-update .btn {
    line-height: 35px;
    height: 32px;
    font-weight: normal;
    letter-spacing: 1px;
}

.block .blocktitle a, #movie-update .btn {
    color: #ccc;
}

#movie-update .blocktitle {
    padding-left: 4%;
    height: 40px;
}

/* breadcrumbs */
.breadcrumbs {
    width: auto;
    margin: auto;
    margin-top: 3%;
    margin-bottom: 3%;
    padding-left: 0%!important;
    display: table;
}

.breadcrumbs .item {
    background-image: none;
    color: #5f5f5f;
    position: relative;
    line-height: 44px;
}

.breadcrumbs .item:after {
    content: ">";
    font-family: roboto;
    width: 10px;
    height: 36px;
    color: #ccc;
    right: 0px;
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
}

.breadcrumbs .item:last-child:after {
    display: none!important;
}

.block .blocktitle {
    background-image: none;
    border: none;
    height: 32px;
    padding-left: 10px;
    color: var(--whiteC1);
}

/* page watch*/
/* media */
#movie {
	width: 100%!important;
    max-width: 100%!important;
}

.jwplayer video, #playdr, .jw-wrapper {
    background: var(--darkC1)!important;
}

#page-watch .blockbody {
	width: 100%;
    padding: 0px;
    border: none;
    text-align: center;
}
.container #page-watch #media {
    width: calc(100vw - 18px);
    height: calc(100vh - 160px);
}
#body-wrap {
    padding-top: 76px;
}

#page-watch .action {
    width: auto;
    padding: 8px 30px;
}

#page-watch .note {
    padding: 25px 15px;
}

#page-watch .note p, #page-watch .note li {
    text-align: center;
}

#page-watch .note p {
    margin-bottom: 10px;
}

#page-watch .note center {
    margin-bottom: 8px;
    margin-top: 15px;
}

#page-watch .note li {
    margin-top: 8px;
}

#page-watch .action div {
    background-color: var(--darkC2);
}

#page-watch .action div.disabled {
    background-color: var(--darkC1);
    cursor: not-allowed;
}

#page-info .detail .tabs .tab.active {
	font-size: 32px;
}

/* list */
.serverlist {
    padding: 15px 15px 0px;
    width: 800px;
    margin: auto;
}
#page-watch .note, .serverlist, span.backlink, .episodelist li a, .serverlist .server:last-child {
    background-color: var(--darkC1);
}
.episodelist li a:hover {
    background-color: var(--mainC);
}
span.backlink {
    padding-left: 8px;
    font-size: 16px;
    letter-spacing: 1px;
    margin-top: 30px;
    margin-bottom: 30px;
}
.serverlist, .serverlist .server {
    border: none;
}
.serverlist .server {
    border-bottom: 1px solid var(--darkC3)!important;
}
.server .label {
    width: 80px;
    color: var(--whiteC1);
}
.server .episodelist {
    margin-left: 80px;
}
/* sidebar */
#sidebar {
    margin-top: 0px;
    width: 800px;
}
#sidebar .block:first-child .blocktitle {
    padding-top: 20px;
    padding-bottom: 10px;
}
#sidebar .block:last-child {
    display: none;
}

/* tab */
#chart .tabs {
    font-size: 14px;
    margin-bottom: 20px;
}
#chart .tabs, .block .subtitle {
    border-top: 1px solid #e4e4e4;
}

.tabs {
    padding-left: 0!important;
    height: auto;
    background-image: none!important;
    letter-spacing: 1px;
    text-align: left!important;
}

.tabs .tab.active, .tabs .tab {
    background-color: var(--darkC3);
    color: var(--whiteC3);
    font-size: 16px;
    width: auto!important;
    border: none!important;
    padding: 0px 0px!important;
    text-align: left!important;
}

#page-watch .blocktitle {
    margin: auto;
    width: auto;
    display: table;
}

#comment .blocktitle {
    margin-top: 80px;
}

#comment .tabs .tab.active {
    font-weight: normal;
    letter-spacing: 1px;
}

#comment .tabs .tab {
	background-color: var(--darkC1)!important;
	background: var(--darkC1)!important;
    padding: 5px 10px 0px 10px!important;
    font-size: 18px;
    border-radius: 10px;
}

/* Xem nhieu - #chart */
#chart li .detail .name a {
    font-size: 24px;
}
#chart .tabs, .block .blockbody {
    color: var(--whiteC1)!important;
    background-color: var(--darkC1);
}
.block .blockbody a, #chart li .detail .name a {
    color: var(--whiteC1);
}
#chart li .detail .views {
    color: var(--whiteC2);
}
#chart .tabs, .block .subtitle, .block .blockbody {
    border: none;
}
#chart .tabs .tab.active {
    border-radius: 0px;
}
#chart li {
    padding-top: 2%;
    padding-bottom: 3%;
    border-color: var(--darkC3);
}
#chart .st {
    filter: invert(93%) hue-rotate(175deg) brightness(120%);
}
#chart .tabs .tab, #chart .tabs .tab.active {
    font-size: 24px;
}

/* footer */
#footer .container {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr 1fr 1fr;
    grid-template-rows: 1fr;
}

#footer .title {
    margin-bottom: 15px;
}

#footer .container ul li {
    margin-top: 8px;
}

#footer a {
    color: var(--whiteC2)!important;
}

#footer {
    background-color: var(--darkC1, #131313);
    margin-top: 10px;
    padding-top: 20px;
    color: var(--whiteC1);
}

#footer .block2, #footer .block1 {
    border: none;
}

#footer .block2 .container {
    margin-top: 5px;
    padding-bottom: 10px;
    padding-top: 0px;
}

/* filter */
#page-list .filter {
    color: var(--whiteC1);
    background-image: none;
    padding-left: 4%;
    border: none;
    background-color: var(--darkC1, #131313);
}

#page-list .filter .item {
    margin-left: 6px;
    margin-right: 6px;
    font-weight: normal;
    color: var(--whiteC1);
    height: auto;
    line-height: 27px;
}

#page-list .filter .item select {
    background: var(--darkC2);
    border: none;
    color: var(--whiteC1);
    height: auto;
    padding: 6px 10px 5px;
    border-radius: 20px;
    box-shadow: none;
    margin-top: 0px;
    margin-left: 3px;
}

#page-list .filter .item select:focus {
    outline: none;
}

#page-list .filter .item .btn {
    margin-top: 0px;
    border: none;
    font-weight: normal;
    padding: 4px 5px 5px;;
    height: 26px;
    width: 90px;
    font-size: 13px;
    border-radius: 20px;
}

#page-list .filter .item span {
    margin-top: 0px;
    margin-right: 10px;
    line-height: 28px;
}

/* paging */
.wp-pagenavi {
    text-align: center;
    margin-top: 2%;
    margin-bottom: 6%;
}

.wp-pagenavi a, .wp-pagenavi span {
    font-size: 14px;
    background: var(--darkC1);
    color: var(--whiteC1);
    font-weight: normal;
    margin: 4px;
    padding: 10px 14px;
    border-radius: 3px;
    transition: color .2s, background .2s;
    transition-delay: 0s;
    transition-timing-function: linear;
}

/* Page info */
#page-info .blockbody {
    background-color: var(--darkC1);
    color: var(--whiteC1);
    border: none;
}

#page-info #info-film {
padding-left: 0px!important;
}

#page-info .info {
    display: grid;
    grid-template-columns: auto;
    grid-template-rows: auto auto auto auto auto;
    justify-items: center;
}
#page-info .info .poster {
    position: relative;
    top: 0;
    left: 0;
    width: auto;
}

#page-info .info .poster img {
    border: none;
    width: 20vw;
    height: auto;
}

#page-info .info .title {
    text-transform: none;
    font-weight: bold;
    color: var(--whiteC1);
    font-size: 28px;
    padding-top: 25px;
}

#page-info .info .dinfo {
    padding: 15px 0 15px;
}

#page-info .like-wrap {
    float: none;
    text-align: left;
    margin: 0;
    background-color: transparent;
}

#page-info .info .fr, #page-info .btn-groups {
    margin-left: 0px!important;
    padding-left: 0px;
}

#page-info .info .fr div {
    width: auto!important;
    margin-bottom: 0px;
}

#page-info .btn-groups {
    grid-row: 4;
    display: grid;
    grid-template-columns: auto auto;
    grid-template-rows: auto;
    justify-items: center;
    align-items: center;
}

.btn-watch {
    margin-top: 20px;
    margin-bottom: 20px;
    grid-row: 1;
    grid-column: 1 / span 2;
}


#page-info .info, #page-info .detail {
    width: 800px;
    margin: auto;
}

#page-info .detail {
    padding-top: 50px;
    padding-bottom: 60px;
}

#page-info .info .name2 h3, #page-info .info .name2, #page-info .info dd, #page-info .info a {
    color: var(--whiteC2);
    border: none;
}

#page-info .info dt {
    color: var(--whiteC1);
}

#page-info .info a {
    border-radius: 5px;
}

.tabs .tab.active {
    background-color: var(--darkC3)!important;
    border-bottom: none!important;
    color: var(--mainC)!important;
}

#page-info .tabs-content p {
    letter-spacing: 0.5px;
    color: var(--whiteC1);
    line-height: 2;
}

.fb-comment {
    margin: auto;
    width: 800px;
    text-align: center;
    margin-bottom: 10%;
    padding: 4%;
    background-color: var(--whiteC1);
}

#page-info .tabs-content a {
    color: var(--whiteC2);
}

#page-info .info dl {
    width: 800px;
    padding: 0;
}

#page-info .info .col2, #page-info .info .col1 {
    float: none;
    padding: 0;
}

#page-info .breadcrumbs {
    width: auto;
    margin: auto;
    margin-top: 3%;
    margin-bottom: 3%;
    padding-left: 0%!important;
    display: table;
}

.block .blocktitle .title {
    letter-spacing: 1px;
    text-transform: none;
    font-size: 32px!important;
}

/* Moive comment */
#wpcomm .wc-comment .wc-comment-right, #wpcomm .wc_comm_form.wc_main_comm_form .wc-field-textarea .wpdiscuz-textarea-wrap textarea, #wpcomm .wpd-form-row .wpd-field, #wpcomm .wpd-form-row .wpdiscuz-item .wc-bin-captcha {
	background-color: var(--darkC3);
	background: var(--darkC3);
    border-radius: 10px;
}

#wpcomm .wpd-form-row .wpd-field, #wpcomm .wc_comm_form.wc_main_comm_form .wc-field-textarea .wpdiscuz-textarea-wrap textarea {
	color: var(--whiteC1);
}

.title {
	margin-bottom: 0px;
}

#wpcomm button, #wpcomm input[type="button"], #wpcomm input[type="reset"], #wpcomm input[type="submit"] {
	border-radius: 5px;
}

#wpcomm .wc-blog-guest>.wc-comment-right .wc-comment-author, #wpcomm .wc-blog-guest>.wc-comment-right .wc-comment-author a, #wpcomm .wc-footer-left .wc-reply-button, #wpcomm .wc-blog-subscriber>.wc-comment-right .wc-comment-author, #wpcomm .wc-blog-subscriber>.wc-comment-right .wc-comment-author a, #wpcomm .wc-footer-right .wc-toggle, #wpcomm .wc-footer-right .wc-toggle a, #wpcomm .wc-footer-right .wc-toggle i {
	color: var(--mainC)!important;
}

#wpcomm .wc-blog-guest>.wc-comment-right .wc-comment-author, #wpcomm .wc-blog-guest>.wc-comment-right .wc-comment-author a {
	border-color: var(--mainC)!important;
    transition: 200ms;
}

#wpcomm .wc-footer-left .wc-reply-button {
	margin-left: 0px;
    border-radius: 7px;
    padding-left: 0;
    transition: 200ms ease-in-out;
}

#wpcomm .wc-cta-button:hover, #wpcomm .wc-footer-left .wc-reply-button.wc-cta-active, #wpcomm .wc-cta-active {
	box-shadow: 5px 4px 19px -7px var(--mainC)!important;
    background-color: var(--mainC)!important;
	background: var(--mainC)!important;
    color: var(--whiteC1)!important;
    padding-left: 10px;
}

#wpcomm .avatar {
	border-radius: 50%;
}

#wpcomm .wc-comment-right .wc-comment-text {
    min-height: 40px;
}

#wpcomm .wc-comment-header {
    margin-bottom: 4px;
    height: 40px;
    padding-top: 5px;
}

#wpcomm .wc-comment .wc-comment-right {
	padding-left: 5px;
}

#wpcomm .wc-comment-author {
	font-weight: bold;
    font-size: 16px;
}

#wpcomm .wc-comment-footer {
    padding: 2px 10px 15px 10px;
}

/* XML sitemap */
.gr__tvhay_org body h1 {
	color: var(--whiteC1)!important;
}

.gr__tvhay_org body #intro {
	color: var(--whiteC1)!important;
    background-color: var(--darkC3)!important;
	background: var(--darkC3)!important;
    border-color: var(--darkC3)!important;
}

.gr__tvhay_org body #intro a {
	color: var(--whiteC2)!important;
}

.gr__tvhay_org body #content table tr, .gr__tvhay_org body #content table th, .gr__tvhay_org body #content table th a, .gr__tvhay_org body #content table tr a {
	color: var(--whiteC2)!important;
}

.gr__tvhay_org body #content table tr.high {
	background-color: var(--darkC3)!important;
	background: var(--darkC3)!important;
}

