css:
@-moz-document url-prefix("https://roosters.windesheim.nl/") {
body {
    background-color : #1F1F1F !important;
    color: #afafaf !important;
}
.claro {
    background-color : #1F1F1F;
    color: #afafaf;
}
#mainMenu.dijitContentPane {
    top : 80px !important;
}
.grupet_widget_LoginWidget thead {
    color: #afafaf;
}
.dijitMenuBar {
    background-image: none !important;
}
.dijitMenuBar.grupet_widget_MainMenu {
    background-color : #353535 !important;
    color: #afafaf !important;
    border : 0px !important;
}
#sidebar {
    padding-top : 0px;
}
.dijitMenuItem {
    color: #FF9900 !important;
    border-right: solid 1px #FF9900 !important;
}
.dijitHover {
    background-color : #FF9900 !important;
    background-image : none !important;
    color: #1F1F1F !important;
    border-right: 0px !important;
}
.dijitContentPane {
    padding : 0px !important;
}
.pagetitle {
    padding : 8px 0px 0px 8px !important;
}
.dijitFocused {
    background-color : #FF9900 !important;
    background-image : none !important;
    color: #1F1F1F !important;
    border-right: 0px !important;
}
.dijitMenuItemActive {
    background-color : #FF9900 !important;
    background-image : none !important;
    color: #1F1F1F !important;
    border-right: 0px !important;
}
.dijitMenuItemInactive {
    background-color : #1F1F1F !important;
    color: #FF9900 !important;
    border-right: solid 1px #FF9900 !important;
    border-right: 0px !important;
}
.logo {
    display : none;
}
.licence {
    color: #afafaf;
}
.dijitCalendarContainer {
    background-color : #353535 !important;
    background-image: none !important;
    border : 0px !important;
}
.currentDateWidget {
    background-color : #353535 !important;
    background-image: none !important;
    border : 0px !important;
}
.selectedDateInfo div {
    color: #afafaf !important;
    background-color : #353535 !important;
    background-image: none !important;
}
.dijitCalendarDateLabel {
    border : 0px !important;
}
.dijitCalendarDayLabel {
    color: #afafaf !important;
}
.dijitCalendarSelectedYear {
    color: #FF9900 !important
}
.dijitCalendarNextYear {
    color: #afafaf !important;
}
.dijitCalendarPreviousYear {
    color: #afafaf !important;
}
.dijitButtonNode {
    border : 0px !important;
}
.dijitCalendarMonthLabel {
    color: #afafaf !important;
}
.grupetWidgetToolbar {
    border : 0px;
    background-color: #353535 !important;
    border-radius : 0px 0px 0px 0px !important;
    margin-bottom : 0px !important;
    margin-top: 6px !important;
}
.dijitTextBox {
    background-color: #afafaf !important;
    border: none !important;
}
.grupetScrollContainerContent {
    background-color: #1F1F1F !important;
}
.timetableContent {
}
.timetableContent {
    border : 0px !important;
    padding : 0px 0px 0px 1px !important;
}
.renderedEntry {
    border : 0px !important;
}
.timetableGridColumn.timetableColumnHeader {
    background-color: #353535 !important;
}
.grupetWidgetTimetable .timetableColumnHeaderContent td.p1 {
    color: #afafaf !important;
}
.grupet_TimetablePage_timetableContainer {
    margin-top : 0px !important;
}
.timetableSectionTop .dijitContainer {
    border : 0px !important;
}
.grupetWidgetTimetable .timetableGridColumn {
    border-right: 0px !important;
}
.grupetWidgetTimetable .timetableRowHeader {
    border : 0px !important;
}
.grupetWidgetTimetable .timetableContent table.gridLayer, .grupetWidgetTimetable .timetableColumnHeader, .grupetWidgetTimetable .timetableRowHeader .timetableGridRow {
    background-color : #1F1F1F !important;
}
.grupetWidgetTimetable table.rowHeader td.center table td {
    color: #afafaf !important;
}
.grupetWidgetTimetableUpdateTimpstamp {
    color: #afafaf !important;
}
.claro .dijitButton .dijitButtonNode, .claro .dijitDropDownButton .dijitButtonNode, .claro .dijitComboButton .dijitButtonNode, .claro .dijitToggleButton .dijitButtonNode {
    background-color : #1F1F1F !important;
    background-image : none !important;
    color: #FF9900 !important;
}
.claro .dijitCalendarDateTemplate {
    background-color: #353535 !important;
    color: #afafaf !important;
}
.claro .dijitCalendarPreviousMonth .dijitCalendarDateLabel, .claro .dijitCalendarNextMonth .dijitCalendarDateLabel {
    color: #FF9900 !important;
}
.claro .dijitCalendarSelectedDate .dijitCalendarDateLabel {
    background-color: #FF9900 !important;
}
.grupetWidgetTimetable .timetableSectionTop {border : 0px !important;}
}
