css:
@-moz-document url-prefix("https://elo.windesheim.nl/") {
/* general ELO interface (home & courses) */ 
body.lightbackground {background-color: #292929 !important; color: #bdbdbd !important;}
.nowidgetborder {background-color: #292929 !important; color: #bdbdbd !important;}
.widget {background: #3c3c3c !important; border-color : #3c3c3c !important;}
a, .btn-link, .btn-like-link {color : #406790  !important;}
.main-top{background-color: #292929 !important;}
strong {color: #bdbdbd !important;}
.dropdown {border : 0px !important;}
.form-control {background: #3c3c3c !important; border-color : #3c3c3c !important;}
.defaultborderline, .card-with-border, .file_list_header, .backup-menu ul ul, .widget_options, .portal_overview_bar, .item-list-header, .item-list>li, .widget-item-list, .back_to_list, .pager_bottom, .notes_overview li, .notes_overview .overview-header, .message_input_field, .test_back, .test_next, .test_nav, .overview-pager, .overview_subheader, #selecteddetails, #selecteddetails .detailstitle {border : 0px;}
.item-container unread{background-color: #292929 !important; color: #bdbdbd !important;}
.pebbles a.pebbles-selection{background-color: #292929 !important;}
.tabs-bar > ul > li.active > a, .tabs-bar > ul > li.active > a:hover, .tabs-bar > ul > li.active > button, .tabs-bar > ul > li.active > button:hover{background-color: #e2e2e2}
.nowidgetborder .widget_header h2 {color : #406790 !important;}

/* folder explorer */ 
.Xtree {background-color: #3c3c3c !important; color: #bdbdbd !important;}
.ExplorerTitle{background-color: #3c3c3c !important; color: #bdbdbd !important; border-color : #406790 !important}
.XbreadTrail{background-color: #3c3c3c !important; color: #bdbdbd !important;}
.Xmenu{background-color: #3c3c3c !important; color: #bdbdbd !important;}
.Xsplitter{background-color: #3c3c3c !important; color: #bdbdbd !important;}
#header, .dropdown-title, .btn, .btn:hover, .item-container-header, .popup_header, .search-dropdown .search-dropdown-filter, .item_header, .portal_overview_bar, .ts-dialog2-header, .v12grid th{background-color: #3c3c3c !important; color: #bdbdbd !important; background-image : none;}
.XponentDialog2Wrapper-header{border-color : #406790 !important}
.ExplorerTree{border-color : #406790 !important}
.FormView{border-color : #406790 !important}
.file_list, .file_list_header, .card, .back_to_list, .pager_bottom, .notes_overview, .mail_overview>ul>li, .item-list .item-badge, .test_nav, .full_page_popup, .webstylelayout .containsMaximizedWidget, .webstylelayout, .lightbackground, .widget-item-list, #selecteddetails{background-color: #3c3c3c !important; color: #bdbdbd !important;}
.Xtree li.selected a, .XbreadTrail li.selected a, .Xlist:not(.Xmenu):not(.XcontextMenu) li.selected a {color : #FFFFFF !important;}
}
