css:
@-moz-document domain(rabb.it) {

.toolbar .toolbar.default {
    background-color: #3e3e3e;
    color: #dbdbdb;
}


.toolbar .toolbar.default .controls .menu {
    background-color: #4a4a4a;
}

.toolbar .toolbar.default .controls .menu .item {
    color: #ffe8d1;
    border-top: 1px solid #5c5c5c;
}

/* GROUP PANE */

.spacesView .spaces:not(.room) .tray > .spacesHeader > .spacesTitle {
    color: #d9d9d9;
}

.spacesView .spaces > .tray {
    background-color: #0d1115;
}

.spacesView .spaces > .tray > .list > .space:hover {
    background-color: rgba(255,255,255,0.05);
}

.spacesView .spaces > .tray > .list > .space > .pictureContainer > .picture {
    border: 1px solid #000;
}

.spacesView .spaces:not(.room) .tray .list > .space.selected {
    background-color: rgba(255,255,255,0.1);
}
.spacesView .spaces:not(.room) .tray .list > .space > .textContainer > .topLine > .spaceName {
    color: #d9d9d9;
}

/* CHAT PANE */

.spacePane {
border-left: 1px solid #353535;
background-color: #18191c;
}

.spaceHeader {
    border-bottom: 1px solid #353535;
}

.spaceHeader > .left > .spaceName {
    color: #d9d9d9;
}

.inviteButtonArea .inviteButton {
    background-color: #575757;
}

.inviteButtonArea .inviteButton span {
    color: #d9d9d9;
}

.inviteButtonArea .inviteButton:hover span {
    color: #242424;
}

.chat .body .chatFadeOverlay {
    background: -moz-linear-gradient(top,transparent 0,#18191c 100%);
    background: linear-gradient(180deg,transparent 0,#18191c);
}

.chat .body .message.me .messageBody {
    color: #fff;
    background: rgba(255,255,255,0.1);
}

.chat .body .message.other .messageBody, .chat .body .message.other .sharedContentCard {
    border-bottom-left-radius: 20px;
    background: none;
    color: #fff;
    border: 1px solid #3c3c3c;
}

.chat > footer {
    border: none;
}

.chat > footer textarea {
    background: #3c3c3c;
    color: white;
    border: 1px solid transparent;
}

.chat > footer textarea:focus {
    color: #fcfcfc;
}

/* SETTINGS PANE */

.spacePane > .pane {
    border-left: 1px solid #353535;
}

.spaceMedia {
    background-color: #0d1115;
}

.spaceMenu {
    color: #d9d9d9;
}

.spaceMedia > .empty .card p {
    color: #d9d9d9;
}

.spaceMedia > .empty .card {
    box-shadow: 0 2px 10px 0 rgba(0,0,0,.4);
    background-color: rgba(255,255,255,0.1);
}


.spaceMedia > .empty .card p {
    color: #d9d9d9;
}
.spaceMedia > .empty .card p {
    font-size: 1em;
}

}
