css:
@-moz-document url-prefix("https://cerp.allphi.eu/") {
body,
h1,
h2,
h3,
h4,
h5,
h6,
label,
legend {
    color: whitesmoke;
}

#home-btn {
    filter: grayscale(1);
}

#header,
#menu-btn,
.menu-wrapper,
div.Sidebar-Calendar-Icon .Sidebar-Icon-Date,
div.Sidebar-Calendar-Icon {
    background-color: #0e0e0e !important;
    color: whitesmoke !important;
}
#container {
    background-color: #242424 !important;
}

.TileWrapper a,
div.Calendar-Icon .Icon-Date,
.menu li:hover,
.setting:hover {
    background-color: #4a4c4c !important;
    color: whitesmoke !important;
}

.a-hover {
    color: whitesmoke !important;
}

div.Calendar-Icon .Icon-Month {
    color: #4a4c4c !important;
}

#actions {
    background-color: #3e657b !important;
}

#footer {
    background-color: #3c3c3c !important;
    color: whitesmoke;
}

#footer .version {
    color: whitesmoke !important;
}

.icon {
    border-bottom: 1px solid #232323 !important;
}



#menu-btn:hover {
    color: gray;
}

div.Sidebar-Calendar-Icon .Sidebar-Icon-Month {
    background-color: whitesmoke !important;
    color: #0e0e0e !important;
}

.setting-hover {
    background-image: url(data:image/svg+xml;base64,);
}

.timesheet-hover {
    background-image: url(data:image/svg+xml;base64,);
}

div.Sidebar-Calendar-Icon .Sidebar-Icon-Month {
    box-shadow: rgb(255, 255, 255) 0px 2px 0px !important;
}

.Sidebar-Icon-Date {
    border: 0.2em solid rgb(255, 255, 255) !important;
}

.Sidebar-Icon-Date:hover {
    border: 0.2em solid rgb(255, 255, 255) !important;
}

.ui-dialog .ui-dialog-content,
.ui-widget-content {
    background-color: gray !important;
    color: whitesmoke !important;
}

.ui-widget-header {
    background: #4c4c4c 50% 50% repeat-x !important;
}

.btn-info,
.btn-primary {
    background-color: #5f7682;
    border-color: #48555a;
}

.btn-info:hover,
.btn-primary:hover {
    background-color: #48555a;
    border-color: #5f7682;
}

.btn-warning {
    color: #fff;
    background-color: #9c8462;
    border-color: #63533e;
}

.btn-warning:hover {
    color: #fff;
    background-color: #63533e;
    border-color: #9c8462;
}

.tabbedNavBar li.active a {
    color: whitesmoke !important;
}
.nav-tabs > li.active > a,
.nav-tabs > li.active > a:focus,
.nav-tabs > li.active > a:hover {
    background-color: #242424 !important;
    border: 1px solid #464040 !important;
    border-bottom-color: transparent !important;
}

.nav-tabs {
    border-bottom: 1px solid #464040;
}

.table-striped > tbody > tr:nth-of-type(odd) {
    background-color: #4e4e4e !important;
}

.panel {
    background-color: #383838 !important;
}

.btn-success {
    color: #fff;
    background-color: #708270;
    border-color: #323c32;
}

.btn-success:hover {
    color: #fff;
    background-color: #323c32;
    border-color: #708270;
}

.km-switch-on .km-switch-container {
    background-color: #558094;
    color: #454b4e;
}

.modal-content {
    background-color: gray;
}

.well {
    background-color: #121212;
}

.text-info {
    color: #1a5673;
}
dl{
background-color: gray !important;
}

.ui-widget-content {
    border: none;
    background: none;
}
}
