css:
@-moz-document domain("wikipedia.org") {
#p-logo > a {
    background-image: url(http://i.imgur.com/vSvAdyp.png)!important;
}
}

@-moz-document domain("wikimedia.org") {
#p-logo > a {
    background-image: url(http://img2.wikia.nocookie.net/__cb20111127203041/fairytail/es/images/c/c9/Wikimedia_Commons.png)!important;
    background-size: contain!important;
}
}

@-moz-document domain("wikipedia.org") {
body {
    background-color: #191919!important;
    background-image: url(http://i.imgur.com/zdZV4JY.png)!important;
    background-attachment: fixed!important;
    background-repeat: repeat-x!important;
}

a {
    color: #32a9da!important;
}

a:hover {
    color: #6ad3ff!important;
}

h1,
h2,
h3,
h4,
h5,
h6 {
    color: rgba(220, 220, 220, 1)!important;
}

#mw-head-base,
#mw-page-base,
#mw-head,
#mw-panel {
    background-color: rgba(20, 20, 20, 0)!important;
    background-image: none!important;
}

#content.mw-body {
    background-color: rgba(100, 100, 100, 0.2)!important;
    border-color: rgba(220, 220, 220, 0.2)!important;
}

* {
    border-color: rgba(220, 220, 220, 0.2)!important;
}

p,
dd,
.sisterproject {
    color: rgba(220, 220, 220, 0.8)!important;
}

.hauptartikel-pfeil,
i,
strong {
    color: #fff!important;
}

.settings-menu-items .menu-section:hover {
    background-color: rgba(220, 220, 220, 0.05)!important;
    color: rgba(220, 220, 220, 0.8)!important;
}

.settings-menu-items .menu-section {
    color: rgba(220, 220, 220, 0.6)!important;
}

.uls-input-settings .uls-input-settings-inputmethods-list {
    background-color: rgba(220, 220, 220, 0.1)!important;
}

li,
td,
tr,
th,
td > h2,
td > div > h2,
dt {
    color: rgba(220, 220, 220, 0.6)!important;
    background-color: rgba(220, 220, 220, 0.01)!important;
}

dt {
    color: rgba(220, 220, 220, 0.8)!important;
    background-color: rgba(220, 220, 220, 0.01)!important;
    text-shadow: 0px 2px 0px #111!important;
}

.rellink {
    color: #daef8f!important;
    background-color: rgba(220, 220, 220, 0.01)!important;
}

code,
.mw-geshi,
pre,
.catlinks,
.NavContent > p {
    color: rgba(220, 220, 220, 0.6)!important;
    background-color: rgba(100, 200, 255, 0.1)!important;
}


#hauptseite .inhalt,
.infobox {
    background-color: rgba(0, 0, 0, 0.25)!important;
    color: #aaa;
}

.toptextcells > tbody tr td div {
    background-color: transparent!important;
}

.infobox > div,
div.flaggedrevs_short {
    background-color: #444!important;
    color: #eee!important;
}

#mw-fr-revisiondetails {
    background-color: rgba(20, 20, 20, 0.9)!important;
}

#mw-panel.collapsible-nav .portal {
    background-image: url(http://i.imgur.com/6G2aYK9.png)!important;
}
.cnotice {
    background: #444;
    border: 1px solid #a2a9b1;
    color: #6a6a6a;
}

[id^="wpTextbox"] {
    background-color: rgba(20, 20, 20, 0.5)!important;
    color: #aaa!important;
}

div.thumbinner > a > img{
    background-color: rgba(200, 200, 200, 0.7)!important;
    border-color: rgba(220, 220, 220, 0.1)!important;
}
div.thumbinner,
#toc,
.toccolours,
div[id^="mw-Anonymous"],
#filetoc {
    background-color: rgba(30, 30, 30, 0.7)!important;
    border-color: rgba(220, 220, 220, 0.1)!important;
}

#toc,
.toccolours,
div[id^="mw-Anonymous"],
#filetoc {
    background-color: rgba(30, 30, 30, 0.7)!important;
    border-color: rgba(220, 220, 220, 0.1)!important;
}

#catlinks{
    background-color: rgba(20, 20, 20, 0.7)!important;
    border-color: rgba(220, 220, 220, 0.1)!important;
}

#ChangeToU2014_v1_anononly {
    border-color: rgba(220, 220, 220, 0.1)!important;
    background: rgba(20, 20, 20, 0.3)!important;
}

#ChangeToU2014_v1_anononly *,
div[id^="mw-Anonymous"],
#stockphoto_base > span > span:last-of-type {
    color: #aaa!important;
}

div.vectorMenu h3 a {
    background: none!important;
}

div.thumbinner > .thumbcaption,
#siteSub {
    color: rgba(220, 220, 220, 0.5)!important;
}

.hintergrundfarbe5,
table {
    background-color: unset !important;
}

div#simpleSearch {
    background-color: rgba(220, 220, 220, 0.05)!important;
    background-image: none!important;
}

#searchInput {
    color: #eee!important;
}

.imeselector-menu,
div.vectorMenu ul,
#language-settings-dialog,
.grid.uls-menu.uls-wide.callout {
    background-color: rgba(20, 20, 20, 0.95)!important;
}

#languagesettings-panels .menu-section.active,
#languagesettings-panels .menu-section.active:hover,
.uls-language-settings-close-block {
    background-color: rgba(220, 220, 220, 0.15)!important;
    color: #eee!important;
}

.uls-menu .search,
.uls-menu .search > .ten.columns > #search-input-block > #languagefilter,
.uls-menu .search > .ten.columns > #search-input-block > #filtersuggestion,
#uls-no-found-more {
    background: rgba(220, 220, 220, 0.05)!important;
    color: #eee!important;
}

.settings-text {
    color: #777!important;
}

#languagesettings-settings-panel {
    background-color: rgba(220, 220, 220, 0.05)!important;
}

.imeselector a,
.ime-disable {
    color: rgba(220, 220, 220, 0.5)!important;
}

div.vectorTabs,
div.vectorTabs ul,
div.vectorTabs ul li,
div.vectorTabs ul li span {
    background-image: none!important;
    background-color: transparent!important;
    border: none!important;
}

div.vectorTabs ul li {
    box-shadow: inset 0px -30px 25px -35px rgba(160, 160, 160, 0.5)!important;
    margin-left: -1px!important;
    transition: 0.4s!important;
}

div.vectorTabs ul li:hover {
    box-shadow: inset 0px -25px 25px -35px rgba(255, 255, 255, 1)!important;
    margin-left: -1px!important;
    transition: 0.4s!important;
}

.wikitable.float-right > tbody > tr > th a {
    color: #999!important;
    text-decoration: underline!important;
}

.wikitable.float-right > tbody > tr > th a:hover {
    color: #eee!important;
    text-decoration: underline!important;
}

div.NavHead,
table.wikitable > tr > th,
table.wikitable > * > tr > th,
table.wikitable > * > tr:last-of-type > td,
#mw-content-text > p > span {
    background: rgba(255, 255, 255, 0.1)!important;
}

.body > select {
    background-color: #222!important;
    padding-top: 1px!Important;
    height: 22px!Important;
    color: #aaa!important;
    width: 130px!Important;
    border: none!important;
    box-shadow: inset 0px 15px 10px -10px #303030, inset 0px 1px 1px #777!important;
    margin: 5px 0px 5px 0px!important;
    border-radius: 20px!important;
    transition: 0.4s!important;
}

input[type="button"] {
    background-color: #222!important;
    height: 23px!Important;
    color: #aaa!important;
    width: 130px!Important;
    border: none!important;
    box-shadow: inset 0px 15px 10px -10px #303030, inset 0px 1px 1px #777!important;
    margin: 5px 0px 5px 0px!important;
    border-radius: 20px!important;
    transition: 0.4s!important;
}

input[type="button"]:hover {
    background-color: #303030!important;
    height: 23px!Important;
    color: #aaa!important;
    width: 130px!Important;
    border: none!important;
    box-shadow: inset 0px 15px 10px -10px #404040, inset 0px 1px 1px #888!important;
    margin: 5px 0px 5px 0px!important;
    border-radius: 20px!important;
    transition: 0.4s!important;
}

.tex {
    background-color: #eee!important;
    padding: 10px!important;
    border-radius: 5px!important;
}

.bookshelf-container .bookend {
    background-image: url(http://i.imgur.com/YndvKCU.png)!important;
}

.search-container.language-search > form > label {
    color: #eee!important;
}

.divider {
    opacity: 0.4!important;
}

fieldset {
    background-color: rgba(200, 200, 200, 0.2)!important;
}

.central-featured-lang > .link-box > em {
    color: #888!important;
}

.central-featured-lang > .link-box > small {
    color: #666!important;
}

h1.central-textlogo > img {
    visibility: hidden!important;
}

h1.central-textlogo {
    background-image: url(http://i.imgur.com/itY7rT0.png)!important;
    background-repeat: no-repeat!important;
    background-position: center!important;
}
.mw-mmv-post-image {
    background-color: #222;
}
.mw-mmv-image-metadata {
    background-color: #333;
}
.mwe-popups {
    background-color: #333;
}
.navbox,
.navbox-subgroup {
    background-color: #111;
}
.mw-ui-button {
    background-color: #000;
    color: #808080;
}
.app-badges {
    background-color: #333;
}
.lang-list-container {
    background-color: #333;
}
.bookshelf .text {
    background-color: #222;
}
.bookshelf {
    display: block;
    border-top: 1px solid #555;
    -webkit-box-shadow: 0 -1px 0 #444;
    -moz-box-shadow: 0 -1px 0 #444;
    box-shadow: 0 -1px 0 #444;
}
.lang-list-active .lang-list-button {
    background-color: #222;
    outline: 1px solid #666;
    border-color: #666;
}
.lang-list-border {
    background-color: #666;
}
.plainlist {
    background: #222!important;
}
.plainlist ul {
    background: #222!important;
}
.plainlist img {
    filter: invert(1);
}
#mp-topbanner {
    background-color: #222!important;
    color: #999!important;
}
#mp-middle{
    background: #333!important;
}
#mp-lower {
    background: #1e1e1e!important;
}
#mp-tfp-h2 {
    background: #524268!important;
}
#mp-other {
    background: #444!important;
}
#mp-sister {
    background: #444!important;
}
#mp-lang {
    background: #444!important;
}
.mw-body,
.parsoid-body {
    color: #ccc;
}
.oo-ui-tagMultiselectWidget.oo-ui-widget-enabled.oo-ui-tagMultiselectWidget-outlined .oo-ui-tagMultiselectWidget-handle {
    background-color: #323232;
}
.mw-rcfilters-ui-filterTagMultiselectWidget-wrapper-content-title {
    color: #ccc;
}
.oo-ui-tagMultiselectWidget.oo-ui-widget-enabled.oo-ui-tagMultiselectWidget-outlined .oo-ui-tagItemWidget.oo-ui-widget-enabled {
    background-color: #838383;
}
.oo-ui-textInputWidget input,
.oo-ui-textInputWidget textarea {
    background-color: #4b4b4b;
}
.oo-ui-textInputWidget.oo-ui-widget-enabled input::placeholder,
.oo-ui-textInputWidget.oo-ui-widget-enabled textarea::placeholder {
    color: #ddd;
}
.oo-ui-buttonElement > .oo-ui-buttonElement-button::-moz-focus-inner {
    border-color: #333;
}
.mw-rcfilters-ui-changesListWrapperWidget .mw-changeslist-legend {
    background-color: #222;
}
.hlist {
	background-color: #1b1b1b !important;
}
div#mw-content-text.mw-content-ltr div.mw-parser-output div {
    background-color: unset;
}
div#mw-content-text.mw-content-ltr div.mw-parser-output div h2 {
    background-color: unset !important;
}

.toctogglelabel {
	color: #ad7006;
}

.mwe-math-fallback-image-inline {
	filter: invert(100%);
}

.tocnumber {
	color: #999;
}

ul {
	list-style-image: unset;
}

.licensetpl_wrapper{
	background-color: #222 !important;
	border: 1px solid #666 !important;
}

.cbnnr-main {
	background-color: #222;
}

.cbnnr-cta {
	color: #aaa;
}

.cbnnr-close {
	background: #444;
}
}
