css:
@-moz-document url-prefix("https://www.belgiantrain.be/") {
.navigation-bar, .bg-blue, .theme-blue, .navigation-bar__main, .navigation-bar__sidebar, .nav-sidebar--panel .nav-sidebar, .nav-sidebar--panel .nav-sidebar__header, .nav-sidebar--navigation.nav-sidebar--open{
    background-color:#242424 !important;
}

.navigation-bar__item-link, .navigation-bar .basket-btn:first-of-type, .navigation-bar .link:first-of-type, .navigation-bar button:first-of-type, .teaser__content, .link{
    color : whitesmoke;
}

.page, main, body, .footer__service, .sidebar-panel-wrapper.active .sidebar-panel{
    background-color:#606060;
    color : whitesmoke;
}

.bg-light, .bg-shade-lightest, .theme-light{
    background-color: #969696 !important;
    color : whitesmoke;
}

.well__content, .planner__more, .planner__more, .planner__nextday, .cookie-notice__info, .nav-sidebar--panel .nav-sidebar-panel--fixed-open, .nav-sidebar-panel--open{
    background-color: darkgray !important;
    color : whitesmoke;
}

.footer__icolink, a{
    color: #929292;
}

.btn--primary, .btn{
        background: #8c6e60;
    box-shadow: 0 2px 0 #564741;
    color: whitesmoke !important;
}

.alert__title{
    color: red;
}
.alert__extra{
    color: #dd3434;
}
}
