@-moz-document domain("vvvvid.it") {
/* Barra superiore */
.navbar-top{
   width: 626px;
   float: right !important;
   left: auto !important;
   right: 0 !important;
   transition: opacity 1s !important;
   opacity: 0 !important;}
.navbar-top:hover{
   transition: opacity .5s !important;
   transition-delay: .2s !important;
   opacity: 1 !important;}
#menu-notifications{
   display: none !important;}
#menu-user{
   margin-right: -50px !important;}

