.show-addtionalinfo-copyright {
   margin-top: 15px !important;
   margin-left: 105px !important;}
/* Apri forum */
#open-forum {
   top: 64px !important;}

/* Riprendi */
.show-watchLast-button{
   position: fixed !important;
   left: auto !important;
   right: 0px !important;
   top: 125px !important;
   background-color: transparent !important;}
.button-vvvvid-red-background {
   box-shadow: none !important;
   background: linear-gradient(to right, transparent,transparent,black,black) !important;}
.button-35 {
   font-size: 17px !important;
   font-weight: bold !important;
   text-align: right !important;
   letter-spacing: 5px !important;
   color: #cecece !important;}
.button-35:hover {
   color: #00d300 !important;}
.show-buttons-actions>div {
   width: 344px !important;}

 /*>>>>>>>>>> Player <<<<<<<<<<*/
/* Caricamento */
.ppbuffering {
    background-color: transparent !important;}

/* Pulsanti */
.player-actions{
   position: fixed !important;
   left: 0 !important;
   margin-left: 0 !important;
   margin-top: 100vh !important;
   width: 100vw !important;}

.player-action-previous-image, .player-action-next-image, .player-action-rate-image, .player-action-share-image, .player-action-addwtl-image{
   opacity: /*[[op%]]*/ !important;}
.player-action-previous-image:hover, .player-action-next-image:hover, .player-action-rate-image:hover, .player-action-share-image:hover, .player-action-addwtl-image{
   opacity: calc(/*[[op%]]*/ + .3) !important;}

/*[[ep]]*/
 
 /*[[altrip]]*/

/* Nascondi elementi - Vari */
#channel_10011 > .transition-gradient-black, #live-button, #mCSB_2_container > li:nth-of-type(1), #mCSB_2_container > li:nth-of-type(2), #mCSB_2_container > li:nth-of-type(3), #mCSB_2_container > li:nth-of-type(4), #mCSB_2_dragger_vertical, #mCSB_2_scrollbar_vertical, #mCSB_2_scrollbar_vertical > .mCSB_draggerContainer, #mCSB_8_container > li:nth-of-type(2), #mCSB_8_container > li:nth-of-type(3), #mCSB_8_container > li:nth-of-type(4), #mCSB_8_container > li:nth-of-type(5), #switch-channel, #switch-games, #switch-kids, .expanded.clickable > div:nth-of-type(2) > .dialogTitle, .info > .clickable.collapsed > .header-opaque, .info > .clickable.collapsed > div:nth-of-type(2), .info > .clickable.collapsed > div:nth-of-type(2) > .dialogTitle, .related > .clickable.collapsed > .header-opaque, .related > .clickable.collapsed > div:nth-of-type(2), .show-likes, .show-share, .show-type, .show.last.player-action-next-container, .topics > .slide, li:nth-of-type(1) > .add > .profile-topics-topic-name, li:nth-of-type(1) > .add > span, li:nth-of-type(2) > .add > .profile-topics-topic-name, li:nth-of-type(2) > .add > span, li:nth-of-type(3) > .add > .profile-topics-topic-name, li:nth-of-type(3) > .add > span, li:nth-of-type(4) > .add > .profile-topics-topic-name, li:nth-of-type(4) > .add > span, li:nth-of-type(5) > .add > .profile-topics-topic-name, li:nth-of-type(5) > .add > span, .player-action-next-label-show.player-action-next-label, .player-action-previous-label-show.player-action-previous-label{
