css:
@-moz-document domain("anilibria.tv") {
	
	:root {
		--body-bg: #F3F3F3 !important;
		--card-bg: #E8EDF1 !important;
		--card-bg-2: #F8FAFB !important;
		--nav-bg: #313131 !important;
		--op-bg: rgba(243, 243, 243, .8) !important;
		--border-color: #BEBEBE !important;
		--link-color: #44B39D !important;
		--primary-color: #212121 !important;
		--secondary-color: #7E7E7E !important;
		--invert: invert(0) !important;
	}
	
    #hslider {
		display: none !important;
	}
	img#logopic {
		padding: 0px !important;
		margin-left: -350px !important;
		margin-top: -30px !important;
	}
	ul.main-navigation {
		position: absolute !important;
		background: #313131!important;
		width: 1560px;
		line-height: 20px !important;
		margin-top: -25px;
		margin-left: -40%;
		padding: 15px 0px 15px 360px;
	}
	a#activelink0, a#activelink1, a#activelink2,
	a#activelink3, a#activelink4, a#activelink5,
	a#activelink6, a#activelink7 {
		background: none;
	}
	div#headercontent {
		height: 0px !important;
	}
	body {
		margin-top: 25px !important;
	}
	/* ADS BLOCKs*/
	#advideo_adv_subs, #vidplay_autoplay_ad, #advideo_adv, #adv_kod_frame
	center[src*="cdn.advideo.ru"], #twitter-widget-0, div.asidehead,
	div.torrent_block, a[href*="https://pushall.ru/"],
	iframe[src*="https://video-play.ru/"], script[src*="https://video-play.ru/js/"]{
		display: none !important;
	} /* ADS BLOCKs END*/
	.bx_filter {
		width: 1220px;
		margin-top: 30px;
		margin-bottom: 30px;
	}
	.bx_filter .bx_filter_button_box, .bx_filter .bx_filter_parameters_box {
		margin: 0px !important;
		padding: 0px !important;
		width: 1130px !important;
	}
	div[id*="comp_"] {
		width: 1240px !important;
		margin-left: 10px;
	}
	div.torrent_block_main {
		padding: 10px !important;
		background-color: #424242;
		width: 250px !important;
		height: 350px !important;
		margin: 0px 40px 40px 0px !important;
	}
	.torrent_descr_wrap {
		width: 250px !important;
		height: 350px !important;
	}
	img.torrent_pic {
		width: 250px !important;
		height: 350px !important;
	}
	p.torrent_name a, p.torrent_name a:visited {
		display: none !important;
	}
	div.navigation-block {
		width: 1200px;
		font-size: 15pt !important;
	}
	div.page-navigation {
		background-color: #282829;
		padding: 16px 13px;
		color: #fff;
		background: #383535 !important;
		border-radius: 100px;
	}
	div#aside-search {
		position: absolute !important;
		margin-top: -25px !important;
		width: 370px !important;
		border-top: 0 !important;
		border-left: 0 !important;
		border-right: 0 !important;
		border-bottom: 0 solid #fff !important;
	}
	div#aside-search input[type="search"] {
		width: 370px !important;
		height: 40px !important;
		background-color: #4b4747!important;
		color: #fff;
	}
	div#aside-search input[type="search"]:focus {
		background-color: #c2bebe!important;
		color: #000 !important;
	}
	div#aside-search input[type="submit"] {
		border: none !important;
		height: 40px !important;
	}
	.authbtn {
		position: absolute !important;
		margin-top: -57px !important;
		margin-left: 470px !important;
	}
	div.news_block {
		width: 1160px;
		margin: 15px 0px 5px 0px;
		padding: 10px 10px;
		border: 10px solid #2f2e2e;
	}
	a.chitat-dalee, a.chitat-dalee:visited {
		margin-right: 10px;
		padding: 7px 20px;
		border: 3px solid #e74c3c;
	}
	div.news-content a img {
		width: 1160px;
	}
	div.libria_static_page, div.forum-info-box,
	div.forum-header-box {
		width: 1150px;
	}
	div.forum-block-container {
		width: 1190px;
		margin-top: 15px;
		border: 7px solid #3a3939!important;
	}
	div.forum-info-box, div.forum-header-box {
		border: 7px solid #3a3939!important;
		margin-top: 15px;
	}
	div.torrent_content {
		width: 1150px;
		border: 7px solid #3a3939!important;
	}
	.title-divider {
		height: 1px;
		background: #0000004d;
		margin: 0 22px;
	}
	hr.poloska-detail {
		width: 760px;
	}
	p.detail-description {
		width: 760px;
		text-align: justify;
	}
	div.detail_torrent_content hr.poloska-detail {
		width: 1110px;
	}
	div.torrent-second-col, div.torrent-fourth-col {
		padding-right: 0px;
		padding-left: 70px;
	}
	div.torrent-third-col {
		padding-right: 30px;
		padding-left: 30px;
	}
	.tab-switcher {
		width: 1130px;
	}
	.tab.show {
		padding: 10px 140px;
	}
	div.news-comments-block {
		width: 1110px;
		padding: 20px;
		border: 7px solid #3a3939!important;
	}
	div.reviews-navigation-box {
		width: 1110px;
		border: 7px solid #3a3939!important;
		margin: 10px 0 10px 0;
	}
	div.reviews-link-box {
		width: 1110px;
		border: 7px solid #3a3939!important;
		margin: 10px 0 10px 0;
		padding: 10px 30px 6px 10px;
		text-align: left;
	}
	footer {
		height: 40px;
	}
	div#footer_content {
		display: none;
	}
	#scrollup {
		left: 30px;
		bottom: 50px !important;
	}
	#anilibria_tp {
		right: 30px;
		bottom: 50px !important;
	}
}
