css:
@-moz-document domain("2ch.hk") {
:root {
    /* Background colors */
    --base03: #002b36;
    --base02: #073642;
    /* Content colors */
    --base01: #586e75;
    --base00: #657b83;
    --base0: #839496;
    --base1: #839496;
    /* Light background colors */
    --base2: #eee8d5;
    --base3: #fdf6e3;
    /* Additional colors */
    --syellow: #b58900;
    --sorange: #cb4b16;
    --sred: #dc322f;
    --smagenta: #d33682;
    --sviolet: #6c71c4;
    --sblue: #268bd2;
    --scyan: #2aa198;
    --sgreen: #859900;
}

* {
    box-sizing: border-box;
}

.cntnt__right {
    display: flex;
    flex-direction: column;
    align-items: center;
}

.hidefm {
    display: none;
}

aside {
    display: none;
}

.search .input {
    width: 480px;
}

.post__file-preview,
.desktop img {
    filter: brightness(0.20);
    filter: opacity(0.2);
}

.post__file-preview:hover {
    filter: brightness(1);
}

html,
body,
.post,
.adminbar__boards,
.input,
.thread {
    background-color: var(--base03);
    color: var(--base0);
}

.fm__header,
.postscontainer {
    background-color: var(--base03);
    color: var(--base01);
    border: 1px solid var(--base02);
}

hr,
.adminbar__menu,
.post,
.input,
.thread::after {
    border-color: var(--base02);
}

.post_type_reply::before {
    color: var(--base02);
}

input {
    background-color: var(--base02);
}

.input:focus {
    background-color: var(--base02);
}

.thread {
    display: flex;
    flex-direction: column;
}

.thread {
    max-width: 1020px;
}

.post_type_reply {
    padding-top: 10px;
    width: 100%;
    border-bottom: 0;
    border-left: 0;
    border-right: 0;
}

.post__message {
    overflow: hidden;
}

.post_preview {
    width: auto;
}

.post__title {
    color: var(--sblue);
}

.a-link-emulator,
a {
    color: var(--sorange);
}

.post__ophui,
.unkfunc {
    color: var(--sgreen);
}

.post__reflink {
    color: var(--base1);
}

.post__details,
.post__filezise {
    color: var(--base01);
    padding: 2px 0;
}

.icon {
    fill: var(--base1);
}

path[fill="#ececec"] {
    fill: var(--base03);
}

.spoiler,
.spoiler a {
    color: var(--base01);
    background-color: var(--base01);
}

.thread_wrap {
    background-color: var(--base03);
    border: 1px solid var(--base02);
}

.thread_wrap::after {
    display: none;
}

.na {
    display: none !important;
}

.spoiler:hover a {
    background-color: var(--base03);
    color: var(--sorange);
}

.fav,
.bb__footer hr,
.bb_type_boardstats {
    background-color: var(--base03);
    border: 1px solid var(--base02);
    color: var(--base0);
}



.spoiler:hover {
    color: var(--base0);
    background-color: var(--base03);
}

.qr_reply,
.qr__header,
.postform_qr .button,
input#submit {
    background-color: var(--base02);
}

.qr_reply,
.postform_qr .button,
.qr__footer > hr,
input#submit {
    border: 1px solid var(--base03);
}

.postform_qr .button,
input#submit {
    color: var(--base0);
}
}
