css:
@-moz-document url-prefix("https://sigaa.ufrn.br"){
}
@-moz-document regexp('https://sigaa.ufrn.br/sigaa/(?!public).+') {
/* Página inicial */
.logon h3 {
    color: initial;
    padding: 1px 5px;
    background: #5CD6F0;
    width: 50%;
    text-align: center;
}
.logon table {
    background: #FAFAFA;
    border: 1px solid #5cd6f0;

    border-width: 3px 1px;
    border-collapse: separate;
    padding-top: 10px;
    padding-bottom: 5px;
}
/* Pagina Principal */
#container::before {
    content: "Esse tema só pode ser visualizado corretamente no Mozilla Firefox com o script para Greasemokey.";
    border: 3px dotted #34accc;
    display: block;
    background: #5CD6F0;
    font-weight: bold;
    text-align: center;
    padding: 3px;
    color: #000;
    font-size: 1.07em;
}
#info-sistema {
    color: #DBF9FC;
    background: #34accc;
    padding: 1px 4px 0 4px;
    position: relative;
    text-align: left;
    margin: 0 auto;
    display: block;
    border-bottom: none;
}
#info-sistema .dir {
    top: 0;
    right: 0;
    text-align: right;
    position: absolute;
    display: inline;
    padding-right: 5px;
    color: inherit;
}
#info-sistema .sair-sistema a {
    color: inherit;
}
#info-sistema .acessibilidade a {
    font-size: 1.1em;
    font-weight: bolder;
    color: inherit;
    margin-left: 2px;
    letter-spacing: 0.1px;
    word-spacing: 0.1px;
}
#painel-usuario {
    background: #5CD6F0;
    border-bottom: 1px solid #54C4DB;
    color: #222;
    position: relative;
    min-height: 48px;
}
#cabecalho #painel-usuario #menu-usuario li a
{
    background-image: none;
    background: #34accc;
	
}
#menu-dropdown {
    background: #34accc;
    padding: 1px 0 1px 10px;
}
.ThemeOfficeMainItem {
    background-color: inherit;
}
.ThemeOfficeMainItemHover {
    background: #000000;
    background: rgba(255, 255, 255, .2);
}
.ThemeOfficeMainFolderLeft, .ThemeOfficeMainItemLeft, .ThemeOfficeMainFolderText, .ThemeOfficeMainItemText, .ThemeOfficeMainFolderRight, .ThemeOfficeMainItemRight {
    background: initial;
}
#painel-usuario #menu-usuario {
    background: none;
    float: right;
    width: 400px;
    padding: 2px 0;
}
#noticias-portal {
    background: inherit !important;
    border-bottom: 1px solid lightgray;
    overflow-y: scroll;
    scrollbar-width: thin;
    border-left: 5px solid #ddd;
}
.controlbutton {
	background: #000;
    opacity: 0.1;
}

.rightnextbutton {
    width: 40px;
    margin-left: -40px;
    height: 100px;
    position: sticky;
    right: 0;
    top: 0;
    float: right;  
}
.leftnextbutton {
    width: 40px;
    margin-right: -40px;
    height: 100px;
    position: sticky;
    left: 0;
    top: 0;
    float: left; 
}
.pausebutton {
    width: 30px;
    height: 30px;
    margin-left: -30px;
    position: sticky;
    right: 50px;
    top: 70px;
    float: right; 
    background-image: url('https://raw.githubusercontent.com/google/material-design-icons/master/av/2x_web/ic_pause_white_48dp.png');
	background-repeat: round;
}
.pausebutton.active {
    background-image: url(
    	'https://raw.githubusercontent.com/google/material-design-icons/master/av/2x_web/ic_play_arrow_white_48dp.png');
}
.viewallbutton {
    width: 30px;
    height: 30px;
    margin-left: -30px;
    position: sticky;
    right: 90px;
    top: 70px;
    float: right; 
    background-image: url(
    	'https://raw.githubusercontent.com/google/material-design-icons/master/action/2x_web/ic_view_list_white_48dp.png');
	background-repeat: round;
}
.simple-panel{
    background: #EAEAEA !important;
    padding: 10px 5px 5px 10px;
    margin: 3em 0 0 5px;
}
#main-docente table thead th {
    background: #DDD;
    border-bottom: 1px solid #96999E;
}
#main-docente table thead th:first-child {
	padding-left: 2px;
}
.linhaPar {
    background-color: #ECECEC;
}
.linhaImpar {
    background-color: #FFF;
}
#portal-docente .listagem  a{
	color: #000;
}
.simple-panel table tr a, .mais a{
	color: #000 !important;
}
#portal-docente span.mais {
	border-top: 1px solid #DDD;
}
#main-docente table tr.odd {
    background: #FFF;
}
table.listagem, table.subListagem {
    font-size: 1.0em;
    border-collapse: collapse;
    width: 100%;
    margin: 0 auto;
    border: 1px solid #DDD;
    background-color: transparent;
    border-width: 0 1px;
}
div.descricaoOperacao {
    margin: 10px auto;
    padding: 10px 20px;
    border: 2px dashed #000;
    background: transparent;
    line-height: 1.2em;
}
a, a:link, a:visited {
	color: #000;
    cursor: pointer;
    text-decoration: none;
	font-weight: bold;
}
a:hover, a:link:hover, a:visited:hover {
	color: #333;
    text-decoration: solid underline #333;
}
.pessoal-docente a, .pessoal-docente a:link, .pessoal-docente a:visited {
    color: #145;
}
#portal-docente #perfil-docente {
    width: 300px;
    float: right;
    background: #a6d6e6;
}
#portal-docente #perfil-docente .pessoal-docente {
    margin: 4px;
    background: transparent;
}
#portal-docente #perfil-docente #agenda-docente {
    padding: 5px;
    background: #8ac1e9;
    margin: 5px;
}
#painel-erros {
    background: #34accc;
    padding: 0;
    border-bottom: 4px double black;
    border-top: 4px double black;
}
#painel-erros ul.info {
    background: initial;
}
#painel-erros ul.info li, #painel-erros ul.erros li {
    color: #000;
}
#painel-erros ul.erros, #painel-erros ul.warning {
    background: url(
    	https://raw.githubusercontent.com/google/material-design-icons/master/alert/2x_web/ic_warning_white_48dp.png) 
        no-repeat left center;
    background-size: contain;
    margin: 0 10px;
    padding-left: 80px;
}

#fechar-painel-erros {
	color: #000 !important;
}
#forum-portal a {
	color: #000;
}
.toplogon {
    display: none;
    z-index: 10;
    position: absolute;
    color: #000;
    top: 20px;
    padding: 20px 10px;
    background: #34accc;
    border-width: 2px 3px;
    border-style: solid;
    border-color: #09b;
}
.loginbutton {
    border: 1px solid #a44;
    padding: 5px;
    background: #c44;
    border-radius: 5px;
    font-weight: bold;
    cursor: pointer;
    margin: 0 8px;
}
.toplogon table input {
    margin: 1px 3px;
}
.toplogon input[type="submit"]
{
    cursor: pointer;
    margin-top: 10px;
	margin-bottom: -5px;
}
input[type="submit"]:hover
{
    background-color: #ddd;
}
.toplogon tbody
{
	color: #000;
}
/* Páginas de atualização do cadastro único */
table.formulario tfoot tr {
    background: #5CD6F0;
}
table.formulario caption, table.listagem caption, table.visualizacao caption, h3.tituloTabela {
    margin: 0 auto;
    padding: 3px 0;
    font-weight: bold;
    font-variant: small-caps;
    color: #FFF;
    letter-spacing: 1px;
    text-align: center;
    background: #5CD6F0;
}
input, textarea, select {
    font-family: Verdana, sans-serif;
    font-size: 1em;
}
h2, h2#title {
    font-size: 1.2em;
    color: #000;
    font-variant: small-caps;
    margin: 0 0px 15px;
    padding: 4px 0 2px 22px;
    letter-spacing: 0px;
    text-align: left;
    background: #6de;
    border-bottom: 1px solid #54C4DB;
}

#rodape {
    position: relative;
    bottom: 0px;
    border-top: 3px solid #54C4DB;
    background: #34accc;
    font-size: 0.9em;
}
table.listagem tr.periodo td {
    background: #5CD6F0;
    padding-left: 10px;
    font-weight: bold;
}
.box-geral .btn, input[type="submit"], input[type="button"] {
    display: inline-block;
    margin-bottom: 0;
    font-weight: normal;
    vertical-align: middle;
    cursor: pointer;
    background-image: none;
    padding: 6px 10px;
    color: #222;
    background-color: #ddd;
    border: none;
}
.box-geral .btn:hover, input[type="submit"]:hover, input[type="button"]:hover {
	background-color: #ccc;
}
.box-geral .box-secao .titulo {
    color: #333;
    font-variant: small-caps;
    margin: 0 0 15px;
    border-bottom: 1px solid #C4D2EB;
    padding: 4px 0 2px 22px;
    letter-spacing: 0;
    text-align: left;
    background: #DEDFE3;
}
.box-geral .btn-primary {
    background-color: #5df;
    color: #000;
}
.box-geral .btn-primary:hover {
    background-color: #0cf;
}
table.listagem > tfoot, table.listagem > tfoot td {
    padding-top: 10px;
    padding-bottom: 10px;
    background: none;
}
table.listagem tbody tr td.periodo {
    background: #5CD6F0;
    padding: 3px;
    font-weight: bold;
    color: #fff;
}
.ThemeOfficeMenuItem {
    background-color: #34accc;
}
.ThemeOfficeMenuItem .ThemeOfficeMenuFolderLeft, .ThemeOfficeMenuItem .ThemeOfficeMenuItemLeft {
    padding: 3px 3px 3px 2px;
    background-color: transparent;
}
.ThemeOfficeSubMenu {
    position: absolute;
    border: 1px solid #09a;
    border-top-width: 0;
}

/* Página da turma */
div#cabecalho {
    border: none;
    background: #5CD6F0;
    border-bottom: 5px solid #34accc;
}
.ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {
    border: 1px solid #34accc;
    background: #aef;
    font-weight: bold;
    color: #36a;
}
.ui-state-default{
    background-color: #aef !important;
}
.ui-state-hover {
	color: #000;
}
div.ui-layout-resizer {
    background: #FFF;
}
.rich-stglpanel{
	border: none !important;
}
.rich-panelbar{
	background: transparent !important;
    border: 1px solid #34accc !important;
    border-width: 1px 0 !important;

}
.ui-layout-pane > table td, .rich-stglpanel-header, .rich-panelbar-interior {
	color: inherit !important;
	background: #5CD6F0 !important;
    border: none !important;
    border-bottom: 1px solid #34accc !important;
}
.itemMenuHeaderTurma, .itemMenuHeaderAlunos, .itemMenuHeaderDE, .itemMenuHeaderMateriais, .itemMenuHeaderAtividades, .itemMenuHeaderConfig, .itemMenuHeaderRelatorios, .itemMenuHeaderAjuda, .itemMenuHeaderQuestionarios {
    height: 17px;
    text-indent: 15px;
    padding-top: 0;
    background-color: transparent;
    border: none;
    background-repeat: no-repeat;
    background-position: top left;
    line-height: 17px;
}
.itemMenu {
    vertical-align: middle;
    border-bottom: 1px solid #39b;
    height: 22px;
    color: #000;
    font-weight: bold;
    padding-top: 5px;
    text-indent: 20px;
    cursor: pointer;
    background: #34accc;
}
.itemMenu:hover {
	background: #aef;
}
.ui-layout-pane {
    background: #FFF;
    border: 1px solid #34accc;
    overflow-x: hidden;
    scrollbar-width: thin;
    scrollbar-color: #34accc #5CD6F0;
    border-left: 4px solid #5CD6F0;
}
.botaoDireita {
    margin-left: 5px !important;
    border: 1px solid #34accc;
    background-color: #5CD6F0;
    background-position: 5px 5px;
}
.botaoDireita:hover {
	background-color: #34accc;
}
/* design da modal de turmas */ 
.ui-widget-header {
    color: #000;
    font-weight: bold;
    background: #5CD6F0;
    border: 1px solid #34accc;
}
#j_id_jsp_1879301362_4 > form > a, #j_id_jsp_303365748_2 > .listagem img, #conteudo > .infoAltRem > img {
	color: #000 !important;
    filter: saturate(10);
	-webkit-filter: saturate(10);
}
body {
    background: #fff;
}
}
