.branding,
.bucketwrap.resaudio .audio-module-cta,
.bucketwrap.resaudio .audio-module-queue .audio-module-queue-text,
.container .conheader,
.container .conintrotext,
.flex-location-wrap .agglocation .bucketwrap li a,
.hp-item .slug,
.item .title>a,
.item-podcast .slug,
.item-podcast,
.menu__item,
.mhp-section.radio-streams .stream-genre,
.more-podcasts,
.navigation__account,
.navigation__donate,
.navigation__shop,
.npr-footer,
.npr-footer__header,
.npr-player .player-extras button,
.npr-player .player-initial .item-initial.item-localization a .tag-one,
.npr-player .player-initial .item-initial.item-localization button .tag-one,
.npr-player .player-initial .item-initial.item-music a .tag-one,
.npr-player .player-initial .item-initial.item-music button .tag-one,
.npr-player .player-initial .item-initial.item-news a .tag-one,
.npr-player .player-initial .item-initial.item-news button .tag-one,
.options__load-more,
.podcast-section.browse-by-topic li a,
.podcast-section.browse-by-topic li,
.podcast-section.channel-detail-overview .detail-overview-content>header,
.podcast-section.channel-detail-overview .detail-overview-content>p,
.podcast-section.episode-list .item .item-info pre,
.podcast-section.episode-list .item .item-info>div:not(.bucketwrap),
.podcast-section.episode-list .item .item-info>p,
.podcast-section.episode-list .item .item-info>ul>li,
.podcast-section.episode-list .item .teaser,
.podcast-section.episode-list,
.podcast-section>header h1,
.podcast-tools__item,
.recommended-story__slug,
.recommended-story__title a,
.storytext h3.edTag,
.storytext h6.edTag,
.subtopics,
.tags,
.teaser,
.title,
