.mhp-section.this-just-in,
.mhp-section.videos,
.podcast-section.podcast-agg .item-podcast
{
	margin: 12px!important;
}

selector {
	margin: 3rem!important;
}

/*___ Paddings _____________________________________________________________________*/

.agg #infinitescroll .item-podcast,
.mhp-section.articles,
.mhp-section.first-listens,
.mhp-section.interstitial,
.mhp-section.latest-news,
.mhp-section.programs,
.mhp-section.radio-streams,
.mhp-section.this-just-in,
.mhp-section.videos,
.podcast-section.podcast-agg .item-podcast,
story-card .card__content.grid__column.grid__column--flex
{
	padding: 12px!important;
}

selector {
	padding: 6px!important;
}

/*___ Heights ______________________________________________________________________*/

selector {
	max-height: none!important;
}

selector {
	height: auto!important;
}

/*___ Widths _______________________________________________________________________*/

#programs-list .bucketwrap.internallink.musicaggpromo,
.correction,
.storytext>.edTag,
.storytext>.hr,
.storytext>blockquote,
.storytext>ol.edTag,
.storytext>ol.mostEmailedList,
.storytext>p,
.storytext>ul.edTag,
.tags
{
	max-width: none!important;
}

selector {
	width: auto!important;
}

#wrapper {
	width: 98%!important;
}

.agg #infinitescroll .item-podcast,
.podcast-section.podcast-agg .item-podcast {
    width: 23%!important;
}







}

@-moz-document domain("npr.org"), domain("secure.npr.org") {
/*__________________________________________________________________________________*/
a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/


a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.agg .item .teaser a,
.branding .branding__mini-teaser,
.janrainContent #janrainProviderPages .janrainPage .providers li a span,
.npr-player .player-extras button,
.podcast-section.episode-list .item .item-info pre,
.podcast-section.episode-list .item .item-info>div:not(.bucketwrap),
.podcast-section.episode-list .item .item-info>p,
.podcast-section.episode-list .item .item-info>ul>li,
.podcast-section.episode-list .item .teaser,
li,
p {
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Khaki ________________________________________________________________________*/

#featured .item .title>a,
.item .title>a,
.npr-player .player-initial .item-initial.item-localization a .tag-one,
.npr-player .player-initial .item-initial.item-localization button .tag-one,
.npr-player .player-initial .item-initial.item-music a .tag-one,
.npr-player .player-initial .item-initial.item-music button .tag-one,
.npr-player .player-initial .item-initial.item-news a .tag-one,
.npr-player .player-initial .item-initial.item-news button .tag-one,
.submenu__category,
h1,
h2 {
	color: #F0E890!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Burlywood ____________________________________________________________________*/


h3,
h4 {
	color: #e0b880!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Sandy Brown __________________________________________________________________*/

#hp-more .hp-label,
h5,
h6,
label {
	color: #F0A060!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________________*/

.bucketwrap.resaudio .audio-module-duration,
.bucketwrap.resaudio .audio-module-listen-duration,
.teaser .date,
progress-bar .player__progress-bar__time,
time
{
	color: #07beff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Green ________________________________________________________________________*/


selector {
	color: #07ff24!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Red __________________________________________________________________________*/


selector {
	color: #ff0707!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
}

/*__________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

::placeholder { /* Chrome, Firefox, Opera, Safari 10.1+ */
  color: #f48f8f;
  opacity: 1; /* Firefox */
}

/*___ Code _________________________________________________________________________*/

.token.punctuation {
    color: #fff!important;
}

.token.selector, .token.attr-name, .token.string, .token.char, .token.builtin, .token.inserted {
    color: #af0!important;
}

.token.atrule, .token.attr-value, .token.keyword {
    color: #00b3ff!important;
}

.token.property, .token.tag, .token.boolean, .token.number, .token.constant, .token.symbol, .token.deleted {
    color: #ff6cbe!important;
}
























}

