css:
@-moz-document domain("iichan.hk") {
/* Глобальное */
html, body {
    background: url(https://i.imgur.com/GKmEkod.jpg) center center fixed;
    background-color: hsl(30, 100%, 4%);
    background-size: cover;
    color: hsl(30, 100%, 75%);
    padding: 0;
    text-shadow: 0px 0px 4px;
}

a {
    color: hsl(30, 100%, 50%);
}

a:hover {
    color: hsl(30, 100%, 75%);
}

a:visited {
    color: hsl(30, 100%, 40%);
}

hr {
    color: hsl(30, 100%, 25%);
    margin: 8px;
}

img.banner, img.thumb, div.greeting > img, div.catthread > img, a > video, img.de-fullimg, img.de-file-img, div.de-fullimg-wrap > video {
    filter: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' color-interpolation-filters='sRGB' shape-rendering='optimizeSpeed'><filter id='pixelize' x='0' y='0'><feGaussianBlur in='SourceGraphic' stdDeviation='0.2' result='blured'/><feFlood x='1' y='1' width='1' height='1'/><feComposite width='3' height='3'/><feTile result='pixels'/><feComposite in='blured' in2='pixels' operator='in'/><feMorphology operator='dilate' radius='1'/><feComponentTransfer><feFuncR type='gamma' exponent='1.0' amplitude='0.84' offset='0.08' /><feFuncG type='gamma' exponent='1.0' amplitude='0.84' offset='0.08' /><feFuncB type='gamma' exponent='1.0' amplitude='0.84' offset='0.08' /></feComponentTransfer><feComponentTransfer><feFuncR type='discrete' tableValues='0 .2 .4 .6 .8 1' /><feFuncG type='discrete' tableValues='0 .2 .4 .6 .8 1' /><feFuncB type='discrete' tableValues='0 .2 .4 .6 .8 1' /><feFuncA type='discrete' tableValues='0 1' /></feComponentTransfer></filter></svg>#pixelize") sepia(50%) drop-shadow(0px 0px 2px);
}

img.banner:hover, img.thumb:hover, div.greeting > img:hover, div.catthread > img:hover, a > video:hover, img.de-fullimg:hover, img.de-file-img:hover, div.de-fullimg-wrap > video:hover {
    filter: unset;
}


/* Меню */
.adminbar {
    background-color: hsla(30, 100%, 10%, 0.8);
    background-image: none;
    margin-bottom: 4px;
}


/* Форма */
.theader {
    background-color: hsla(30, 100%, 20%, 0.5);
}

.postblock {
    background: hsl(0, 0%, 0%, 0);
}

textarea, input[type='text'], input[type='password'] {
    background-color: hsla(30, 100%, 5%, 0.5);
    border: 1px solid hsl(30, 100%, 20%);
    border-radius: 3px;
    padding: 4px;
}

button, input[type='button'], input[type='submit'], input[type='reset'] {
    background-color: hsl(30, 100%, 5%);
    border: 1px solid hsl(30, 100%, 20%);
    margin: 1px;
    padding: 4px 16px;
}

button:hover, input[type='button']:hover, input[type='submit']:hover, input[type='reset']:hover {
    background-color: hsl(30, 100%, 10%);
}

button:active, input[type='button']:active, input[type='submit']:active, input[type='reset']:active {
    background-color: hsl(30, 100%, 20%);
}

input[type='checkbox'], input[type='radio'], input[type='file'], select {
    filter: invert(100%) sepia(100%) drop-shadow(0px 0px 2px);
}

#captcha {
    filter: contrast(50%) brightness(200%) sepia(100%) drop-shadow(0px 0px 4px);
}

.prohibition {
    color: hsl(15, 100%, 50%);
}


/* Треды */
body > form > div > table {
    border-spacing: 0;
    margin-top: 8px;
}

.reply {
    background-color: hsla(30, 100%, 25%, 0.5);
    border-radius: 2px;
    box-shadow: hsla(30, 100%, 50%, 1.0) 0px 0px 4px 0px;
}

.abbrev, .omittedposts {
    color: hsl(30, 40%, 50%);
}

.filetitle, .replytitle {
    color: hsl(15, 100%, 50%);
}

.postername, .commentpostername {
    color: hsl(45, 100%, 50%);
}

.adminname {
    color: hsl(0, 100%, 50%);
}

.postertrip {
    color: hsl(60, 100%, 50%);
}

.reflink a {
    color: hsl(30, 100%, 50%);
}

.postnum {
    color: hsl(30, 40%, 50%);
}


/* Сообщение */
.unkfunc {
    color: hsl(45, 33.3%, 50%);
}

.spoiler, .spoiler:hover {
    background-color: hsla(30, 50%, 20%, 0.5);
    color: hsla(0, 0%, 0%, 0);
}


/* Главная */
.news {
    background-color: hsla(30, 100%, 20%, 0.5);
}

.header {
    background-color: hsl(30, 100%, 30%, 0.5);
}

.category {
    background-color: hsl(30, 100%, 10%, 0.5);
}

.list div a:hover {
    background-color: hsl(30, 100%, 60%);
    color: hsl(30, 100%, 20%) !important;
}


/* Панель управления */
.passvalid {
    background-color: hsla(30, 100%, 20%, 0.5);
}

.passvalid ~ form > table td, .passvalid ~ form > table th:not([colspan]) {
    border-color: hsla(30, 100%, 25%, 0.5);
}

.managehead {
    background-color: hsla(30, 100%, 15%, 0.5);
}

.row1 {
    background-color: hsla(30, 100%, 10%, 0.5);
}

.row2 {
    background-color: hsla(30, 100%, 20%, 0.5);
}


/* Куклоскрипт */
#de-panel, .de-win-head, .de-file-utils, .de-popup-btn {
    background-color: hsl(30, 100%, 10%) !important;
}

#de-main > .de-win-body, #de-win-fav > .de-win-body, #de-win-hid > .de-win-body, #de-win-reply > .de-win-body, .de-fold-block, .de-menu, .de-pview, .de-popup, .de-cfg-body, #de-cfg-buttons {
    border: 1px solid hsl(30, 100%, 10%) !important;
}

#de-win-reply {
    background-color: hsla(30, 100%, 25%, 0.9) !important;
}

.de-file {
    background-color: hsla(30, 38%, 50%, .15);
    border: 1px dashed hsl(30, 50%, 50%);
}

.de-file-utils {
    filter: sepia(100%);
}

.de-pview {
    background-color: hsla(30, 100%, 20%, 0.9) !important;
}

.de-new-post {
    box-shadow: hsla(120, 100%, 50%, 0.5) 0px 0px 4px 0px !important;
}

.de-selected {
    box-shadow: hsla(0, 100%, 50%, 0.5) 0px 0px 4px 0px !important;
}

[class^='de-btn'], .de-post-counter, .de-wait {
    filter: sepia(50%) drop-shadow(0px 0px 1px);
}


/* Фиксы */
button, input, select, textarea, .greeting, .news, .logo, .logo > a, .sublogo, .sublogo > a, .header, .list div a, .postblock, .reply, .highlight, .spoiler:hover, .managehead, .row1, .row2 {
    color: inherit !important;
}

.list {
    border: none;
}

.adminbar > a {
    text-shadow: inherit;
}
}
