css:
@-moz-document url-prefix("http://www.chexquest.org") {
/* Styles for the general looks for the Curve theme.
------------------------------------------------------- */

/* Normal, standard links. */
a:link, a:visited
{
	color: #c5a79c;
	text-decoration: none;
}
a:hover
{
	text-decoration: underline;
}

/* Tables should show empty cells. */
table
{
	empty-cells: show;
}

/* Set a fontsize that will look the same in all browsers. */
body
{
/*	background: #252525 url(https://i.imgur.com/WEsZniq.png) repeat-x; */
	background-image: url('https://i.imgur.com/WEsZniq.png');
    background-repeat: repeat;
	font: 78%/130% verdana, Helvetica, sans-serif;
	margin: 0 auto;
	padding: 15px 5%;
}

/* Help popups require a different styling of the body element. */
body#help_popup
{
	padding: 1em;
}

/* use dark grey for the text, leaving black for headers etc */
body, td, th, tr
{
	color: #7f7f7f;
}

/* This division wraps the entire forum when a forum width is set. */
div#wrapper
{
	margin: 0 auto;
}

/* lets give all forms zero padding/margins */
form
{
	padding: 0;
	margin: 0;
}

/* We can style the different types of input buttons to be uniform thoughout different browsers and their colour themes.
	.button_submit - covers input[type=submit], input[type=button], button[type=submit] and button[type=button] in all browsers
	.button_reset  - covers input[type=reset] and button[type=reset] thoughout all browsers
	.input_check	- covers input[type=checkbox] thoughout all browsers
	.input_radio	- covers input[type=radio] thoughout all browsers
	.input_text	 - covers input[type=text] thoughout all browsers
	.input_file	 - covers input[type=file] thoughout all browsers
*/

input, button, select, textarea
{
	font: 95%/115% verdana, Helvetica, sans-serif;
	color: #FFFFFF;
	background: #2e2d2d;
	border: 1px solid #656565;
	padding: 2px;
}

/* Select elements look horrible with the extra padding, so leave them unpadded. */
select
{
	padding: 0;
}

/* Add some padding to the options instead. */
select option
{
	padding: 1px;
}

/* The font size of textareas should be just a little bit larger. */
textarea
{
	font: 100%/130% verdana, Helvetica, sans-serif;
}

/* Buttons should be styled a bit differently, in order to make them look more button'ish. */
.button_submit, .button_reset
{
	background: #191919 url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/submit_bg.png) no-repeat;
	border: 1px solid #545454;
	cursor: pointer;
	font-weight: normal;
}
input:hover, textarea:hover, button:hover, select:hover
{
	border: solid 1px #454545;
}
.button_submit:hover, .button_reset:hover
{
	border: 1px solid #545454;
	background: url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/submit_bg.png) no-repeat 0 -218px #121212;
}
input:focus, textarea:focus, button:focus, select:focus
{
	border: solid 1px #454545;
}

/* All input elements that are checkboxes or radio buttons shouldn't have a border around them. */
input.input_check, input.input_radio
{
	border: none;
	background: none;
}
h3.catbg input.input_check
{
	margin: 9px 7px 0 7px;
}

/* Give disabled text input elements a different background color. */
input[disabled].input_text
{
	background-color: #eee;
}

/* Standard horizontal rule.. ([hr], etc.) */
hr, .hrcolor
{
	height: 1px;
	border: 0;
	color: #666666;
	background-color: #666666;
}

/* By default set the color on these tags as black. */
h1, h2, h3, h4, h5, h6
{
	color: #f4f0f4;
	font-size: 1em;
	margin: 0;
	padding: 0;
}

/* Fieldsets are used to group elements. */
fieldset
{
	border: groove #fff 2px;
	padding: 1em;
	margin: 0 0 0.3em 0;
}
/* No image should have a border when linked. */
a img
{
	border: 0;
}

/* Define strong as bold, and em as italics */
strong
{
	font-weight: bold;
}

em
{
	font-style: italic;
}
/* Alternative for u tag */
.underline
{
	text-decoration: underline;
}

/* Common classes to easy styling.
------------------------------------------------------- */

.floatright, .align_right
{
	float: right;
}
.floatleft, .align_left
{
	float: left;
}

.flow_auto
{
	overflow: auto;
}
.flow_hidden
{
	overflow: hidden;
}
.flow_hidden .windowbg, .flow_hidden .windowbg2
{
	margin-top: 2px;
}
.clear
{
	clear: both;
}
.clear_left
{
	clear: left;
}
.clear_right
{
	clear: right;
}

/* Default font sizes: small (8pt), normal (10pt), and large (14pt). */
.smalltext, tr.smalltext th
{
	font-size: 0.85em;
	font-family: verdana, sans-serif;
}
.middletext
{
	font-size: 0.9em;
	line-height: 1em;
	font-family: verdana, sans-serif;
}
.normaltext
{
	font-size: 1em;
	line-height: 1.2em;
}
.largetext
{
	font-size: 1.4em;
}
.centertext
{
	margin: 0 auto;
	text-align: center;
}
.righttext
{
	margin-left: auto;
	margin-right: 0;
	text-align: right;
}
.lefttext
{
	margin-left: 0;
	margin-right: auto;
	text-align: left;
}
.double_height
{
	line-height: 2em;
}
/* some common padding styles */
.padding
{
	padding: 0.7em;
}
.main_section, .lower_padding
{
	padding-bottom: 0.5em;
}
/* a quick reset list class. */
ul.reset, ul.reset li
{
	padding: 0;
	margin: 0;
	list-style: none;
}
/* Some BBC related styles.
------------------------------------------------------- */

/* A quote, perhaps from another post. */
.quoteheader
{
	color: #7F7F7F;
}
.codeheader, .quoteheader
{
	font-size: 0.9em;
	font-weight: bold;
	padding-left: 5px;
}
blockquote, blockquote blockquote blockquote
{
	font-size: 0.9em;
	color: #8F8F8F;
	line-height: 1.4em;
	background: url(https://i.imgur.com/KmX56Uw.png) 2px 2px no-repeat #2c2c2c;
	border-top: solid 1px #545454;
	border-bottom: solid 1px #545454;
	padding: 1em 1em 0.6em 2em;
	margin: 0.3em 0 0.5em 0;
}
blockquote blockquote
{
	background: #1F1F1F;
	font-size: 0.9em;
	margin: 0 0 0.5em 0;	
	color: #7F7F7F;
}
/* A code block - maybe even PHP ;). */
code
{
	display: block;
	font-family: "courier new", "times new roman", monospace;
	color: #7F7F7F;
	background: #1F1F1F;
	border-top: solid 1px #545454;
	border-bottom: solid 1px #545454;
	line-height: 1.3em;
	padding: 3px 1em;
	overflow: auto;
	white-space: pre;
	/* Stop after about 24 lines, and just show a scrollbar. */
	max-height: 24em;
}
/* For links to change the code stuff... */
.codeoperation
{
	font-weight: normal;
}

/* Styling for BBC tags */
.bbc_link:link, .bbc_link:visited
{
	color: #c5a79c;
}
.bbc_size
{
	line-height: 1.4em;
}
.bbc_color a
{
	color: inherit;
}
.bbc_img
{
	border: 0;
}
.bbc_table
{
	font: inherit;
	color: inherit;
}
.bbc_table td
{
	font: inherit;
	color: inherit;
	vertical-align: top;
}
.bbc_u
{
	text-decoration: underline;
}

/* Generally, those [?] icons.  This makes your cursor a help icon. */
.help
{
	cursor: help;
}

/* /me uses this a lot. (emote, try typing /me in a post.) */
.meaction
{
	color: red;
}

/* The main post box - this makes it as wide as possible. */
.editor
{
	width: 96%;
}

/* Highlighted text - such as search results. */
.highlight
{
	background-color: yellow;
	font-weight: bold;
	color: black !important;
}

/* A more discreet highlight color, for selected membergroups etc. */
.highlight2
{
	background-color: #B3F4B0;
	color: black !important;
}

/* Generic, mostly colour-related, classes.
------------------------------------------------------- */

.titlebg, .titlebg2, thead tr.titlebg th, tr.titlebg td, tr.titlebg2 td
{
	color: #efebef;
	font-family: arial, helvetica, sans-serif;
	font-size: 1.1em;
	font-weight: bold;
	background: url(https://i.imgur.com/ArBFR8C.png) no-repeat -10px -120px;
}
.catbg, .catbg2, tr.catbg td, tr.catbg2 td, tr.catbg th, tr.catbg2 th
{
	color: white;
	font-family: arial, helvetica, sans-serif;
	font-size: 1.1em;
	font-weight: bold;
	background: url(https://i.imgur.com/ArBFR8C.png) no-repeat -10px -160px;
}

/* adjust the table versions of headers */
tr.titlebg td, tr.titlebg2 td
{
	padding: 6px;
}
tr.catbg td, tr.catbg2 td, td.catbg, td.catbg2, tr.catbg th, tr.catbg2 th, th.catbg, th.catbg2
{
	padding: 6px;
}
tr.titlebg td a, tr.titlebg2 td a
{
	color: #efebef;
}
tr.catbg td a, tr.catbg2 td a, .catbg a
{
	color: white;
}
tr.catbg th.smalltext
{
	font-size: 0.9em;
}
tr.titlebg th.smalltext, thead tr.titlebg th a:link, thead tr.titlebg th a:visited
{
	color: #efebef;
	font-size: 0.95em;
}

/* Alternating backgrounds for posts, and several other sections of the forum. */
.windowbg, #preview_body
{
	color: #efebef;
	background-color: #282828;
}
.windowbg2
{
	color: #efebef;
	background-color: #202020;
}
.windowbg3
{
	color: #f4f0f4;
	background-color: #cacdd3;
}

/* the page navigation area */
.pagesection
{
	font-size: 0.9em;
	padding: 0 0 0.2em 0;
	overflow: hidden;
	margin-bottom: 1px;
}
.pagesection span
{
	padding: 0 0.5em;
}
div.pagesection div.floatright input
{
	margin-top: 3px;
}

.pagelinks
{
	padding:  0.6em 0 0.4em 0;
}

/* GenericList */
.additional_row
{
	float: right;
	padding: 0.5em 0 0.5em 2em;
}
.additional_row input
{
	margin-left: 1em;
}
table.table_grid thead tr.catbg th.smalltext
{
	white-space: nowrap;
}

/* Colors for background of posts requiring approval */
.approvebg
{
	color: #c5a79c;
	background-color: #1e1e1e;
}
.approvebg2
{
	color: #c5a79c;
	background-color: #282828;
}

/* Color for background of *topics* requiring approval */
.approvetbg
{
	color: #c5a79c;
	background-color: #2e2e2e;
}
.approvetbg2
{
	color: #c5a79c;
	background-color: #4a4a4a;
}

/* Sticky topics get a different background */
.stickybg
{
	background: #2e2d2d;
}
.stickybg2
{
	background: #303030;
}

/* Locked posts get a different shade, too! */
.lockedbg
{
	background: #323232;
	font-style: italic;
}
.lockedbg2
{
	background: #383838;
	font-style: italic;
}

/* Posts and personal messages displayed throughout the forum. */
.post, .personalmessage
{
	overflow: auto;
	line-height: 1.4em;
	padding: 0.1em 0;
}

/* All the signatures used in the forum.  If your forum users use Mozilla, Opera, or Safari, you might add max-height here ;). */
.signature, .attachments
{
	width: 98%;
	overflow: auto;
	clear: right;
	padding: 1em 0 3px 0;
	border-top: solid 1px #191919;
	line-height: 1.4em;
	font-size: 0.85em;
}
.custom_fields_above_signature
{
	width: 98%;
	clear: right;
	padding: 1em 0 3px 0;
	border-top: solid 1px #202020;
	line-height: 1.4em;
	font-size: 0.85em;
}

/* Sometimes there will be an error when you post */
.error
{
	color: red;
}

/* Messages that somehow need to attract the attention. */
.alert
{
	color: red;
}

/* Calendar colors for birthdays, events and holidays */
.birthday
{
	color: #920AC4;
}

.event
{
	color: #078907;
}

.holiday
{
	color: #EA5F38;
}

/* Colors for warnings */
.warn_mute
{
	color: red;
}

.warn_moderate
{
	color: #FFA500;
}

.warn_watch, .success
{
	color: green;
}

a.moderation_link, a.moderation_link:visited
{
	color: red;
	font-weight: bold;
}

.openid_login
{
	background: black url(http://www.chexquest.org/Themes/Krilliunsmf2/images/openid.gif) no-repeat;
	padding-left: 18px;
}

/* a descriptive style */
.description, .plainbox
{
	padding: 0.5em 1em 1em 1em;
	font-size: 0.9em;
	line-height: 1.4em;
	border: solid 1px #545454;
	background: #171717;
	margin: 0 0 1em 0;
}
/* an informative style */
.information
{
	padding: 0.5em 1em 1em 1em;
	font-size: 0.9em;
	line-height: 1.3em;
	border: solid 1px #545454;
	background: #161616;
	margin: 0.3em 0 1em 0;
}
.information p
{
	padding: 1em;
	margin: 0;
}
/* AJAX notification bar
------------------------------------------------------- */
#ajax_in_progress
{
	background: url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/loadingbar.png) repeat-x;
	color: #F96F00;
	text-align: center;
	font-size: 16pt;
	padding: 8px;
	width: 100%;
	height: 66px;
	line-height: 25px;
	position: fixed;
	top: 0;
	left: 0;
}

#ajax_in_progress a
{
	color: orange;
	text-decoration: underline;
	font-size: smaller;
	float: right;
	margin-right: 20px;
}

/* a general table class */
table.table_grid
{
	border-collapse: collapse;
}
table.table_grid td
{
	padding: 3px;
	border-bottom: solid 1px #171717;
	border-right: solid 1px #171717;
}

/* Lists with settings use these a lot.
------------------------------------------------------- */
dl.settings
{
	clear: right;
	overflow: auto;
	margin: 0 0 10px 0;
	padding: 0;
}
dl.settings dt
{
	width: 48%;
	float: left;
	margin: 0 0 10px 0;
	padding: 0;
	clear: both;
}
dl.settings dt.settings_title
{
	width: 100%;
	float: none;
	margin: 0 0 10px 0;
	padding: 5px 0 0 0;
	font-weight: bold;
	clear: both;
}
dl.settings dt.windowbg
{
	width: 98%;
	float: left;
	margin: 0 0 3px 0;
	padding: 0 0 5px 0;
	clear: both;
}
dl.settings dd
{
	width: 48%;
	float: left;
	margin: 0 0 3px 0;
	padding: 0;
}
dl.settings img
{
	margin: 0 10px 0 0;
}
/* help icons */
dl.settings dt a img
{
	position: relative;
	top: 2px;
}

/* Styles for rounded headers.
------------------------------------------------------- */
h3.catbg, h3.catbg2, h3.titlebg, h4.titlebg, h4.catbg, .table_list tbody.header td
{
	overflow: hidden;
	height: 31px;
	line-height: 32px;
	font-size: 1.2em;
	font-weight: bold;
}
h3.catbg a, h3.catbg, .table_list tbody.header td, .table_list tbody.header td a
{
	color: white;
}
h3.catbg2 a, h3.catbg2
{
	color: #feb;
}
h3.catbg a:hover, .table_list tbody.header td a:hover
{
	color: #fd9;
	text-decoration: none;
}
h3.catbg2 a:hover
{
	color: #fff;
	text-decoration: none;
}
h3.titlebg a, h3.titlebg, h4.titlebg, h4.titlebg a
{
	color: #efebef;
}
h3.titlebg a:hover, h4.titlebg a:hover
{
	color: #7f7f7f;
	text-decoration: none;
}
h3.catbg img.icon, h4.titlebg img.icon
{
	vertical-align: middle;
	margin: -2px 5px 0 0;
}
div.cat_bar
{
	background: url(https://i.imgur.com/ArBFR8C.png) no-repeat 0 -160px;
	padding-left: 9px;
	overflow: hidden;
}
div.title_bar
{
	background: url(https://i.imgur.com/ArBFR8C.png) no-repeat 0 -200px;
	padding-left: 9px;
	overflow: hidden;
}
div.title_barIC
{
	background: url(https://i.imgur.com/ArBFR8C.png) no-repeat 0 -120px;
	padding-left: 9px;
	overflow: hidden;
}

h4.catbg span.left, h4.catbg2 span.left, h3.catbg span.left, h3.catbg2 span.left, .table_list tbody.header td span.left
{
	float: left;
	display: block;
	width: 9px;
	height: 31px;
	background: url(https://i.imgur.com/ArBFR8C.png) no-repeat 0 -160px;
}

h4.catbg, h4.catbg2 , h3.catbg , h3.catbg2 , .table_list tbody.header td.catbg
{
	background: url(https://i.imgur.com/ArBFR8C.png) no-repeat 100% -160px;
	padding-right: 9px;
}

h4.titlebg span.left, h3.titlebg span.left
{
	float: left;
	display: block;
	width: 9px;
	height: 31px;
	background: url(https://i.imgur.com/ArBFR8C.png) no-repeat 0 -200px;
}

h4.titlebg, h3.titlebg
{
	background: url(https://i.imgur.com/ArBFR8C.png) no-repeat 100% -200px;
	padding-right: 9px;
}

h4.titlebg img.icon
{
	float: left;
	margin: 5px 8px 0 0;
}

/* rounded bars needs a different background here */
#upshrinkHeaderIC h3.catbg span.left, #upshrinkHeaderIC h3.catbg2 span.left
{
	background: url(https://i.imgur.com/ArBFR8C.png) no-repeat 0 -160px;
}
#upshrinkHeaderIC h3.catbg, #upshrinkHeaderIC h3.catbg2
{
	background: url(https://i.imgur.com/ArBFR8C.png) no-repeat 100% -160px;
}
#upshrinkHeaderIC h4.titlebg span.left
{
	background: url(https://i.imgur.com/ArBFR8C.png) no-repeat 0 -120px;
}
#upshrinkHeaderIC h4.titlebg
{
	background: url(https://i.imgur.com/ArBFR8C.png) no-repeat 100% -120px;
}
#upshrinkHeaderIC p.pminfo
{
	margin: 0;
	padding: 0.5em;
}
img#upshrink_ic, img#newsupshrink
{
	float: right;
	margin: 10px 5px 0 0;
}
table.table_list a.unreadlink,table.table_list a.collapse
{
	float: right;
}
table.table_list a.collapse
{
	margin: 10px 5px 0 1em;
}

/* The half-round header bars for some tables. */
.table_grid thead th
{
	height: 28px;
	color: white;
	font-family: arial, helvetica, sans-serif;
	font-size: 1.1em;
	font-weight: bold;
	background: url(https://i.imgur.com/ArBFR8C.png) no-repeat -10px -160px;
}
.table_grid thead th a:link, .table_grid thead th a:visited
{
	color: white;
}
.table_grid th.first_th
{
	background: url(https://i.imgur.com/ArBFR8C.png) no-repeat 0 -160px;
}
.table_grid th.last_th
{
	background: url(https://i.imgur.com/ArBFR8C.png) no-repeat 100% -160px;
}
.table_grid th.last_th input
{
	margin: 0 2px;
}

/* Common styles used to add corners to divisions.
------------------------------------------------------- */
.windowbg span.topslice
{
	display: block;
	padding-left: 20px;
	background: url(https://i.imgur.com/ArBFR8C.png) 0 -30px no-repeat;
}
.windowbg span.topslice span
{
	display: block;
	background: url(https://i.imgur.com/ArBFR8C.png) 100% -30px no-repeat;
	height: 11px;
}
.windowbg span.botslice
{
	display: block;
	padding-left: 20px;
	background: url(https://i.imgur.com/ArBFR8C.png) 0 -40px no-repeat;
	font-size: 5px;
	line-height: 5px;
	margin-bottom: 0.2em;
}
.windowbg span.botslice span
{
	display: block;
	background: url(https://i.imgur.com/ArBFR8C.png) 100% -40px no-repeat;
	height: 11px;
}

.windowbg2 span.topslice
{
	display: block;
	padding-left: 20px;
	background: url(https://i.imgur.com/ArBFR8C.png) 0 -60px no-repeat;
}
.windowbg2 span.topslice span
{
	display: block;
	background: url(https://i.imgur.com/ArBFR8C.png) 100% -60px no-repeat;
	height: 11px;
}
.windowbg2 span.botslice
{
	display: block;
	padding-left: 20px;
	background: url(https://i.imgur.com/ArBFR8C.png) 0 -71px no-repeat;
	font-size: 5px;
	line-height: 5px;
	margin-bottom: 0.2em;
}
.windowbg2 span.botslice span
{
	display: block;
	background: url(https://i.imgur.com/ArBFR8C.png) 100% -71px no-repeat;
	height: 11px;
}
.approvebg span.topslice
{
	display: block;
	padding-left: 20px;
	background: url(https://i.imgur.com/ArBFR8C.png) 0 0 no-repeat;
}
.approvebg span.topslice span
{
	display: block;
	background: url(https://i.imgur.com/ArBFR8C.png) 100% 0 no-repeat;
	height: 11px;
}
.approvebg span.botslice
{
	display: block;
	padding-left: 20px;
	background: url(https://i.imgur.com/ArBFR8C.png) 0 -11px no-repeat;
	margin-bottom: 0.2em;
}
.approvebg span.botslice span
{
	display: block;
	background: url(https://i.imgur.com/ArBFR8C.png) 100% -11px no-repeat;
	height: 11px;
}
.postbg { border-left: 1px solid #7F7F7F; border-right: 1px solid #7F7F7F;}


/* Used for sections that need somewhat larger corners.
----------------------------------------------------------- */
.roundframe
{
	padding: 0 10px;
	background: #1f1f1f;
	border-left: 1px solid #4a4a4a;
	border-right: 1px solid #4a4a4a;
}
.roundframe dl, .roundframe dt, .roundframe p
{
	margin: 0;
}
.roundframe p
{
	padding: 0.5em;
}
span.upperframe
{
	padding: 0;
	display: block;
	background: url(https://i.imgur.com/ArBFR8C.png) 0 -90px no-repeat;
	padding-left: 20px;
}
span.upperframe span
{
	padding: 0;
	height: 12px;
	display: block;
	background: url(https://i.imgur.com/ArBFR8C.png) 100% -90px no-repeat;
}
span.lowerframe
{
	padding: 0;
	display: block;
	background: url(https://i.imgur.com/ArBFR8C.png) 0 -102px no-repeat;
	padding-left: 20px;
}
span.lowerframe span
{
	padding: 0;
	height: 12px;
	display: block;
	background: url(https://i.imgur.com/ArBFR8C.png) 100% -102px no-repeat;
}

/* The main content area.
------------------------------------------------------- */
.content
{
	padding: 0.5em 1.2em;
	margin: 0;
	border: none;
}
.content p
{
	margin: 0 0 0.5em 0;
}

/* Styles used by the auto suggest control.
------------------------------------------------------- */
.auto_suggest_div
{
	border: 1px solid #7f7f7f;
	position: absolute;
	visibility: hidden;
}
.auto_suggest_item
{
	background-color: #171717;
}
.auto_suggest_item_hover
{
	background-color: #353535;
	cursor: pointer;
	color: #EEEEEE;
}

/* Styles for the standard dropdown menus.
------------------------------------------------------- */
#main_menu
{
	padding: 0 0 0 0.5em;
	float: left;
	margin: 0;
	width: 98%;
}

.dropmenu, .dropmenu ul
{
	list-style: none;
	line-height: 1em;
	padding: 0;
	margin: 0;
}
.dropmenu
{
	padding: 0 0.5em;
}
.dropmenu a
{
	display: block;
	color: #f4f0f4;
	text-decoration: none;
}
.dropmenu a span
{
	display: block;
	padding: 0 0 0 5px;
	font-size: 0.9em;
}
/* the backgrounds first level only*/
.dropmenu li a.firstlevel
{
	margin-right: 8px;
}
.dropmenu li a.firstlevel span.firstlevel
{
	display: block;
	position: relative;
	left: -5px;
	padding-left: 5px;
	height: 22px;
	line-height: 19px;
}
.dropmenu li
{
	float: left;
	padding: 0;
	margin: 0;
	position: relative;
}
.dropmenu li ul
{
	z-index: 90;
	display: none;
	position: absolute;
	width: 19.2em;
	font-weight: normal;
	border-bottom: solid 1px #131313;
	background: url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/menu_gfx.png) 0 -100px no-repeat;
	padding: 7px 0 0 0;
}
.dropmenu li li
{
	width: 19em;
	margin: 0;
	border-left: solid 1px #131313;
	border-right: solid 1px #131313;
}
.dropmenu li li a span
{
	display: block;
	padding: 8px;
}
.dropmenu li ul ul
{
	margin: -1.8em 0 0 13em;
}

/* the active button */
.dropmenu li a.active
{
	background: url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/menu_gfx.png) no-repeat 100% 0;
	color: #ffffff;
	font-weight: bold;
}
.dropmenu li a.active span.firstlevel
{
	background: url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/menu_gfx.png) no-repeat 0 0;
}
/* the hover effects */
.dropmenu li a.firstlevel:hover, .dropmenu li:hover a.firstlevel
{
	background: url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/menu_gfx.png) no-repeat 100% -30px;
	color: #ffffff;
	cursor: pointer;
	text-decoration: none;
}
.dropmenu li a.firstlevel:hover span.firstlevel, .dropmenu li:hover a.firstlevel span.firstlevel
{
	background: url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/menu_gfx.png) no-repeat 0 -30px;
}
/* the hover effects on level2 and 3*/
.dropmenu li li a:hover, .dropmenu li li:hover>a
{
	background: #242424 url(https://i.imgur.com/ArBFR8C.png) no-repeat -10px -160px;
	color: #fff;
	text-decoration: none;
}
.dropmenu li:hover ul ul, .dropmenu li:hover ul ul ul
{
	top: -999em;
}
.dropmenu li li:hover ul
{
	top: auto;
}
.dropmenu li:hover ul
{
	display: block;
}
.dropmenu li li.additional_items
{
	background-color: #333333;
}

/* The dropdown menu toggle image */
#menu_toggle
{
	float: right;
	margin-right: 10px;
	padding-top: 3px;
}
#menu_toggle span
{
	position: relative;
	right: 5000px;
}

/* Styles for the standard button lists.
------------------------------------------------------- */

.buttonlist ul
{
	z-index: 100;
	padding: 5px;
	margin: 0 0.2em 0 0;
}
.buttonlist ul li
{
	margin: 0;
	padding: 0;
	list-style: none;
	float: left;
}
.buttonlist ul li a
{
	display: block;
	font-size: 0.8em;
	color: #f4f0f4;
	background: url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/menu_gfx.png) no-repeat 0 -60px;
	padding: 0;
	margin-left: 12px;
	text-transform: uppercase;
	cursor: pointer;
}
.buttonlist ul li a:hover
{
	background: url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/menu_gfx.png) no-repeat 0 0;
	color: #fff;
	text-decoration: none;
}
.buttonlist ul li a span
{
	background: url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/menu_gfx.png) no-repeat 100% -60px;
	display: block;
	height: 24px;
	line-height: 20px;
	padding: 0 8px 0 0;
	position: relative;
	right: -8px;
}
.buttonlist ul li a:hover span
{
	background: url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/menu_gfx.png) no-repeat 100% 0;
}
/* the active one */
.buttonlist ul li a.active
{
	background: url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/menu_gfx.png) no-repeat 0 -30px;
	color: white;
	font-weight: bold;
}
.buttonlist ul li a.active span
{
	background: url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/menu_gfx.png) no-repeat 100% -30px;
}
.buttonlist ul li a.active
{
	font-weight: bold;
}
.buttonlist ul li a.active:hover
{
	color: #f4f0f4;
}
.align_top ul li a, .align_bottom ul li a
{
	margin: 0 12px 0 0;
}

/* the navigation list */
ul#navigation
{
	margin: 0;
	font-size: 0.9em;
	padding: 1em 0.4em;
}
ul#navigation li
{
	float: none;
	font-size: 0.95em;
	display: inline;
}

#adm_submenus
{
	padding-left: 2em;
}
#adm_submenus, #adm_submenus ul
{
	height: 3em;
	overflow: auto;
}

/* Styles for the general looks for the Curve theme.
------------------------------------------------------- */

/* the framing graphics */
#header
{
	background: url(https://i.imgur.com/ArBFR8C.png) #1c1c1c no-repeat 0 -240px;
	padding-left: 20px;
}
#header div.frame
{
	background: url(https://i.imgur.com/ArBFR8C.png) no-repeat 100% -240px;
	display: block;
	padding: 5px 20px 0 0px;
}
/* the content section */
#content_section
{
	background: #1b1b1b url(https://i.imgur.com/LWGR55h.png) repeat-y top left;
	padding-left: 20px;
}
#content_section div.frame
{
	background: url(https://i.imgur.com/LWGR55h.png) repeat-y right;
	display: block;
	padding: 0px 20px 0px 0;
}
#main_content_section
{
	width: 100%;
	min-height: 200px;
}

/* the main title, always stay at 45 pixels in height! */
h1.forumtitle
{
	line-height: 45px;
	font-size: 1.8em;
	font-family: Geneva, verdana, sans-serif;
	margin: 0;
	padding: 0;
	float: left;
}
/* float these items to the right */
#siteslogan, img#smflogo
{
	margin: 0;
	padding: 0;
	float: right;
	line-height: 3em;
}
h3, h4
{
	padding-bottom: 3px;
}
/* the upshrink image needs some tweaking */
img#upshrink
{
	float: right;
	margin: 1em;
}
/* ..so does the SMF logo */
img#smflogo
{
	margin-left: 1em;
}
/* the upper_section, float the two each way */
#upper_section
{
	padding: 5px;
	margin-bottom: 1.5em;
}
#upper_section ul li.greeting
{
	font-size: 1.3em;
	font-weight: bold;
	line-height: 1.5em;
}
#upper_section div.news
{
	width: 50%;
	float: right;
	text-align: right;
}
#guest_form
{
	overflow: hidden;
}
#guest_form .info
{
	padding: 4px 0 ;
}
div#upper_section div.user
{
	width: 50%;
	float: left;
	overflow: auto;
}
div#upper_section div.user p
{
	float: left;
	margin: 0 1em 1em 0;
	padding: 0;
}
div#upper_section div.user ul
{
	margin: 0;
	padding-left: 10px;
}
div#upper_section div.user ul li
{
	margin-bottom: 2px;
}
div#upper_section div.news p
{
	display: inline;
}
div#upper_section div.news form
{
	padding-bottom: 10px;
}
/* clearing the floats */
#top_section
{
	min-height: 65px;
	overflow: hidden;
	margin-bottom: 3px;
}
#upper_section
{
	overflow: hidden;
}

/* The navigation list (i.e. linktree) */
.navigate_section
{
	padding: 0.5em;
	margin: 1em 0 0 0;
}
.navigate_section ul
{
	display: block;
	margin: 0;
	font-size: 0.9em;
	padding: 1em 0 0.5em 0;
	border-top: dashed #545454 1px;
	overflow: hidden;
	list-style: none;
}
.navigate_section ul li
{
	float: left;
	padding: 0 0.5em 0 0;
	font-size: 0.95em;
}

/* The footer wih copyright links etc. */
#footer_section
{
	text-align: center;
	background: url(https://i.imgur.com/ArBFR8C.png) no-repeat 0 -820px;
	padding-left: 20px;
}
#footer_section span.smalltext
{
	font-size: 100%;
}
#footer_section div.frame
{
	background: url(https://i.imgur.com/ArBFR8C.png) no-repeat 100% -820px;
	display: block;
	padding: 60px 0 0 0;
}
#footer_section ul li, #footer_section p
{
	font-size: 0.8em;
}
#footer_section ul li
{
	display: inline;
	padding-right: 5px;
}
#footer_section ul li.copyright
{
	display: block;
}
select.qaction, input.qaction
{
	font-size: 0.7em;
	padding: 0;
}
#mlist table tbody td.windowbg2
{
	text-align: center;
}

/* Styles for a typical table.
------------------------------------------------------- */
table.table_list
{
	width: 100%;
}
table.table_list p
{
	padding: 0;
	margin: 0;
}
table.table_list td,table.table_list th
{
	padding: 5px;
}
table.table_list tbody.header td
{
	padding: 0px;
}
table.table_list tbody.content td.stats
{
	font-size: 90%;
	width: 15%;
	text-align: center;
}
table.table_list tbody.content td.lastpost
{
	line-height: 1.3em;
	font-size: 85%;
	width: 24%;
}
table.table_list tbody.content td.icon
{
	text-align: center;
	width: 6%;
}

/* Styles for the board index.
------------------------------------------------- */

/* the board title! */
.table_list tbody.content td.info a.subject
{
	font-weight: bold;
	font-size: 110%;
	color: #c3c3c3;
}
.table_list tbody.content td.children
{
	color: #c5a79c;
	font-size: 85%;
}
p.moderators
{
	font-size: 0.8em;
	font-family: verdana, sans-serif;
}
/* hide the table header/footer parts - but its here for those needing to style it */
#boardindex_table .table_list thead, #boardindex_table .table_list tfoot
{
	display: none;
}

/* the posting icons */
#posting_icons
{
	padding: 0 1em 0.5em 1em;
	margin: 0 0 1em 0;
	line-height: 1em;
}
#posting_icons ul
{
	font-size: 0.8em;
}
#posting_icons img
{
	vertical-align: middle;
	margin: 0 0 0 4ex;
}
#postbuttons_upper ul li a span
{
	line-height: 19px;
	padding: 0 0 0 6px;
}
.nextlinks
{
	text-align: right;
	margin-top: -1px;
}
.nextlinks_bottom
{
	clear: right;
	text-align: right;
}
.mark_read
{
	padding: 0 0.5em;
}

/* the newsfader */
#smfFadeScroller
{
	text-align: center;
	padding: 0 2em;
	overflow: auto;
	margin: 1em 0;
}

/* Styles for the info centre on the board index.
---------------------------------------------------- */

#upshrinkHeaderIC
{
	margin-top: 4px;
}
dl#ic_recentposts
{
	margin: 0 0 0.5em 0;
	padding: 0.5em;
	line-height: 1.3em;
}
dl#ic_recentposts dt
{
	float: left;
}
dl#ic_recentposts dd
{
	text-align: right;
}
#upshrinkHeaderIC p
{
	margin: 0 0 0.5em 0;
	padding: 0.5em;
}
#upshrinkHeaderIC p.last
{
	margin: 0;
	padding: 0.5em;
	border-top: dotted 2px #565656;
}
#upshrinkHeaderIC p.inline
{
	border: none;
	margin: 0;
	padding: 0.2em 0.5em 0.2em 0.5em;
}
#upshrinkHeaderIC p.stats
{
	font-size: 1.1em;
	padding-top: 8px;
}
form#ic_login
{
	padding: 0.5em;
	height: 2em;
}
form#ic_login ul li
{
	margin: 0;
	padding: 0;
	float: left;
	width: 20%;
	text-align: center;
}
form#ic_login ul li label
{
	display: block;
}

/*the small stats*/
#index_common_stats
{
	display: block;
	margin: 0 0 0.5em 0;
	text-align: right;
	font-size: 0.9em;
	position: relative;
	top: -20px;
	line-height: 1px;
}

#upshrinkHeaderIC p.pminfo
{
	margin: 0;
	padding: 0.5em;
}
table.table_list a.unreadlink,table.table_list a.collapse
{
	float: right;
}
table.table_list a.collapse
{
	margin: 10px 5px 0 1em;
}
img.new_posts
{
	padding: 0 0.1em;
}
/* Styles for the message (topic) index.
---------------------------------------------------- */

#messageindex
{
	clear: both;
}
/* the page navigation area */
#childboards h3
{
	padding-bottom: 0;
}
#childboards .table_list thead
{
	display: none;
}
#childboards .table_list
{
	margin-bottom: 1em;
}
.lastpost img
{
	float: right;
	padding: 4px;
}

/* Styles for the display template (topic view).
---------------------------------------------------- */

#postbuttons div.buttons
{
	padding: 0.5em;
	width: 40%;
	float: right;
}
#postbuttons div.middletext
{
	width: 60%;
}
#postbuttons span
{
	display: block;
	text-align: right;
}
#postbuttons span.lower
{
	clear: right;
}
#postbuttons .buttonlist
{
	float: right;
}
#postbuttons #pagelinks
{
	padding-top: 1em;
}
#moderationbuttons
{
	overflow: hidden;
}
/* Events */
#edit_event
{
	margin: 0 1em;
	vertical-align: middle;
}
/* Poll question */
#poll
{
	overflow: hidden;
}
h4#pollquestion
{
	padding: 0 0 0.5em 2em;
}

/* Poll vote options */
#poll_options ul.options
{
	border-top: 1px solid #545454;
	padding: 1em 2.5em 0 2em;
	margin: 0 0 1em 0;
}
#poll_options div.submitbutton
{
	border-bottom: 1px solid #545454;
	clear: both;
	padding: 0 0 1em 2em;
	margin: 0 0 1em 0;
}

/* Poll results */
#poll_options dl.options
{
	border: solid #545454;
	border-width: 1px 0;
	padding: 1em 2.5em 1em 2em;
	margin: 0 1em 1em 0;
}

#poll_options dl.options dt
{
	padding: 0.3em 0;
	width: 30%;
	float: left;
	margin: 0;
	clear: left;
}

#poll_options dl.options dt.voted
{
	font-weight: bold;
}
#poll_options dl.options dd
{
	margin: 0;
	padding: 0.3em 0 0.3em 2em;
	width: 60%;
	max-width: 500px;
	float: left;
}
#poll .content
{
	padding: 0 1em;
}
span.pollbar, span.pollbar span, span.percent
{
	height: 1.1em;
	display: block;
}
span.percent
{
	float: right;
}
span.pollbar
{
	background: #171717;
	border: solid 1px #888;
	margin-right: 6em;
}
span.pollbar span
{
	background: #FE9540;
}
#poll_options dd.voted span.pollbar span
{
	background: #FFC495;
}

/* Poll notices */
#poll_options p
{
	margin: 0 1.5em 0.2em 1.5em;
	padding: 0 0.5em 0.5em 0.5em;
}

div#pollmoderation
{
	margin: 0;
	padding: 0;
	overflow: auto;
}

/* onto the posts */
#forumposts
{
	clear: both;
}
/* author and topic informaton */
#forumposts h3 span#author, #forumposts h3 span#top_subject
{
	float: left;
}
#forumposts h3 span#top_subject
{
	margin: 0 0 0 7.7em;
}
#forumposts h3 img
{
	float: left;
	margin: 4px 0.5em 0 0;
}
#forumposts h3.catbg
{
	margin-bottom: 3px;
}
p#whoisviewing
{
	margin: 0;
	padding: 0.5em;
}
/* poster and postarea + moderation area underneath */
.poster
{
	float: left;
	width: 15em;
}
.postarea, .moderatorbar
{
	margin: 0 0 0 16em;
}

.moderatorbar
{
	clear: right;
}
/* poster details and list of items */
.poster h4, .poster ul
{
	padding: 0;
	margin: 0 1em 0 1.5em;
}
.poster h4
{
	margin: 0.2em 0 0.4em 1.1em;
