}
.poster h4, .poster h4 a
{
	color: #c06002;
}
.poster ul ul
{
	margin: 0.3em 1em 0 0;
	padding: 0;
}
.poster ul ul li
{
	display: inline;
}
.poster li.stars, .poster li.avatar, .poster li.blurb, li.postcount, li.im_icons ul
{
	margin-top: 0.5em;
}
.messageicon
{
	float: left;
	margin: 0 0.5em 0 0;
}
.messageicon img
{
	padding: 6px 3px;
}
.keyinfo
{
	float: left;
	width: 50%;
}
.modifybutton
{
	clear: right;
	float: right;
	margin: 8px 20px 10px 0;
	text-align: right;
}

/* The quick buttons */
div.quickbuttons_wrap
{
	padding: 0.2em 0.5em;
}
ul.quickbuttons
{
	margin: 0.9em 11px 0 0;
