}
ul.quickbuttons li.quote_button
{
	background: url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/quickbuttons.png) no-repeat 0 0;
}
ul.quickbuttons li.remove_button
{
	background: url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/quickbuttons.png) no-repeat 0 -30px;
}
ul.quickbuttons li.modify_button
{
	background: url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/quickbuttons.png) no-repeat 0 -60px;
}
ul.quickbuttons li.approve_button
{
	background: url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/quickbuttons.png) no-repeat 0 -90px;
}
ul.quickbuttons li.restore_button
{
	background: url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/quickbuttons.png) no-repeat 0 -120px;
}
ul.quickbuttons li.split_button
{
	background: url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/quickbuttons.png) no-repeat 0 -150px;
}
ul.quickbuttons li.reply_button
{
	background: url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/quickbuttons.png) no-repeat 0 -180px;
}
ul.quickbuttons li.reply_all_button
{
	background: url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/quickbuttons.png) no-repeat 0 -180px;
}
ul.quickbuttons li.notify_button
{
	background: url(http://www.chexquest.org/Themes/Krilliunsmf2/images/theme/quickbuttons.png) no-repeat 0 -210px;
}
ul.quickbuttons li.inline_mod_check
{
	margin: 0 0 0 5px;
}

.post
{
	margin-top: 0.5em;
	clear: right;
}
.inner
{
	padding: 1em 1em 0 0;
	margin: 0 1em 0 0;
	border-top: solid 1px #545454;
}
.inner img.smiley
{
	vertical-align: bottom;
}
#forumposts .modified
{
	float: left;
}
