/* ==UserStyle==
@name           PyPI Dark
@namespace      github.com/openstyles/stylus
@homepageURL    https://github.com/Bloumbs/PyPI-Dark
@updateURL      https://raw.githubusercontent.com/Bloumbs/PyPI-Dark/master/pypi-dark.user.css
@version        1.0.0
@description    Dark theme for pypi.org
@author         Bloumbs
==/UserStyle== */

@-moz-document domain("pypi.org") {

    body {
        background-color: #262626;
    }
    .page-title {
        color: #fff;
    }
    h1 {
        color: #fff;
    }
    h2 {
        color: #fff;
    }
    p {
        color: #ccc;
    }
    a {
        color: #2aa4d0;
    }
    pre {
        border: 1px solid #505050;
        background-color: #363636;
        color: #fff;
    }
    h3 {
        color: #fff;
    }
    dt {
        color: #fff;
    }
    li {
        color: #ccc;
    }
    .horizontal-section--grey {
        background-color: #262626;
        border-bottom: 1px solid #505050;
        border-top: 1px solid #505050;
    }
    .horizontal-section {
        border-bottom: 1px solid #505050;
    }
    .horizontal-section.horizontal-section--grey {
        background-color: #212121;
    }
    .package-snippet {
        background-color: #363636;
        border: 1px solid #505050
    }
    code {
        background-color: #363636;
        border: 1px solid #505050;
        padding: 5px 8px 6px 8px;
        font-size: 75%;
        color: #d6d6d6;
    }
    .vertical-tabs__tab--is-active,
    .vertical-tabs__tab--is-active:hover {
        background: #363636;
        color: #fff;
        border: 1px solid #505050;
    }
    .vertical-tabs__tab:hover {
        text-decoration: underline;
        color: #2aa4d0;
    }
    a:hover {
        color: #2aa4d0;
        text-decoration: underline;
    }
    .vertical-tabs__tab:focus {
        text-decoration: none;
        color: #fff;
    }
    .statistics-bar__statistic {
        color: #e2e2e2;
        font-size: 20px;
        display: inline-block;
        padding: 10px 20px;
        margin: 0;
        box-sizing: border-box;
    }
    .lede-paragraph {
        font-weight: 600;
        font-size: 1.2rem;
        color: #e6e6e6;
    }
    .release__card {
        border: 1px solid #505050;
        background-color: #353535;
        box-shadow: 1px 1px 2px 1px #252525;
        display: block;
        padding: 15px;
        margin: 15px 0;
    }
    .release--current,
    .release--current .release__node {
        background-color: #262626;
    }
    .release--current {
        border: 1px dotted #252525;
    }
    .release__node {
        background-color: #252525;
        padding: 7px 0;
        position: relative;
        z-index: 5;
    }
    .release {
        display: table;
        table-layout: fixed;
        width: 100%;
        border-left: 1px solid #252525;
        border-right: 1px solid #252525;
        padding: 0 15px;
    }
    github-repo-info {
        color: #66cc81;
    }
    .package-snippet__name,
    .package-snippet__version {
        color: #2aa4d0;
    }
    .accordion__link {
        padding: 5px;
        display: block;
        cursor: pointer;
        border: 0;
        background-color: transparent;
        color: #2aa4d0;
    }
    label {
        color: #a6a6a6;
    }
    .button-group .button-group__button + .button-group__button {
        margin-left: -2px;
        color: #ebebeb;
        border-color: #606060;
    }
    .button--primary {
        border-color: #006dad !important;
        background-color: #006dad;
        color: #fff;
        z-index: 2;
    }
    .button-group .button-group__button + .button-group__button:hover {
        margin-left: -2px;
        color: #ebebeb;
        border-color: #006dad;
    }
    .button {
        border: 2px solid #606060;
        color: #ebebeb;
    }
    .button--disabled,
    .button[disabled] {
        cursor: not-allowed;
        background-color: #303030;
        border: 2px solid #303030;
        color: #727272;
        text-decoration: line-through;
        pointer-events: none;
    }
    ::selection {
        color: #fff;
        background: #2aa4d0;
    }
    .project-description table td,
    .project-description table th {
        margin: 0;
        padding: 10px 7px;
        border: 1px solid #505050;
        background-color: #353535;
        color: #cecece;
    }
    .banner {
        background-color: #303030;
        color: #fff;
        padding: 50px 0;
    }
    .release--current .release__card {
        border: 1px solid #656565;
        background-color: #454545;
        box-shadow: 1px 1px 2px 1px rgba(0, 0, 0, .05);
    }
    .release__version {
        color: #2aa4d0
    }
    .release--current .release__card .release__version {
        color: #2aa4d0;
    }
    .project-description .c1 {
        color: #9c9c9c;
    }
    .project-description .kn {
        color: #2aa4d0;
    }
    .project-description .nn {
        color: #ff0090;
    }
    .project-description .k {
        color: #74d4f4;
    }
    .project-description .nb {
        color: #2aa4d0;
    }
    .project-description .mi {
        color: #00c31b;
    }
    .project-description .ow {
        color: #00c31b;
    }
    .project-description .bp {
        color: #0df;
    }
    .table td,
    .table th {
        margin: 0;
        padding: 10px 7px;
        border-right: 1px solid #d3d3d3;
        background-color: #353535;
        border: 1px solid #505050;
        color: #fff;
    }
    .github-repo-info {
        color: #ccc;
    }
    .project-description {
        color: white;
    }
    .project-description table {
        background-color: #2b2b2b;
    }
    .notification-bar {
        border-bottom: 2px solid #303030;
    }
    .notification-bar__message a {
        color: #28b7e4;
        text-decoration: underline;
    }
    .package-snippet__description {
        color: #b5b5b5;
    }
    .sidebar-section {
        border-bottom: 1px solid #505050;
    }
    .button {
        border: 2px solid #51b1ff;
        color: #ebebeb;
    }
    .homepage-banner__browse a {
        text-decoration: none;
    }
    .homepage-banner__browse a:hover {
        text-decoration: underline;
    }

     kbd {
        font-family: Source Code Pro,monospace;
        background-color: #353535;
        border: none;
        padding: 0 2px 1px;
        font-size: 85%;
        color: white;
    }
    tt {
        background-color: #35353500;
        color: #0af;
        border: none;
    }
}
