css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hordes.fr") {
/* v2.0 */
#modules{
top:5px;
margin-left:30px;
position:absolute;
z-index:4;
padding-left:5px;
padding-right:5px;
background-color:#5c2b20;
-moz-border-radius:0px;
outline:1px solid black;
border:1px solid #f0d79e;
}

#moduleshelp{
padding-top:8px;
position:absolute;
width:100%;
z-index:100;
display:none;
}

#moduleshelp div.helpboxcontent{
margin-left:230px;
width:450px;
position:absolute;
z-index:9;
padding:5px;
color:#f0d79e;
background-color:#5c2b20;
-moz-border-radius:0px;
outline:2px solid black;
border:1px solid #b37c4a;
}

#modules p,
#modules ul,
#modules h1 span,
#modules h1 div.modhelp{
display:none;
}
#modules h1 div.modhelp img{
opacity: 0.7;
cursor: pointer;
}
#modules h1 div.modhelp img:hover{
opacity: 1; /* hover de img, l'image de l'aide se renforce au survol */
}

#modules:hover p,
#modules:hover ul{
display:block;
}
#modules:hover h1 span,
#modules:hover h1 div.modhelp{
display:inline;
}

#modules h1,
#moduleshelp h1{
margin:0px ;
padding : 0px;
height:auto;
font-size:8pt;
text-transform:none;
font-variant:small-caps;
background:none;
}

#modules:hover h1{
border-bottom:1px solid #b37c4a;
margin-bottom:5px;
}
#modules h1 div.modhelp {
float: right;
margin-top: 2px;
}
#modules a.modon,
#modules a.modoff{
display:block;
width:145px;
height:18px;
overflow:hidden;
text-decoration:none;
}
#modules a:active, #modules a:focus {
outline: 0;
}
#modules a.modon span{
text-decoration:underline;
}
#modules a.modoff span{
text-decoration:line-through;
color: #999999;
}
#modules a.modon:hover{
background-color:#696486;
}
#modules a.modoff:hover{
background-color:#534E6A;
color: #DDDDDD;
}
#modules a.modon img{
width:16px;
height:16px;
vertical-align:-25%;
}
#modules a.modoff img{
width:16px;
height:16px;
vertical-align:-25%;
opacity: 0.3;
}
#modules ul{
margin:0px ;
padding : 0px;
float:left;
position:relative;
margin-bottom:8px;
list-style:none;
}
#modules ul li{
margin:0px ;
margin-left: 5px ; /* sÃ©paration entre les colonnes */
padding : 0px;
}
#modules p,
#moduleshelp p{
margin:0px ;
padding : 0px;
font-size:9pt;
line-height:11pt;
text-align:justify;
}

#modules p{
margin-bottom:10px;
width:300px;
}

#modules p i,
#moduleshelp p i {
color: #BB0000;
}

#moduleshelp h1.modheader{
margin-top: 20px ;
}
#moduleshelp img.modheader{
margin-right: 5px ;
}
#moduleshelp div.moddoc{
margin-top: 8px;
max-height:380px; /* en rÃ©glage */
overflow: auto; /* pour le scroll si besoin */
background-color:#59281d; /* lÃ©gÃ¨rement plus foncÃ©, trouver le bon ton */
/*padding:5px;
color:#f0d79e;
outline:2px solid black;*/
border:1px solid #b37c4a; /* en rÃ©glage */
}
#moduleshelp div.moddoc span.nomoremodules {
color:#59281d; /* la mÃªme que le background-color de div.moddoc */
}
#moduleshelp div.moddoc p{
padding-right:5px; /* sinon touche la bare de scroll */
}
#moduleshelp div.moddoc p,
#moduleshelp div.moddoc img.modheader {
padding-left:5px; /* pas coller Ã  la zone de scroll*/
}

#moduleshelp a.button{
float:right;
}

.creditsHide{
display:none ;
}

.creditShow{
display:block;
margin-top: 5px;
font-size: smaller;
font-style: italic;
padding-right:5px;
padding-left:5px;
}

}
