css:
@-moz-document domain("iichan.hk") {
/* Глобальное */
html, body {
	background-color: hsl(0, 0%, 95%);
	color: hsl(0, 0%, 33.3%);
}

html[xmlns] > body {
	padding: 0px 4px;
}

a {
	color: hsl(240, 50%, 50%);
}

/*
a:visited {
    color: hsl(270, 50%, 50%);
}
*/
a:visited {
	color: hsl(240, 20%, 50%);
}

a:hover {
	color: hsl(0, 80%, 50%);
	text-decoration: underline dotted hsl(0, 50%, 50%) !important;
}


/* Предварительные фиксы */
.greeting, .news, .logo, .logo > a, .sublogo, .sublogo > a, .header, .list div a, .postblock, .reply, .highlight, .spoiler:hover, .managehead {
	color: inherit !important;
}


/* Меню навигации */
.adminbar {
	background-color: hsl(0, 0%, 100%);
	box-shadow: hsla(0, 0%, 0%, 0.2) 0px 1px 4px 0px;
	margin-right: -8px;
	padding: 4px;
	width: calc(100% + 8px);
}


/* Баннер */
.logo {
	padding-top: 4px;
}


/* Форма */
.theader {
	background-color: hsl(0, 0%, 40%);
}

.postblock {
	background: hsl(0, 0%, 80%);
}

textarea, input[type='text'], input[type='password'] {
	background-color: hsl(0, 0%, 96%);
	border: 1px solid hsl(0, 0%, 80%);
	border-radius: 2px;
	padding: 4px;
}

textarea:focus, input[type='text']:focus, input[type='password']:focus {
	background-color: hsl(0, 0%, 98%);
	border: 1px solid hsl(224, 100%, 80%);
	box-shadow: hsla(224, 100%, 80%, 0.4) 0 0 4px 0;
}

button, input[type='button'], input[type='submit'], input[type='reset'] {
	background-color: hsl(0, 0%, 85%);
	border: 1px solid hsl(0, 0%, 75%);
	border-radius: 2px;
	margin: 1px;
	padding: 4px 16px;
}

button:hover, input[type='button']:hover, input[type='submit']:hover, input[type='reset']:hover {
	background-color: hsl(0, 0%, 90%);
	color: hsl(0, 0%, 10%);
}

button:active, input[type='button']:active, input[type='submit']:active, input[type='reset']:active {
	background-color: hsl(0, 0%, 100%);
	box-shadow: hsl(224, 100%, 80%) 0 0 4px 0;
}

input[type='checkbox'], input[type='radio'], input[type='file'], select {
	filter: grayscale(100%);
}

#captcha {
	filter: grayscale(100%) invert(40%);
}


/* Посты */
.doubledash {
	color: hsl(224, 100%, 66.6%);
}

.abbrev {
	margin-bottom: 4px !important;
}

.omittedposts {
	background-color: hsl(0, 0%, 95%);
	color: hsl(224, 100%, 66.6%);
	margin: -2px !important;
	padding: 4px;
}

form > div > table + table {
	margin-top: 8px;
}

.reply {
	background-color: hsl(0, 0%, 99%);
	border-radius: 2px;
	box-shadow: hsla(0, 0%, 0%, 0.2) 0px 1px 4px 0px;
}

.highlight {
	background-color: hsl(224, 100%, 96%);
	border: none;
	border-radius: 2px;
	box-shadow: hsla(224, 50%, 50%, 0.4) 0px 1px 4px 0px !important;
}


.filetitle, .replytitle {
	color: hsl(5, 25%, 50%);
}

.postername, .commentpostername {
	color: hsl(150, 25%, 50%);
}

.adminname {
	color: hsl(0, 75%, 50%);
}

.postertrip {
	color: hsl(165, 25%, 50%);
}

.reflink {
	color: hsl(224, 25%, 50%);
}

.unkfunc {
	color: hsl(105, 25%, 50%);
}

.spoiler, .spoiler:hover {
	background-color: hsl(0, 0%, 80%);
	color: hsla(0, 0%, 0%, 0);
}


/* Главная */
.news {
	background: hsl(0, 0%, 90%);
}

.header {
	background: hsl(0, 0%, 80%);
}

.category {
	background: hsl(0, 0%, 90%);
}

.list {
	border: none;
}


/* Панель управления */
.passvalid {
	background-color: hsl(0, 0%, 80%);
}

table[align='center'], table[align='center'] td, table[align='center'] th:not([colspan]) {
	border-color: hsl(0, 0%, 70%) !important;
}

.managehead {
	background: hsl(0, 0%, 75%);
}

.row1, .row2 {
	background: hsl(0, 0%, 99%);
	color: hsl(0, 0%, 40%);
}

form .row2 {
	background: hsl(0, 0%, 85%);
	color: hsl(0, 0%, 20%);
}

.passvalid ~ form > table tr > td:nth-child(3) a {
	color: hsl(5, 25%, 50%);
	font-weight: bold;
}

.passvalid ~ form > table tr > td:nth-child(4) {
	color: hsl(150, 25%, 50%) !important;
}

.delbuttons:nth-child(5) > input:nth-child(1) {
	background-color: hsl(224, 50%, 85%);
	border: 1px solid hsl(224, 50%, 75%);
}

.delbuttons:nth-child(5) > input:nth-child(2), .delbuttons:nth-child(7) > input:nth-child(1) {
	background-color: hsl(0, 50%, 85%);
	border: 1px solid hsl(0, 50%, 75%);
}

.delbuttons:nth-child(5) > input:nth-child(3), .delbuttons:nth-child(7) > input:nth-child(2) {
	background-color: hsl(120, 50%, 85%);
	border: 1px solid hsl(120, 50%, 75%);
}


/* Куклоскрипт */
.de-oppost {
	background-color: hsl(0, 0%, 99%);
	border-radius: 2px;
	box-shadow: hsla(0, 0%, 0%, 0.2) 0px 1px 4px 0px;
	display: table;
	overflow: auto;
	padding: 2px;
}

.de-oppost + table {
	margin-top: 8px;
}

.de-oppost .omittedposts {
	clear: both;
	display: block;
	margin: 2px;
}

.de-new-post, #de-popup-upload {
	background-color: hsl(120, 100%, 96%);
	box-shadow: hsla(120, 50%, 50%, 0.4) 0px 1px 4px 0px !important;
}

.de-post-removed, #de-popup-delete {
	background-color: hsl(0, 100%, 96%);
	box-shadow: hsla(0, 50%, 50%, 0.4) 0px 1px 4px 0px !important;
}

.de-selected, #de-popup-newposts {
	background-color: hsl(224, 100%, 96%);
	box-shadow: hsla(224, 50%, 50%, 0.4) 0px 1px 4px 0px !important;
}

#de-wrapper-popup, .de-popup, .de-pview {
	border: none !important;
	margin: 4px !important;
}

#de-main > .de-win-body, #de-win-fav > .de-win-body, #de-win-hid > .de-win-body, #de-win-reply > .de-win-body, .de-fold-block, .de-menu, .de-pview, .de-popup, .de-cfg-body, #de-cfg-buttons {
	border: 1px solid hsl(0, 0%, 90%) !important;
}

#de-win-reply, .de-pview {
	box-shadow: hsla(0, 0%, 0%, 0.4) 0px 2px 6px 0px;
}

.de-post-counter {
	color: hsl(120, 25%, 50%);
	font: inherit;
	font-size: 75%;
	font-weight: bold;
}

.de-post-counter-deleted {
	color: hsl(0, 100%, 75%);
}
}
