css:
@-moz-document domain("reason.com") {
/*BEGIN 2019-02-10*/

/*Base page color*/
body,html{
    background-color: #303030
}

/*Top bar in mobile view*/
/*@media screen and (max-width: 1028px) and (min-width: 0px)
html.responsive*/ #nav1 {
    background: rgba(113, 113, 113, 0.9);
    background-color: rgba(113, 113, 113, 0.9);
}

/*Hamburger menu "Close" top*/
.sidr ul li.sidr-class-closemenu a {
    background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABUAAAAVCAYAAACpF6WWAAAAGXRFWâ€¦6AFg6EijpLbPoZ8dRRDNCcYw/E6khip4B4NxA/wqUJIMAADCxyLHnyHCkAAAAASUVORK5CYII=) 8px 12px no-repeat #575757;
    padding-left: 25px;
    color: #c0c0c0;
}

/*Hamburger menu text*/
.sidr ul li a {
    color: #c0c0c0;
}

/*Hamburger menu background*/
.sidr {
    background: #3e3e3e;
    background-color: rgb(62, 62, 62);
}

/*Login or Sign-up link text*/
/*@media screen and (max-width: 8000px) and (min-width: 1029px)*/
html.responsive div#user_login_control a span {
    color: #7a75d1;
}

/*Top nav bar*/
/*@media screen and (max-width: 8000px) and (min-width: 1029px)*/
html.responsive #nav_container {
    background: #4b4b4b;
    background-color: rgb(75, 75, 75);
}

/*Top nav bar text*/
/*@media screen and (max-width: 8000px) and (min-width: 1029px)*/
html.responsive #nav1 li>a {
    color: #c0c0c0;
}

/*Search bar left round edge*/
/*@media screen and (max-width: 8000px) and (min-width: 1029px)*/
html.responsive #nav1 li form button.magglass {
    background: #676767;
    background-color: rgb(103, 103, 103);
}

/*The rest of the search bar*/
/*@media screen and (max-width: 8000px) and (min-width: 1029px)*/
html.responsive #nav1 li form input {
    background-color: #676767;
}

/*Stronger links under nav bar "STOSSEL"*/
html.responsive #nav2 li a[href*=stossel] {
    color: #979494!important;
}

/*Stronger links under nav bar "Volokh Conspiracy"*/
/*@media screen and (max-width: 8000px) and (min-width: 1029px)*/
html.responsive #nav2 li a[href*=volokh] {
    color: #979797!important;
}

/*Most text*/
/*@media screen and (max-width: 8000px) and (min-width: 1029px)*/
html.responsive body {
    color: #c0c0c0;
}

/*Links near title (email, share, print)*/
/*@media screen and (max-width: 8000px) and (min-width: 1029px)*/
html.responsive .post .options>ul>li>a, html.responsive .viewcomments {
    color: #c0c0c0;
}

/*Band under nav*/
/*@media screen and (max-width: 8000px) and (min-width: 1029px)*/
html.responsive #leaderboard {
    background: #303030;
    background-color: rgb(48, 48, 48);
}

/*Right-nav text*/
/*@media screen and (max-width: 8000px) and (min-width: 1029px)*/
html.responsive #visited_commented ol.stories li h3 a {
    color: #c0c0c0;
}

/*Right-nav lower text*/
/*@media screen and (max-width: 8000px) and (min-width: 1029px)*/
html.responsive #hitandrun ul li h3 a, html.responsive #volokh ul li h3 a {
    color: #c0c0c0;
}

/*Right-nav lower dividers*/
/*@media screen and (max-width: 8000px) and (min-width: 1029px)*/
html.responsive #hitandrun ul li:after, html.responsive #volokh ul li:after {
    filter: invert(81%);
}

/*Comments bar*/
.commentsheading {
    padding: 3px 4px 1px;
    background: #1f1f1f;
    margin-bottom: 10px;
    cursor: pointer;
}

/*Near bottom bar.*/
/*@media screen and (max-width: 8000px) and (min-width: 1029px)*/
html.responsive #content-col .navigation {
    background: linear-gradient(to top,#fff,#dcffff 2px,#d6d7d7 3px,#1f1f1f 3px,#303030 100%);
    background-image: linear-gradient(to top, rgb(255, 255, 255), rgb(220, 255, 255) 2px, rgb(214, 215, 215) 3px, rgb(91, 91, 91) 3px, rgb(48, 48, 48) 100%);
}

/*Near bottom bar text*/
/*@media screen and (max-width: 8000px) and (min-width: 1029px)*/
html.responsive a {
    text-decoration: none;
    text-decoration-line: none;
    text-decoration-style: initial;
    text-decoration-color: initial;
    color: #c0c0c0;
}

/*END 2019-02-10*/
}
