@-moz-document url(https://cmaisonneuve.omnivox.ca) {
/******************
MADE BY DAVID GENOIS
*******************

THIS THEME CHANGES THE LOGIN/LOGOUT/MAIN OMNIVOX/OMNIVOX SERVICES PAGES/LEA'S MAIN SECTIONS
TO MAKE THEM SIMPLER,RETAINING ONLY THE IMPORTANT FEATURES AND 
IT CHANGES COLOR SCHEME TO BLUE SHADES

Still improving it

Report issues or send comments to to david.genois13@gmail.com

***********************************************************STYLE**********************************************************************************/

/*Move disabled items at the bottom*/
 .ovx10_offreServices {
    display: flex;
    flex-direction: column;
}
.servInactif {
    order:10;
    opacity:0.6;
}

/*Remove pic from main page*/
 #MasterWrapper {
    margin-top: 26px;
}

/*adjust body*/
 #ctl00_body {
    margin-left:-9px;
}
#headerImage, #ctl00_cntFormulaire_ctl00_tdImprimer,.Instructions_Detail,.Instructions_Titre {
    display:none;
}

/*remove pic from lea */
 .trHeader{
    display:none;
}

/*remove community+liked sites from main screen*/
 #ovx10_wrap_comm, #ovx10divStackHolder {
    display:none;
}

/*Change the color of the headers*/
 #ovx10_header_offreServices, .Module_Titre, .TitrePage, #ovx10_header_offreServices, #ovx10_header_calendrier, #ovx10_header_actualites, .ovx10_offreServices li.trig, .ovx10_offreServices li.ovx_sepClass, #ovx10_header_QDN, #ctl00_cntFormulaire_ctl00_tblEntete, #titrePage,font>b,font>b>center,.TitreModule,.sousTitrePageLigne1,.sousTitrePageLigne2,.TitrePageLigne1,.Explication,.titrePageLea,.TitrePageLigne2,.centerPageLea>span>span,.milieuTitre>table>tbody>tr>td>span,.nomCours,.webPart_LienVersGestion2,.DocDispo,.NomMoisMiniature,#cntFormulaire_lnkAjoutEvent2,.Lien_TitrePage,.Lien_TitrePage2 {
    color: #252f64 !important;
}

/*moved omnivox logo from navbar*/
 header #headerFixedBar #headerOmnivoxLogo {
    margin-left:132px !important;
}

/*remove mio and other annoying option in lea as well as mobile omni ad in main screen, header lea*/
 #cntFormulaire_mioInfoTile, #cntFormulaire_leaInfoTile, .ovx10_offreServices.UlMobileTitre, #cntFormulaire_nomCollege, .CVIR_lblExplications {
    display: none;
}

/* omnivox logo filter*/
 #headerOmnivoxLogo, div.identification-intraflex.right, .material-icons, .home, img[name=v_print], .SectionHeaderModule, .svg-icon,.EnteteListTab,
.InfoProfEtuEntete/*,.trBandeau,.tblMenuSelectionMenuV2*/,.EnteteListTabTravauxEtu{
    filter: sepia(100%) hue-rotate(190deg) saturate(500%)!important;
}

/* li in blue*/
 .ovx10_offreServices li.ovx10_sub {
    background: transparent /*url(/images/General/ovx10_menu_puce.jpg)*/ no-repeat 0px 8px !important;
}

/* fix menu lea */
 #divChoixCoursGroupeCoursMenuV2 {
    top:207px!important;
}

/* remove conseil lea */

/*ml body table tbody tr td.cvirContenuCVIR form#ctl00 center>table:first-of-type:not(#tabListeTravEtu),
html body table tbody tr td.cvirContenuCVIR form#ctl00 center>table:nth-of-type(2):not(#tabListeTravEtu),*/
 html body table tbody tr td.cvirContenuCVIR form#ctl00 center>br, #blExplicationsEtudiant, #CVIR_lblExplicationsTitreLien {
    display:none !important;
}

/* lea blue filter for nav
.trBandeau{
    filter: sepia(93%) hue-rotate(173deg) saturate(571%);

}*/


/* Remove explanations of the modules + proper color */
 span.ovx10_descLienService {
    display: none !important;
}
.msgTitreMessage_Grand, .Gen_TitrePageModule_SousTitre, .Gen_TitrePageModule_Titre {
    color: #150846 !important;
}
span.ovx10_lienService {
    color:#0175c1 !important;
}

/* Remove ugly border from main page and the top weird thing*/
 #MasterWrapper {
    /*border: none;*/
    border-color:transparent;
    padding-top:26px;
    margin-top:0px;
}

/* Align navbar right side */
 #headerNavbarQuitter {
    margin-right: 128px;
    border-right: 1px solid #e8e8e8;
}

/*Remove duplicate accueil shit */
 #ctl00_partOffreServices_offreV2_Intr_Accueil {
    display:none;
}

/* Calendrier bleu */
 .CalendrierScolaire .calNomMois {
    color: #2a3f75 !important;
}
.CalendrierScolaire .calJour {
    background-color:#7ec0ff !important;
    color: #fff !important;
}
.CalendrierScolaire .calJourSemaine {
    color: #fff !important;
    background-color: #2d3264 !important;
}

/* Faded banner removal */
 .faded {
    background-color:white !important;
}

/* Fix menu lea height */
 #tblChoixCoursGroupeSession {
    top:205px !important;
}

/* remove pic in omnivox's modules */
 .tdBackgroundHead {
    height:35px !important;
}
.tdBackgroundHead>table {
    height:35px !important;
}

/* Keep "paper" style on omnivox's service tabs*/
 .tbContenantPageLayout, table#menu td, .mainContent, .tbContenantPageLayout td, .tbContenantPageLayout table, #Gen_Footer {
    background-color: #E2FFDC;
}
.mainContent, .tbContenantPageLayout {
    padding: 13px;
    padding-top: 142px;
    margin-top: -142px;
    box-shadow: 3px 4px 22px 1px rgba(166, 166, 166, 0.56);
}
.Gen_Titre_Texte {
    background-color: #E2FFDC !important;
}
.spaceForFixedHeader {
    margin-top:65px;
}
main>.page-body {
    margin-top: 33px !important;
    box-shadow: 3px 4px 22px 1px rgba(166, 166, 166, 0.56);
}

/*blue buttons*/
 .Gen_BtnNextStep, .Categorie_Partout, .Categorie_Partout, .Categorie_Partout_Item_Selected, .Gen_BtnContinuer, .Gen_BtnContinuerEtAjouter, .Gen_BtnConsulter, .Gen_BtnPayer, #divUnInstant input[name=confirm], .Gen_Btn, .Gen_BtnPrecedent, .btnRetour,.tdEnteteMiniature {
    background-color: #3a3b62 !important;
    color: #fff;
}

/*Remove footers*/
 #footer, #ovx10_footer_copyright, .footerPosi {
    display:none;
}

/*password box*/
 .mdp_content {
    padding:none;
}
.mdp_content>table, .mdp_content td {
    background-color:#E2FFDC;
}

/* Calendrier blue */

.calendrier>table>tbody>tr:first-of-type,.tblMenuStyleXPDeuxNiveaux{
	filter: sepia(16%) hue-rotate(507deg) saturate(353%)!important;
}

.calendrier>table>tbody>tr:last-of-type{
	filter: sepia(16%) hue-rotate(507deg) saturate(353%)!important;
}

/* Menu lea blue */
.TrBandeau>td:first-of-type>table:first-of-type{
	filter: sepia(16%) hue-rotate(587deg) saturate(353%)!important;
}

.TrBandeau>td:first-of-type>table:nth-of-type(2){
	filter:sepia(16%) hue-rotate(871deg) saturate(353%)!important
}
.contenuMenuGroupeLEASelectV3estdMenuV2, .contenuMenuGroupeSelectAutreCGV3estd,.contenuMenuGroupeSelectV3s ,.contenuMenuGroupeSelectSessionV3s{
	background-color:#3c3cff!important;
    background-image:none !important;
}
.contenuMenuGroupeLEASelectV3estdMenuV2:hover, .contenuMenuGroupeSelectAutreCGV3estd:hover{
	color:grey!important;
}

/* Menu lea blue */
.TrBandeau>td:first-of-type>table:nth-of-type(4),.TrBandeau>td:first-of-type>table:nth-of-type(5),.TrBandeau>td:first-of-type>table:nth-of-type(6),.TrBandeau>td:first-of-type>table:last-of-type,.TrBandeau>td:first-of-type>img:last-of-type{
	display:none;
}

.TrBandeau>td>img:first-of-type{
	display:none;
}

.infoCGNoCours,.infoCGNomCours,.infoCGNoGroupe{
	color:#563b19 !important;
}

/*remove lea print*/
.TrBandeau>td:nth-of-type(2)>a{
	display:none;
}

/* lea navbar */
.TrBandeau>td:nth-of-type(2)>table:first-of-type{

    padding-right: 7px;
    margin-left: 455px;
    padding-bottom: 8px;
    background:linear-gradient(0deg, rgb(0, 252, 255) 0%, rgba(60,60,255,1) 59%);
    border-radius: 9px;
    position: absolute;
    top: -9px;
    padding-top: 12px;
    color: white !important;
}

.CVIR_liensEnDessousFleur{
	color:white;
}
