p.rs {border-bottom: 1px dotted #D3E1F9!important;}
#res {margin-right: 0 !important;}
.tphdr {margin-top: 0 !important;}
.lhshdr {z-index: 0 !important;}
#rshdr {margin-top: 30px !important;}
div#main {margin-top: 0px !important;}
